import { ApiProperty } from '@nestjs/swagger';

export class BotAuditLogResponse {
  @ApiProperty({ example: 'audit-log-uuid' })
  id: string;

  @ApiProperty({ example: '2025-08-07T10:30:00Z' })
  timestamp: string;

  @ApiProperty({ example: 'BOT_REQUEST_SUCCESS' })
  action: string;

  @ApiProperty({ example: 'GET /users' })
  resource: string;

  @ApiProperty({ example: 'GET' })
  method: string;

  @ApiProperty({ example: '/users' })
  path: string;

  @ApiProperty({ example: 200, required: false })
  statusCode?: number;

  @ApiProperty({ example: '150ms', required: false })
  duration?: string;

  @ApiProperty({ example: 'chatbot-external-001' })
  botAlias: string;

  @ApiProperty({ example: 'bot_1691424000_abc123def456', required: false })
  tokenId?: string;

  @ApiProperty({ example: 'company-uuid', required: false })
  companyId?: string;

  @ApiProperty({ example: '192.168.1.100', required: false })
  ipAddress?: string;

  @ApiProperty({ example: 'ChatbotClient/1.0', required: false })
  userAgent?: string;

  @ApiProperty({ example: 'req-12345', required: false })
  requestId?: string;

  @ApiProperty({ required: false })
  query?: Record<string, any>;

  @ApiProperty({ required: false })
  params?: Record<string, any>;

  @ApiProperty({ required: false })
  error?: Record<string, any>;
}

export class BotAuditResponse {
  @ApiProperty({
    type: [BotAuditLogResponse],
    description: 'List of bot audit logs',
  })
  logs: BotAuditLogResponse[];

  @ApiProperty({
    example: 25,
    description: 'Total number of logs returned',
  })
  total: number;

  @ApiProperty({
    description: 'Applied filters for this query',
  })
  filters: Record<string, any>;
}

export class BotStatisticsResponse {
  @ApiProperty({ description: 'General audit statistics' })
  general: any;

  @ApiProperty({ description: 'BOT-specific statistics' })
  botSpecific: {
    totalRequests: number;
    successfulRequests: number;
    errorRequests: number;
    methodBreakdown: Record<string, number>;
    statusCodeBreakdown: Record<string, number>;
    averageResponseTime: string;
    topEndpoints: Array<{ endpoint: string; count: number }>;
    uniqueTokens: number;
    dateRange: {
      from: string;
      to: string;
    };
  };

  @ApiProperty({ example: 'user-uuid' })
  generatedBy: string;

  @ApiProperty({ example: '2025-08-07T10:30:00Z' })
  generatedAt: string;
}

import { AuditLog } from '@core/entities/audit-log.entity';
import {
  BotAuditLogResponse,
  BotAuditResponse,
} from '@application/dtos/responses/bot/bot-audit.response';

export class BotAuditMapper {
  /**
   * Convert domain AuditLog entity to response DTO
   */
  static toAuditLogResponse(auditLog: AuditLog): BotAuditLogResponse {
    return {
      id: auditLog.id.getValue(),
      timestamp: auditLog.timestamp.toISOString(),
      action: auditLog.action,
      resource: auditLog.metadata.resource || 'unknown',
      method: auditLog.metadata.method || 'unknown',
      path: (auditLog.metadata.path as string) || 'unknown',
      statusCode: auditLog.metadata.statusCode as number | undefined,
      duration: auditLog.metadata.duration ? String(auditLog.metadata.duration) : undefined,
      botAlias: (auditLog.metadata.botAlias as string) || auditLog.userId?.getValue() || 'unknown',
      tokenId: auditLog.metadata.tokenId as string | undefined,
      companyId: auditLog.metadata.companyId as string | undefined,
      ipAddress: auditLog.metadata.ipAddress as string | undefined,
      userAgent: auditLog.metadata.userAgent as string | undefined,
      requestId: auditLog.metadata.requestId as string | undefined,
      query: auditLog.metadata.query as Record<string, any> | undefined,
      params: auditLog.metadata.params as Record<string, any> | undefined,
      error: auditLog.metadata.error as Record<string, any> | undefined,
    };
  }

  /**
   * Convert array of domain AuditLog entities to BotAuditResponse
   */
  static toBotAuditResponse(
    auditLogs: AuditLog[],
    filters: Record<string, any>,
    appliedBy: string,
  ): BotAuditResponse {
    const logs = auditLogs.map(log => this.toAuditLogResponse(log));

    return {
      logs,
      total: logs.length,
      filters: {
        ...filters,
        appliedBy,
        appliedAt: new Date().toISOString(),
      },
    };
  }

  /**
   * Calculate method breakdown from audit logs
   */
  static getMethodBreakdown(logs: AuditLog[]): Record<string, number> {
    const methods: Record<string, number> = {};
    logs.forEach(log => {
      const method = log.metadata.method || 'UNKNOWN';
      methods[method] = (methods[method] || 0) + 1;
    });

    return methods;
  }

  /**
   * Calculate status code breakdown from audit logs
   */
  static getStatusCodeBreakdown(logs: AuditLog[]): Record<string, number> {
    const codes: Record<string, number> = {};
    logs.forEach(log => {
      const code = log.metadata.statusCode || 'UNKNOWN';
      codes[code] = (codes[code] || 0) + 1;
    });

    return codes;
  }

  /**
   * Calculate average response time from audit logs
   */
  static calculateAverageResponseTime(logs: AuditLog[]): string {
    const durations = logs
      .map(log => log.metadata.duration)
      .filter(
        duration => duration && typeof duration === 'string' && (duration as string).endsWith('ms'),
      )
      .map(duration => parseInt((duration as string).replace('ms', '')));

    if (durations.length === 0) return '0ms';

    const average = durations.reduce((sum, duration) => sum + duration, 0) / durations.length;

    return `${Math.round(average)}ms`;
  }

  /**
   * Get top endpoints from audit logs
   */
  static getTopEndpoints(
    logs: AuditLog[],
    limit: number = 10,
  ): Array<{ endpoint: string; count: number }> {
    const endpoints: Record<string, number> = {};
    logs.forEach(log => {
      const endpoint = `${log.metadata.method || 'UNKNOWN'} ${log.metadata.path || 'unknown'}`;
      endpoints[endpoint] = (endpoints[endpoint] || 0) + 1;
    });

    return Object.entries(endpoints)
      .sort(([, a], [, b]) => (b as number) - (a as number))
      .slice(0, limit)
      .map(([endpoint, count]) => ({ endpoint, count }));
  }
}

import { UserId } from '@core/value-objects/user-id.vo';
import { AuditLogId } from '@core/value-objects/audit-log-id.vo';
import { AggregateRoot } from '@core/events/domain-event.base';

/**
 * Niveles de severidad para logs de auditoría
 *
 * **Propósito**: Categoriza la importancia y severidad de los eventos registrados
 * para facilitar filtrado, alertas y análisis de seguridad.
 *
 * @example
 * ```typescript
 * // Login exitoso - información general
 * AuditLog.create('info', 'auth', 'login', 'User logged in')
 *
 * // Intento de login fallido - advertencia
 * AuditLog.create('warn', 'security', 'login', 'Failed login attempt')
 *
 * // Error crítico del sistema - error crítico
 * AuditLog.create('critical', 'system', 'error', 'Database connection lost')
 * ```
 */
export type AuditLogLevel = 'info' | 'warn' | 'error' | 'debug' | 'critical';

/**
 * Tipos de eventos que pueden ser registrados en auditoría
 *
 * **Propósito**: Categoriza los eventos por área funcional para facilitar
 * análisis, reportes y compliance con regulaciones de seguridad.
 *
 * **Categorías**:
 * - **auth**: Eventos de autenticación y autorización
 * - **user**: Operaciones CRUD de usuarios
 * - **role/permission**: Cambios en control de acceso
 * - **company**: Operaciones a nivel empresa (multi-tenancy)
 * - **system**: Eventos internos del sistema
 * - **api**: Llamadas API y respuestas
 * - **database**: Operaciones de base de datos
 * - **security**: Eventos relacionados con seguridad
 * - **exception**: Errores y excepciones
 * - **transaction**: Transacciones de base de datos
 */
export type AuditLogType =
  | 'auth'
  | 'user'
  | 'role'
  | 'permission'
  | 'company'
  | 'system'
  | 'api'
  | 'database'
  | 'security'
  | 'exception'
  | 'transaction'
  | 'bot';

/**
 * Acciones específicas que pueden ser auditadas
 *
 * **Propósito**: Especifica la acción concreta realizada, complementando
 * el tipo de evento para proporcionar contexto granular.
 *
 * **Categorías**:
 * - **CRUD**: create, read, update, delete
 * - **Auth**: login, logout, register, verify, reset
 * - **Authorization**: assign, revoke, access
 * - **System**: error, exception
 * - **Transactions**: transaction_start, transaction_commit, transaction_rollback
 */
export type AuditLogAction =
  | 'create'
  | 'read'
  | 'update'
  | 'delete'
  | 'login'
  | 'logout'
  | 'register'
  | 'verify'
  | 'reset'
  | 'assign'
  | 'revoke'
  | 'access'
  | 'error'
  | 'exception'
  | 'transaction_start'
  | 'transaction_commit'
  | 'transaction_rollback';

/**
 * Metadata estructurada para logs de auditoría
 *
 * **Propósito**: Proporciona contexto detallado para cada evento auditado,
 * incluyendo información técnica, de usuario y de aplicación necesaria
 * para debugging, análisis de seguridad y compliance.
 *
 * **Categorías de información**:
 * - **Request**: Detalles de la petición HTTP
 * - **User/Session**: Información del usuario y sesión
 * - **Application**: Contexto de la aplicación
 * - **Error**: Información de errores y excepciones
 * - **Performance**: Métricas de rendimiento
 * - **Business**: Contexto de negocio y datos
 *
 * @example
 * ```typescript
 * const metadata: IAuditLogMetadata = {
 *   // Request info
 *   method: 'POST',
 *   url: '/api/auth/login',
 *   statusCode: 401,
 *
 *   // User context
 *   userAgent: 'Mozilla/5.0...',
 *   ipAddress: '192.168.1.100',
 *
 *   // Error context
 *   errorMessage: 'Invalid password',
 *   duration: 150
 * };
 * ```
 */
export interface IAuditLogMetadata {
  // Request information
  /** Método HTTP (GET, POST, PUT, DELETE) */
  method?: string;
  /** URL de la petición */
  url?: string;
  /** Código de estado HTTP de respuesta */
  statusCode?: number;
  /** Headers HTTP relevantes */
  headers?: Record<string, string>;
  /** Query parameters de la petición */
  query?: Record<string, unknown>;
  /** Path parameters de la petición */
  params?: Record<string, unknown>;
  /** Body de la petición (sanitizado) */
  body?: Record<string, unknown>;

  // User/Session information
  /** User agent del cliente */
  userAgent?: string;
  /** Dirección IP del cliente */
  ipAddress?: string;
  /** ID de la sesión activa */
  sessionId?: string;
  /** Token de sesión */
  sessionToken?: string;

  // Application context
  /** Nombre de la aplicación o microservicio */
  application?: string;
  /** Módulo o área funcional */
  module?: string;
  /** Controller que maneja la petición */
  controller?: string;
  /** Handler o método específico */
  handler?: string;

  // Error/Exception information
  /** Mensaje de error legible */
  errorMessage?: string;
  /** Stack trace del error */
  errorStack?: string;
  /** Código de error específico */
  errorCode?: string;
  /** Tipo de excepción */
  exceptionType?: string;

  // Performance information
  /** Duración de la operación en millisegundos */
  duration?: number;

  // Additional context
  /** Recurso afectado (tabla, entidad, etc.) */
  resource?: string;
  /** ID específico del recurso */
  resourceId?: string;
  /** Valor previo (para updates) */
  previousValue?: unknown;
  /** Nuevo valor (para creates/updates) */
  newValue?: unknown;

  // Custom metadata
  /** Campos adicionales específicos del contexto */
  [key: string]: unknown;
}

/**
 * Entidad de dominio AuditLog - Sistema integral de auditoría
 *
 * **Propósito**: Entidad principal para el registro comprehensivo de todos los eventos
 * del sistema, proporcionando trazabilidad completa, análisis de seguridad y compliance.
 *
 * **Responsabilidades**:
 * - Registra todas las actividades de usuarios y sistema
 * - Proporciona contexto detallado para debugging y análisis
 * - Implementa sanitización automática de datos sensibles
 * - Soporta categorización multi-dimensional (level, type, action)
 * - Facilita compliance con regulaciones de auditoría
 *
 * **Arquitectura**: Siguiendo DDD (Domain-Driven Design)
 * - Extend AggregateRoot para soporte de domain events
 * - Inmutable después de creación (audit trail integrity)
 * - Factory methods para diferentes tipos de logs
 * - Value Objects para IDs tipados
 *
 * **Características de seguridad**:
 * - Sanitización automática de información sensible
 * - Detección de contenido sensible
 * - Formateo consistente para análisis
 * - Metadata estructurada y extensible
 *
 * @example
 * ```typescript
 * // Crear log de autenticación exitosa
 * const authLog = AuditLog.createAuthLog(
 *   'login',
 *   'User logged in successfully',
 *   userId,
 *   { ipAddress: '192.168.1.100', duration: 150 }
 * );
 *
 * // Crear log de error de seguridad
 * const securityLog = AuditLog.createSecurityLog(
 *   'access',
 *   'Unauthorized access attempt',
 *   null,
 *   { resource: '/admin/users', method: 'GET' },
 *   'error'
 * );
 *
 * // Verificar sensibilidad y obtener metadata sanitizada
 * if (auditLog.isSensitive()) {
 *   const safeMetadata = auditLog.getSanitizedMetadata();
 * }
 * ```
 *
 * **Integración con sistema**:
 * - Usado por AuditLogService para lógica de negocio
 * - Persistido via AuditLogRepository
 * - Procesado asincrónicamente por AuditLogQueueService
 * - Limpieza automática por AuditLogCleanupService
 */
export class AuditLog extends AggregateRoot {
  private constructor(
    public readonly id: AuditLogId,
    public readonly level: AuditLogLevel,
    public readonly type: AuditLogType,
    public readonly action: AuditLogAction,
    public readonly message: string,
    public readonly userId: UserId | null,
    public readonly metadata: IAuditLogMetadata,
    public readonly timestamp: Date,
    public readonly context: string,
  ) {
    super();
  }

  /**
   * Factory method principal para crear nuevos logs de auditoría
   *
   * **Propósito**: Punto de entrada unificado para crear cualquier tipo de audit log
   * con todos los parámetros requeridos y generación automática de ID y timestamp.
   *
   * @param level - Nivel de severidad del evento
   * @param type - Tipo/categoría del evento
   * @param action - Acción específica realizada
   * @param message - Mensaje descriptivo del evento
   * @param userId - ID del usuario asociado (null para eventos del sistema)
   * @param metadata - Contexto adicional estructurado
   * @param context - Contexto de la aplicación donde ocurrió el evento
   * @returns Nueva instancia de AuditLog inmutable
   */
  static create(
    level: AuditLogLevel,
    type: AuditLogType,
    action: AuditLogAction,
    message: string,
    userId: UserId | null = null,
    metadata: IAuditLogMetadata = {},
    context: string = 'system',
  ): AuditLog {
    const id = AuditLogId.generate();
    const timestamp = new Date();

    return new AuditLog(id, level, type, action, message, userId, metadata, timestamp, context);
  }

  /**
   * Factory method para logs de autenticación
   *
   * **Propósito**: Crea logs específicos para eventos de autenticación
   * con configuración predeterminada optimizada para auth events.
   *
   * **Casos de uso**:
   * - Login exitoso/fallido
   * - Logout
   * - Registro de usuarios
   * - Verificación de email/OTP
   * - Reseteo de password
   *
   * @param action - Acción de auth (login, logout, register, etc.)
   * @param message - Descripción del evento de auth
   * @param userId - Usuario involucrado (null para intentos fallidos sin usuario)
   * @param metadata - Contexto como IP, user agent, duración, etc.
   * @param level - Nivel de severidad ('info' por defecto)
   */
  static createAuthLog(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    metadata: IAuditLogMetadata,
    level: AuditLogLevel = 'info',
  ): AuditLog {
    return AuditLog.create(level, 'auth', action, message, userId, metadata, 'auth');
  }

  /**
   * Factory method para logs de seguridad
   *
   * **Propósito**: Crea logs específicos para eventos relacionados con seguridad
   * con nivel de advertencia por defecto para facilitar monitoreo.
   *
   * **Casos de uso**:
   * - Intentos de acceso no autorizado
   * - Violaciones de permisos
   * - Ataques detectados
   * - Cambios en configuración de seguridad
   * - Anomalías en patrones de uso
   *
   * @param action - Tipo de evento de seguridad
   * @param message - Descripción del evento de seguridad
   * @param userId - Usuario involucrado (null para ataques anónimos)
   * @param metadata - Contexto de seguridad (IP, resource, método, etc.)
   * @param level - Nivel de severidad ('warn' por defecto)
   */
  static createSecurityLog(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    metadata: IAuditLogMetadata,
    level: AuditLogLevel = 'warn',
  ): AuditLog {
    return AuditLog.create(level, 'security', action, message, userId, metadata, 'security');
  }

  /**
   * Factory method para logs de excepciones
   *
   * **Propósito**: Crea logs específicos para errores y excepciones del sistema
   * con configuración optimizada para debugging y resolución de problemas.
   *
   * **Casos de uso**:
   * - Excepciones no controladas
   * - Errores de validación de dominio
   * - Fallos en servicios externos
   * - Errores de configuración
   * - Timeouts y fallos de red
   *
   * @param message - Descripción del error o excepción
   * @param userId - Usuario asociado al contexto del error
   * @param metadata - Contexto técnico (stack trace, error code, etc.)
   * @param level - Nivel de severidad ('error' por defecto)
   */
  static createExceptionLog(
    message: string,
    userId: UserId | null,
    metadata: IAuditLogMetadata,
    level: AuditLogLevel = 'error',
  ): AuditLog {
    return AuditLog.create(level, 'exception', 'exception', message, userId, metadata, 'exception');
  }

  /**
   * Factory method para logs de API
   *
   * **Propósito**: Crea logs específicos para llamadas API y respuestas
   * con contexto optimizado para monitoreo de servicios.
   *
   * **Casos de uso**:
   * - Request/Response logging
   * - Monitoreo de performance de API
   * - Análisis de uso de endpoints
   * - Rate limiting events
   * - Validación de inputs
   *
   * @param action - Tipo de operación API (create, read, update, delete, etc.)
   * @param message - Descripción de la operación API
   * @param userId - Usuario que realiza la llamada
   * @param metadata - Contexto HTTP (method, url, status, duration, etc.)
   * @param level - Nivel de severidad ('info' por defecto)
   */
  static createApiLog(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    metadata: IAuditLogMetadata,
    level: AuditLogLevel = 'info',
  ): AuditLog {
    return AuditLog.create(level, 'api', action, message, userId, metadata, 'api');
  }

  /**
   * Factory method para logs de transacciones
   *
   * **Propósito**: Crea logs específicos para operaciones de base de datos
   * con contexto optimizado para debugging de transacciones.
   *
   * **Casos de uso**:
   * - Inicio/commit/rollback de transacciones
   * - Operaciones CRUD en entidades
   * - Violaciones de constraints
   * - Deadlocks y timeouts
   * - Migraciones de datos
   *
   * @param action - Tipo de operación de transacción
   * @param message - Descripción de la operación de BD
   * @param userId - Usuario asociado a la transacción
   * @param metadata - Contexto de BD (tables, duration, query info, etc.)
   * @param level - Nivel de severidad ('debug' por defecto)
   */
  static createTransactionLog(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    metadata: IAuditLogMetadata,
    level: AuditLogLevel = 'debug',
  ): AuditLog {
    return AuditLog.create(level, 'transaction', action, message, userId, metadata, 'transaction');
  }

  /**
   * Obtiene entrada de log formateada para visualización
   *
   * **Propósito**: Genera una representación textual estructurada del log
   * siguiendo un formato consistente para análisis manual y herramientas de logging.
   *
   * **Formato**: `TIMESTAMP [LEVEL] [CONTEXT] [TYPE:ACTION] [USER_INFO] - MESSAGE`
   *
   * @returns String formateado con todos los elementos clave del audit log
   *
   * @example
   * ```typescript
   * const log = AuditLog.createAuthLog('login', 'User logged in', userId, metadata);
   * console.log(log.getFormattedEntry());
   * // Output: "2025-08-06T19:30:15.123Z [INFO] [AUTH] [AUTH:LOGIN] [User: user-123] - User logged in"
   * ```
   */
  getFormattedEntry(): string {
    const timestamp = this.timestamp.toISOString();
    const userInfo = this.userId ? `[User: ${this.userId.getValue()}]` : '[System]';
    const contextInfo = `[${this.context.toUpperCase()}]`;
    const levelInfo = `[${this.level.toUpperCase()}]`;
    const typeInfo = `[${this.type.toUpperCase()}:${this.action.toUpperCase()}]`;

    return `${timestamp} ${levelInfo} ${contextInfo} ${typeInfo} ${userInfo} - ${this.message}`;
  }

  /**
   * Verifica si este log contiene información sensible
   *
   * **Propósito**: Identifica logs que requieren manejo especial de seguridad
   * para aplicar políticas de retención diferenciadas y controles de acceso.
   *
   * **Criterios de sensibilidad**:
   * - Tipos sensibles: auth, security, exception
   * - Acciones sensibles: login, logout, reset, error, exception
   *
   * @returns true si el log contiene información sensible
   *
   * @example
   * ```typescript
   * const authLog = AuditLog.createAuthLog('login', 'Login failed', null, metadata);
   * if (authLog.isSensitive()) {
   *   // Aplicar políticas especiales de retención y acceso
   *   applySecurityPolicies(authLog);
   * }
   * ```
   */
  isSensitive(): boolean {
    const sensitiveTypes: AuditLogType[] = ['auth', 'security', 'exception'];
    const sensitiveActions: AuditLogAction[] = ['login', 'logout', 'reset', 'error', 'exception'];

    return sensitiveTypes.includes(this.type) || sensitiveActions.includes(this.action);
  }

  /**
   * Obtiene metadata sanitizada (remueve información sensible)
   *
   * **Propósito**: Proporciona versión segura de la metadata para logging público,
   * análisis no privilegiado y cumplimiento de políticas de privacidad.
   *
   * **Campos sanitizados**: password, passwordHash, token, secret, key, authorization
   * y cualquier campo anidado que contenga estos términos.
   *
   * **Comportamiento**:
   * - Reemplaza valores sensibles con '[REDACTED]'
   * - Procesa recursivamente objetos y arrays anidados
   * - Preserva estructura de datos original
   * - No modifica la instancia original (immutable)
   *
   * @returns Copia de metadata con información sensible removida
   *
   * @example
   * ```typescript
   * const metadata = {
   *   email: 'user@example.com',
   *   password: 'secretPass123',
   *   userAgent: 'Mozilla/5.0...',
   *   authToken: 'jwt-token-here'
   * };
   *
   * const log = AuditLog.create('info', 'auth', 'login', 'Login attempt', null, metadata);
   * const safeMetadata = log.getSanitizedMetadata();
   *
   * // safeMetadata = {
   * //   email: 'user@example.com',
   * //   password: '[REDACTED]',
   * //   userAgent: 'Mozilla/5.0...',
   * //   authToken: '[REDACTED]'
   * // }
   * ```
   */
  getSanitizedMetadata(): IAuditLogMetadata {
    const sanitized = { ...this.metadata };

    // Remove sensitive fields
    const sensitiveFields = ['password', 'passwordHash', 'token', 'secret', 'key', 'authorization'];

    Object.keys(sanitized).forEach(key => {
      if (sensitiveFields.some(field => key.toLowerCase().includes(field))) {
        sanitized[key] = '[REDACTED]';
      }

      // Sanitize nested objects
      if (typeof sanitized[key] === 'object' && sanitized[key] !== null) {
        sanitized[key] = this.sanitizeObject(sanitized[key]);
      }
    });

    return sanitized;
  }

  private sanitizeObject(obj: any): any {
    if (Array.isArray(obj)) {
      return obj.map(item => this.sanitizeObject(item));
    }

    if (typeof obj === 'object' && obj !== null) {
      const sanitized: any = {};
      Object.keys(obj).forEach(key => {
        const sensitiveFields = [
          'password',
          'passwordHash',
          'token',
          'secret',
          'key',
          'authorization',
        ];
        if (sensitiveFields.some(field => key.toLowerCase().includes(field))) {
          sanitized[key] = '[REDACTED]';
        } else {
          sanitized[key] = this.sanitizeObject(obj[key]);
        }
      });

      return sanitized;
    }

    return obj;
  }
}

/**
 * Enum que define todas las posibles razones de fallo en la autenticación
 *
 * **Propósito**: Proporciona un conjunto tipado y exhaustivo de razones por las cuales
 * una autenticación puede fallar, permitiendo logging específico y manejo de errores granular.
 *
 * **Casos de uso**:
 * - Audit logging con razones específicas de fallo
 * - Respuestas de error diferenciadas por tipo de fallo
 * - Análisis de seguridad y patrones de fallo
 * - Debugging y troubleshooting de problemas de autenticación
 *
 * **Arquitectura**: Siguiendo Clean Architecture, este enum reside en la capa de dominio
 * y puede ser usado por todas las capas superiores (aplicación, presentación)
 *
 * @example
 * ```typescript
 * // En un servicio de validación
 * if (!user) {
 *   return {
 *     success: false,
 *     failureReason: AuthFailureReason.USER_NOT_FOUND
 *   };
 * }
 *
 * // En logging de auditoría
 * this.auditLogService.logSecurity('login', `Login failed: ${reason}`, ...);
 * ```
 */
export enum AuthFailureReason {
  /** Usuario no existe en la base de datos */
  USER_NOT_FOUND = 'user_not_found',

  /** Password proporcionado es incorrecto */
  INVALID_PASSWORD = 'invalid_password',

  /** Cuenta de usuario está desactivada */
  USER_INACTIVE = 'user_inactive',

  /** Usuario está baneado temporalmente o permanentemente */
  USER_BANNED = 'user_banned',

  /** Email no ha sido verificado y la verificación está habilitada */
  EMAIL_NOT_VERIFIED = 'email_not_verified',

  /** Se requiere autenticación de dos factores (OTP) */
  OTP_REQUIRED = 'otp_required',

  /** Código OTP proporcionado es inválido */
  OTP_INVALID = 'otp_invalid',

  /** Sesión ha expirado */
  SESSION_EXPIRED = 'session_expired',

  /** Demasiados intentos de autenticación fallidos */
  TOO_MANY_ATTEMPTS = 'too_many_attempts',

  /** Cuenta bloqueada por políticas de seguridad */
  ACCOUNT_LOCKED = 'account_locked',

  /** Formato de email es inválido */
  INVALID_EMAIL_FORMAT = 'invalid_email_format',

  /** Error del sistema durante el proceso de autenticación */
  SYSTEM_ERROR = 'system_error',

  /** Usuario BOT no tiene token activo válido */
  BOT_NO_ACTIVE_TOKEN = 'bot_no_active_token',
}

/**
 * Interfaz que representa el resultado de una validación de autenticación
 *
 * **Propósito**: Estructura estandarizada para comunicar el resultado de validaciones
 * de credenciales, incluyendo detalles específicos sobre éxito o fallo.
 *
 * **Casos de uso**:
 * - Resultado de UserService.validateCredentials()
 * - Base para construir respuestas de login
 * - Información para audit logging
 * - Debugging y troubleshooting
 *
 * @example
 * ```typescript
 * // Uso típico en servicio de validación
 * const result: IAuthValidationResult = {
 *   success: false,
 *   failureReason: AuthFailureReason.USER_BANNED,
 *   message: 'Account is banned until 2025-12-31',
 *   details: {
 *     bannedUntil: new Date('2025-12-31'),
 *     banReason: 'Policy violation'
 *   }
 * };
 * ```
 */
export interface IAuthValidationResult {
  /** Indica si la validación fue exitosa */
  success: boolean;

  /** Entidad User si la validación fue exitosa */
  user?: any; // Will be User entity

  /** Razón específica del fallo si success=false */
  failureReason?: AuthFailureReason;

  /** Mensaje descriptivo del resultado */
  message?: string;

  /** Detalles adicionales específicos del tipo de fallo/éxito */
  details?: {
    /** Fecha hasta cuando el usuario está baneado */
    bannedUntil?: Date;
    /** Razón del ban */
    banReason?: string;
    /** Intentos restantes antes de bloqueo */
    attemptsRemaining?: number;
    /** Tiempo hasta desbloqueo */
    lockoutTime?: Date;
    /** Si se requiere verificación de email */
    emailVerificationRequired?: boolean;
    /** Si se requiere OTP */
    requiresOtp?: boolean;
    /** Mensaje de error del sistema */
    systemError?: string;
    /** Tipo de error técnico */
    errorType?: string;
  };
}

/**
 * Interfaz que representa el resultado completo del flujo de login
 *
 * **Propósito**: Estructura unificada para manejar todos los posibles resultados
 * del proceso de login, incluyendo flujos multi-paso como verificación de email y 2FA.
 *
 * **Casos de uso**:
 * - Resultado de AuthenticationValidationService.validateLoginFlow()
 * - Comunicación entre capa de aplicación y presentación
 * - Manejo de flujos de autenticación complejos
 * - Base para respuestas HTTP estandarizadas
 *
 * **Flujos soportados**:
 * - Login exitoso completo (tokens + user data)
 * - Login que requiere verificación de email
 * - Login que requiere 2FA/OTP
 * - Login fallido con razones específicas
 *
 * @example
 * ```typescript
 * // Login exitoso completo
 * const result: ILoginFlowResult = {
 *   success: true,
 *   nextStep: 'complete',
 *   authResponse: { accessToken, refreshToken, user }
 * };
 *
 * // Login que requiere email verification
 * const result: ILoginFlowResult = {
 *   success: true,
 *   nextStep: 'email_verification',
 *   requiresEmailVerification: true,
 *   userId: 'user-123',
 *   email: 'user@example.com'
 * };
 *
 * // Login fallido
 * const result: ILoginFlowResult = {
 *   success: false,
 *   failureReason: AuthFailureReason.INVALID_PASSWORD,
 *   auditDetails: { email, duration: 150, ... }
 * };
 * ```
 */
export interface ILoginFlowResult {
  /** Indica si el proceso de login fue exitoso */
  success: boolean;

  /** Siguiente paso requerido en el flujo de login */
  nextStep?: 'complete' | 'email_verification' | 'otp_required';

  /** Respuesta de autenticación completa (si nextStep='complete') */
  authResponse?: any; // AuthResponse

  /** Indica si se requiere verificación de email */
  requiresEmailVerification?: boolean;

  /** Indica si se requiere código OTP/2FA */
  requiresOtp?: boolean;

  /** ID del usuario para continuación del flujo */
  userId?: string;

  /** Email del usuario para continuación del flujo */
  email?: string;

  /** Mensaje descriptivo para el usuario */
  message?: string;

  /** Razón del fallo si success=false */
  failureReason?: AuthFailureReason;

  /** Detalles para audit logging (siempre incluidos) */
  auditDetails?: {
    /** Email del intento de login */
    email: string;
    /** User agent del cliente */
    userAgent?: string;
    /** Dirección IP del cliente */
    ipAddress?: string;
    /** Duración del proceso en millisegundos */
    duration: number;
    /** Razón del fallo si aplica */
    failureReason?: AuthFailureReason;
    /** Cualquier metadata adicional */
    [key: string]: any;
  };
}

/**
 * Interface for BOT Token Provider
 * Infrastructure implementations must follow this contract
 */
export interface IBotTokenProvider {
  /**
   * Generate a BOT token (technical implementation)
   */
  generateToken(params: {
    botUserId: string;
    botEmail: string;
    tokenId: string;
    companyId?: string;
  }): Promise<{
    accessToken: string;
    expiresIn: string;
    tokenId: string;
  }>;

  /**
   * Validate a BOT token
   */
  validateToken(token: string): Promise<any | null>;

  /**
   * Revoke a BOT token
   */
  revokeToken(tokenId: string): Promise<boolean>;

  /**
   * List active BOT tokens
   */
  listActiveTokens(): Promise<
    Array<{
      tokenId: string;
      botUserId: string;
      companyId?: string;
      createdAt: Date;
    }>
  >;
}

import { Injectable, OnModuleInit, OnModuleDestroy, Inject } from '@nestjs/common';
import {
  AuditLog,
  AuditLogLevel,
  AuditLogType,
  AuditLogAction,
  IAuditLogMetadata,
} from '@core/entities/audit-log.entity';
import { UserId } from '@core/value-objects/user-id.vo';
import { ErrorSanitizationUtil } from '@core/utils/error-sanitization.util';
import { BusinessConfigurationService } from './business-configuration.service';
import { IAuditLogRepository } from '@core/repositories/audit-log.repository.interface';
import { AUDIT_LOG_REPOSITORY, LOGGER_SERVICE } from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';
import { Mutex } from 'async-mutex';

/**
 * Interfaz para logs en cola de procesamiento
 *
 * **Propósito**: Estructura optimizada para mantener logs en memoria
 * durante el procesamiento asíncrono, minimizando overhead.
 */
interface IQueuedAuditLog {
  level: AuditLogLevel;
  type: AuditLogType;
  action: AuditLogAction;
  message: string;
  userId: UserId | null;
  metadata: IAuditLogMetadata;
  context: string;
  timestamp: Date;
}

/**
 * Servicio de cola de alta performance para audit logs
 *
 * **Propósito**: Servicio especializado en procesamiento asíncrono de audit logs
 * optimizado para manejar millones de requests con mínimo impacto en performance.
 *
 * **Arquitectura de performance**:
 * - **Queue en memoria**: Array optimizado para O(1) enqueue
 * - **Procesamiento por lotes**: Batches de 1000 logs para máxima eficiencia
 * - **Intervalo configurable**: Procesamiento cada 2 segundos por defecto
 * - **Gestión de memoria**: Límites de cola y emergency flush automático
 * - **Fallback graceful**: Nunca bloquea operaciones principales
 *
 * **Características clave**:
 * - **Non-blocking**: Enqueue es O(1) y no bloquea nunca
 * - **Resiliente**: Continúa funcionando ante errores de BD
 * - **Configurable**: Parámetros ajustables por environment
 * - **Monitoreable**: Estadísticas de cola y performance
 * - **Auto-cleanup**: Previene memory leaks automáticamente
 * - **Transacción-independiente**: Logs persisten fuera de transacciones
 *
 * **Políticas de seguridad**:
 * - Logs se guardan FUERA de contexto transaccional
 * - Sanitización automática de campos sensibles
 * - Truncado inteligente de payloads grandes
 * - Drop de logs antiguos bajo presión de memoria
 *
 * **Performance targets**:
 * - 50,000+ logs/segundo enqueue rate
 * - <1ms latencia para enqueue
 * - 1,000 logs procesados por lote
 * - Máximo 50,000 logs en memoria simultanea
 *
 * @example
 * ```typescript
 * // Uso típico (ultra-rápido)
 * this.queueService.enqueue('info', 'auth', 'login', 'User logged in', userId, metadata);
 *
 * // Verificar estadísticas
 * const stats = this.queueService.getQueueStats();
 * console.log(`Queue size: ${stats.queueSize}, Processing: ${stats.isProcessing}`);
 *
 * // Emergency flush
 * const processed = await this.queueService.forceFlush();
 * ```
 *
 * **Integración con sistema**:
 * - Usado por AuditLogService para eventos async
 * - Iniciado automáticamente en OnModuleInit
 * - Flush automático en OnModuleDestroy
 * - Configuración via BusinessConfigurationService
 */
@Injectable()
export class AuditLogQueueService implements OnModuleInit, OnModuleDestroy {
  private queue: IQueuedAuditLog[] = [];
  private processingInterval: NodeJS.Timeout | null = null;
  private isProcessing = false;
  private droppedLogsCount = 0; // Track dropped logs for monitoring
  private emergencyFlushPromise: Promise<void> | null = null;
  private emergencyFlushId = 0; // Atomic counter for emergency flush tracking
  private readonly MAX_EMERGENCY_FLUSH_ID = Number.MAX_SAFE_INTEGER - 1000; // Safety margin // Atomic emergency flush control

  // Rate limiting for DoS protection (with memory leak protection)
  private readonly requestCounts = new Map<string, { count: number; resetTime: number }>();
  private readonly MAX_REQUESTS_PER_MINUTE = 1000; // Per user/IP
  private readonly RATE_LIMIT_WINDOW = 60000; // 1 minute
  private readonly MAX_RATE_LIMIT_ENTRIES = 10000; // Prevent unbounded growth
  private readonly CLEANUP_THRESHOLD = 8000; // Start cleanup at 80% capacity

  // Thread-safety primitives
  private readonly queueMutex = new Mutex();
  private readonly processingMutex = new Mutex();

  // Configuration for high performance
  private readonly BATCH_SIZE = 1000; // Process 1000 logs at once
  private readonly PROCESS_INTERVAL = 2000; // Process every 2 seconds
  private readonly MAX_QUEUE_SIZE = 50000; // Max logs in memory
  private readonly EMERGENCY_FLUSH_SIZE = 45000; // Emergency flush threshold

  constructor(
    private readonly businessConfigService: BusinessConfigurationService,
    @Inject(AUDIT_LOG_REPOSITORY)
    private readonly auditLogRepository: IAuditLogRepository,
    @Inject(LOGGER_SERVICE)
    private readonly logger: ILogger,
  ) {
    this.logger.setContext(AuditLogQueueService.name);
  }

  onModuleInit() {
    // Start background processing
    this.startProcessing();
  }

  async onModuleDestroy() {
    // Flush remaining logs before shutdown
    this.stopProcessing();

    // Thread-safe final flush
    const release = await this.queueMutex.acquire();
    try {
      if (this.queue.length > 0) {
        await this.processQueueBatch();
      }
    } finally {
      release();
    }
  }

  /**
   * Añade entrada de log a la cola (ultra-rápido, no-bloqueante)
   *
   * **Propósito**: Método principal ultra-optimizado para añadir logs
   * a la cola con latencia mínima (<1ms) y garantía de no-bloqueo.
   *
   * **Optimizaciones de performance**:
   * - Operación O(1) - array.push() directo
   * - Verificación instantánea de feature flags
   * - Emergency flush automático bajo presión
   * - Drop inteligente de logs antiguos (previene memory overflow)
   * - Optimización de metadata en tiempo real
   *
   * **Políticas de gestión**:
   * - Skip si audit logging está deshabilitado
   * - Emergency flush a 45,000 logs (90% capacidad)
   * - Drop oldest logs a 50,000 (100% capacidad)
   * - Optimización automática de metadata
   *
   * @param level - Nivel de severidad del log
   * @param type - Categoría del evento
   * @param action - Acción específica realizada
   * @param message - Mensaje descriptivo
   * @param userId - Usuario asociado (opcional)
   * @param metadata - Contexto adicional (será optimizado)
   * @param context - Contexto de aplicación
   */
  async enqueue(
    level: AuditLogLevel,
    type: AuditLogType,
    action: AuditLogAction,
    message: string,
    userId: UserId | null = null,
    metadata: IAuditLogMetadata = {},
    context: string = 'system',
  ): Promise<void> {
    // Skip if audit logging is disabled
    const featureFlags = this.businessConfigService.getFeatureFlags();
    if (!featureFlags.auditLoggingEnabled) {
      return;
    }

    // Input validation and sanitization
    if (!this.validateInput(level, type, action, message, metadata)) {
      return; // Reject invalid input silently to prevent log spam
    }

    // Rate limiting check
    if (!this.checkRateLimit(userId?.toString() || 'anonymous')) {
      return; // Reject if rate limit exceeded
    }

    // Thread-safe enqueue operation
    await this.queueMutex.runExclusive(() => {
      // Security: Atomic emergency flush with proper race condition prevention
      if (this.queue.length >= this.EMERGENCY_FLUSH_SIZE) {
        // Triple-check pattern with atomic counter
        if (!this.emergencyFlushPromise) {
          // Increment atomic counter with overflow protection
          if (this.emergencyFlushId >= this.MAX_EMERGENCY_FLUSH_ID) {
            this.emergencyFlushId = 1; // Reset to prevent overflow
          }
          const currentFlushId = ++this.emergencyFlushId;

          // Create promise with unique ID for atomic cleanup
          const flushPromise = this.emergencyFlush()
            .catch(error => {
              // Log error but don't propagate to prevent blocking enqueue
              const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'emergency-flush');
              console.error(`Emergency flush ${currentFlushId} failed:`, sanitizedError.message);
            })
            .finally(() => {
              // Atomic cleanup - only reset if this flush ID is still active
              if (
                this.emergencyFlushPromise === flushPromise &&
                this.emergencyFlushId === currentFlushId
              ) {
                this.emergencyFlushPromise = null;
              }
            });

          // Final atomic assignment with double-check
          if (!this.emergencyFlushPromise) {
            this.emergencyFlushPromise = flushPromise;
          }
        }
      }

      // Drop oldest logs if queue is at max capacity (prevent memory overflow)
      if (this.queue.length >= this.MAX_QUEUE_SIZE) {
        // Drop in batches for better performance (shift() is O(n) on large arrays)
        const dropCount = Math.min(this.BATCH_SIZE, this.queue.length);
        this.queue.splice(0, dropCount);
        this.droppedLogsCount += dropCount;

        // Log critical overflow (but don't queue it to avoid recursion)
        console.warn(
          `CRITICAL: Audit log queue overflow - dropped ${dropCount} old logs. Queue: ${this.queue.length}/${this.MAX_QUEUE_SIZE}`,
        );
      }

      // Add to queue (O(1) operation)
      this.queue.push({
        level,
        type,
        action,
        message,
        userId,
        metadata: this.optimizeMetadata(metadata),
        context,
        timestamp: new Date(),
      });
    });
  }

  /**
   * Get queue statistics
   */
  getQueueStats(): {
    queueSize: number;
    isProcessing: boolean;
    maxQueueSize: number;
    batchSize: number;
    processInterval: number;
    droppedLogsCount: number;
    queueUtilization: number;
  } {
    // Thread-safe read of queue size
    const queueSize = this.queueMutex.isLocked() ? -1 : this.queue.length;

    return {
      queueSize,
      isProcessing: this.isProcessing,
      maxQueueSize: this.MAX_QUEUE_SIZE,
      batchSize: this.BATCH_SIZE,
      processInterval: this.PROCESS_INTERVAL,
      droppedLogsCount: this.droppedLogsCount,
      queueUtilization: Math.round((queueSize / this.MAX_QUEUE_SIZE) * 100), // Utilization percentage
    };
  }

  /**
   * Force flush queue (for testing or emergency)
   */
  async forceFlush(): Promise<number> {
    // Thread-safe force flush
    const release = await this.processingMutex.acquire();
    try {
      const processed = await this.processQueueBatch();

      return processed;
    } finally {
      release();
    }
  }

  /**
   * Start background processing
   */
  private startProcessing(): void {
    this.processingInterval = setInterval(async () => {
      // Thread-safe check and process
      if (this.processingMutex.isLocked()) {
        return; // Already processing
      }

      const hasWork = await this.queueMutex.runExclusive(() => this.queue.length > 0);
      if (hasWork) {
        this.processQueueBatch().catch(console.error);
      }
    }, this.PROCESS_INTERVAL);
  }

  /**
   * Stop background processing
   */
  private stopProcessing(): void {
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
      this.processingInterval = null;
    }
  }

  /**
   * Emergency flush when queue is getting too large (atomic operation)
   */
  private async emergencyFlush(): Promise<void> {
    try {
      // Security: Check if processing is already happening to avoid conflicts
      if (this.processingMutex.isLocked()) {
        // Already processing, this emergency flush will be handled by regular processing
        return;
      }

      // Acquire processing mutex for exclusive access
      const release = await this.processingMutex.acquire();
      try {
        // Process multiple batches if queue is very large
        let processed = 0;
        const maxEmergencyProcessing = 5; // Max 5 batches per emergency flush

        for (let i = 0; i < maxEmergencyProcessing; i++) {
          const batchProcessed = await this.processQueueBatch();
          processed += batchProcessed;

          // Stop if queue is under control or no more logs to process
          if (batchProcessed === 0 || this.queue.length < this.EMERGENCY_FLUSH_SIZE) {
            break;
          }
        }

        if (processed > 0) {
          console.warn(
            `Emergency flush completed: processed ${processed} logs, queue size: ${this.queue.length}`,
          );
        }
      } finally {
        release();
      }
    } catch (error) {
      // Log error with sanitization - don't re-throw to prevent blocking enqueue
      const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'emergency-flush-execution');
      console.error('Emergency flush error:', sanitizedError.message);
    }
  }

  /**
   * Process batch of logs (async, non-blocking)
   */
  private async processQueueBatch(): Promise<number> {
    // Thread-safe batch processing
    const release = await this.processingMutex.acquire();

    try {
      // Check if there's work to do
      const batch = await this.queueMutex.runExclusive(() => {
        if (this.queue.length === 0) {
          return [];
        }

        // Extract batch from queue atomically
        const batchSize = Math.min(this.BATCH_SIZE, this.queue.length);

        return this.queue.splice(0, batchSize);
      });

      if (batch.length === 0) {
        return 0;
      }

      this.isProcessing = true;
      let processed = 0;

      try {
        // Convert to domain entities
        const auditLogs = batch.map(item =>
          AuditLog.create(
            item.level,
            item.type,
            item.action,
            item.message,
            item.userId,
            item.metadata,
            item.context,
          ),
        );

        // Batch insert to database (will be handled by repository)
        await this.batchInsertLogs(auditLogs);
        processed = auditLogs.length;
      } catch (error) {
        const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'batch-processing');
        console.error('Failed to process audit log batch:', sanitizedError.message);
        // In case of error, we could optionally re-add failed logs to queue
        // but for performance, we'll just log the error and continue
      } finally {
        this.isProcessing = false;
      }

      return processed;
    } finally {
      release();
    }
  }

  /**
   * Inserta logs en lotes a la base de datos
   *
   * **IMPORTANTE**: Los logs se guardan FUERA de cualquier contexto transaccional
   * para garantizar que se persistan incluso si la operación principal falla o rollback.
   *
   * **Arquitectura de persistencia**:
   * - Cada log se guarda independientemente
   * - Promise.allSettled para máxima resiliencia
   * - Logging de errores sin afectar procesamiento
   * - Nunca lanza excepciones hacia arriba
   *
   * **Políticas de error**:
   * - Fallos individuales no afectan el lote
   * - Logging detallado de errores para debugging
   * - Continuación de procesamiento ante fallos
   * - Máximo 3 errores reportados por lote
   *
   * @param auditLogs - Array de logs para persistir
   */
  private async batchInsertLogs(auditLogs: AuditLog[]): Promise<void> {
    try {
      // Save logs individually to ensure each log is saved independently
      // This ensures audit logs are saved even if the main transaction fails
      const promises = auditLogs.map(log => this.saveSingleLog(log));
      const results = await Promise.allSettled(promises);

      // Log any failures (with sanitized error messages)
      const failures = results.filter(r => r.status === 'rejected');
      if (failures.length > 0) {
        this.logger.warn({
          message: 'Some audit logs failed to save',
          failedCount: failures.length,
          totalCount: auditLogs.length,
          errors: failures.slice(0, 3).map((f: any) => {
            const error = f.reason;
            const sanitized = ErrorSanitizationUtil.forLogging(error, 'batch-insert');

            return sanitized.message;
          }),
        });
      }
    } catch (error) {
      // Never throw - audit logging should not affect main operation (with sanitization)
      const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'critical-batch-insert');
      this.logger.error({
        message: 'Critical error in batch audit log insert',
        error: sanitizedError.message,
        logsCount: auditLogs.length,
      });
    }
  }

  /**
   * Save single audit log
   * IMPORTANT: This is executed OUTSIDE of any transaction to ensure
   * audit logs are always persisted regardless of transaction outcome
   */
  private async saveSingleLog(auditLog: AuditLog): Promise<void> {
    try {
      // Save without transaction context to ensure persistence
      await this.auditLogRepository.save(auditLog);

      // Log success for debugging (can be removed in production)
      this.logger.debug({
        message: 'Audit log saved successfully',
        auditLogId: auditLog.id.getValue(),
        type: auditLog.type,
        action: auditLog.action,
      });
    } catch (error) {
      // Log error but don't throw - audit failures should not affect main operation (with sanitization)
      const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'single-log-save');
      this.logger.error({
        message: 'Failed to save individual audit log',
        error: sanitizedError.message,
        auditLogData: {
          id: auditLog.id.getValue(),
          level: auditLog.level,
          type: auditLog.type,
          action: auditLog.action,
          message: ErrorSanitizationUtil.sanitizeMessage(auditLog.message || 'No message'),
        },
      });
    }
  }

  /**
   * Optimize metadata to reduce memory usage (prototype pollution protected)
   */
  private optimizeMetadata(metadata: IAuditLogMetadata): IAuditLogMetadata {
    // Security: Create object without prototype to prevent pollution
    const optimized = Object.create(null);

    // Safely copy only safe properties to prevent prototype pollution
    if (metadata && typeof metadata === 'object' && metadata.constructor === Object) {
      const safeKeys = Object.keys(metadata).filter(key => {
        // Security: Reject dangerous keys that could cause prototype pollution
        return (
          key !== '__proto__' &&
          key !== 'constructor' &&
          key !== 'prototype' &&
          !key.startsWith('__') &&
          key.length < 100
        ); // Prevent excessively long keys
      });

      for (const key of safeKeys) {
        const value = metadata[key];

        // Security: Only copy safe primitive types and plain objects
        if (value === null || value === undefined) {
          // Skip null/undefined to save space
          continue;
        } else if (typeof value === 'string') {
          // Truncate large text fields
          if (key === 'errorStack' && value.length > 2000) {
            optimized[key] = value.substring(0, 2000) + '... [truncated]';
          } else if (value.length > 5000) {
            optimized[key] = '[Large text truncated]';
          } else {
            optimized[key] = value;
          }
        } else if (typeof value === 'number' && isFinite(value)) {
          optimized[key] = value;
        } else if (typeof value === 'boolean') {
          optimized[key] = value;
        } else if (Array.isArray(value)) {
          // Safely handle arrays with size limit
          optimized[key] = value.length > 100 ? '[Large array truncated]' : value.slice(0, 100);
        } else if (typeof value === 'object' && value.constructor === Object) {
          // Security: Deep sanitization to prevent prototype pollution
          try {
            const sanitizedObject = this.sanitizeObjectRecursive(value, 0);
            const sanitizedStr = JSON.stringify(sanitizedObject);

            if (sanitizedStr.length > 1000) {
              optimized[key] = { message: '[Large object truncated]' };
            } else {
              optimized[key] = sanitizedObject;
            }
          } catch {
            optimized[key] = '[Invalid object]';
          }
        }
        // Ignore functions, symbols, and other unsafe types
      }
    }

    return optimized;
  }

  /**
   * Recursively sanitize objects to prevent prototype pollution (comprehensive protection)
   */
  private sanitizeObjectRecursive(obj: any, depth: number, visited = new WeakSet()): any {
    // Security: Prevent deep recursion attacks - strict depth limit
    const MAX_DEPTH = 3; // Further reduced for security
    if (depth > MAX_DEPTH) {
      return '[Max depth exceeded]';
    }

    // Handle primitive values
    if (obj === null || obj === undefined) {
      return null;
    }

    if (typeof obj !== 'object') {
      // Additional validation for strings to prevent exploitation
      if (typeof obj === 'string' && obj.length > 1000) {
        return '[String too long]';
      }

      return obj;
    }

    // Prevent circular reference infinite loops
    if (visited.has(obj)) {
      return '[Circular reference]';
    }
    visited.add(obj);

    // Enhanced prototype pollution protection
    if (obj.constructor && obj.constructor !== Object && obj.constructor !== Array) {
      return '[Non-plain object rejected]';
    }

    // Check for prototype pollution attempts more thoroughly
    const dangerousKeys = [
      '__proto__',
      'constructor',
      'prototype',
      '__defineGetter__',
      '__defineSetter__',
      '__lookupGetter__',
      '__lookupSetter__',
    ];
    for (const key in obj) {
      if (dangerousKeys.includes(key) || key.startsWith('__')) {
        return '[Potentially dangerous object]';
      }
    }

    // Handle arrays
    if (Array.isArray(obj)) {
      const cleanArray = [];
      const maxItems = Math.min(obj.length, 50); // Reduced limit

      for (let i = 0; i < maxItems; i++) {
        try {
          cleanArray.push(this.sanitizeObjectRecursive(obj[i], depth + 1, visited));
        } catch {
          cleanArray.push('[Item error]');
        }
      }

      if (obj.length > 50) {
        cleanArray.push('[Array truncated]');
      }

      return cleanArray;
    }

    // Security: Ultra-strict prototype pollution detection
    if (
      !obj ||
      obj.constructor !== Object ||
      Object.getPrototypeOf(obj) !== Object.prototype ||
      Object.getOwnPropertyNames(obj).some(
        name => name.includes('proto') || name.includes('constructor') || name.startsWith('__'),
      )
    ) {
      return '[Non-plain object rejected]';
    }

    // Check for prototype chain manipulation and dangerous getters
    try {
      // Verify object is truly plain with clean prototype chain
      if (Object.getPrototypeOf(Object.getPrototypeOf(obj)) !== null) {
        return '[Suspicious prototype chain]';
      }

      // Test for getters/setters that could be malicious
      for (const key in obj) {
        const descriptor = Object.getOwnPropertyDescriptor(obj, key);
        if (descriptor && (descriptor.get || descriptor.set)) {
          return '[Object with getters/setters]';
        }
      }

      // Safe JSON.stringify test with size validation
      const jsonStr = JSON.stringify(obj);
      if (!jsonStr || jsonStr === '{}' || jsonStr.length > 10000) {
        return '[Invalid or oversized JSON]';
      }
    } catch {
      return '[Object serialization failed]';
    }

    // Security: Create clean object without prototype
    const cleanObj = Object.create(null);

    // Strict key limiting and validation
    const keys = Object.keys(obj)
      .filter(key => {
        // Strict key validation
        return (
          key.length <= 50 &&
          /^[a-zA-Z0-9_.-]+$/.test(key) &&
          !key.startsWith('__') &&
          !['constructor', 'prototype', '__proto__'].includes(key)
        );
      })
      .slice(0, 20); // Further reduced limit

    // Security: Filter out dangerous keys and process safe ones
    for (const key of keys) {
      // Security: Reject prototype pollution keys
      if (
        key === '__proto__' ||
        key === 'constructor' ||
        key === 'prototype' ||
        key.startsWith('__')
      ) {
        continue;
      }

      // Security: Limit key length to prevent memory exhaustion
      if (key.length > 100) {
        continue;
      }

      // Security: Only allow safe key patterns
      if (!/^[a-zA-Z0-9_.-]+$/.test(key)) {
        continue;
      }

      try {
        cleanObj[key] = this.sanitizeObjectRecursive(obj[key], depth + 1, visited);
      } catch {
        // If sanitization of a property fails, skip it
        continue;
      }
    }

    return cleanObj;
  }

  /**
   * Validate input to prevent injection attacks and malformed data
   */
  private validateInput(
    level: string,
    type: string,
    action: string,
    message: string,
    metadata: any,
  ): boolean {
    // Validate required string fields
    if (!level || !type || !action || !message) {
      return false;
    }

    // Validate string lengths to prevent massive payloads
    if (level.length > 20 || type.length > 100 || action.length > 100 || message.length > 1000) {
      return false;
    }

    // Security: Use character-by-character validation to prevent ReDoS attacks
    // Replace regex with safe character set validation
    if (
      !this.isValidSafeString(level) ||
      !this.isValidSafeString(type) ||
      !this.isValidSafeString(action)
    ) {
      return false;
    }

    // Validate metadata size to prevent memory exhaustion
    if (metadata && typeof metadata === 'object') {
      const metadataStr = JSON.stringify(metadata);
      if (metadataStr.length > 10000) {
        // 10KB limit
        return false;
      }
    }

    return true;
  }

  // Security: Pre-computed allowed character ranges for memory efficiency
  private static readonly ALLOWED_CHAR_CODES = {
    // Letters: A-Z, a-z
    UPPER_A: 65,
    UPPER_Z: 90,
    LOWER_A: 97,
    LOWER_Z: 122,
    // Numbers: 0-9
    NUM_0: 48,
    NUM_9: 57,
    // Safe special characters (pre-computed for efficiency)
    SAFE_CHARS: new Set([
      32, // space
      33, // !
      35, // #
      36, // $
      37, // %
      38, // &
      40, // (
      41, // )
      43, // +
      44, // ,
      45, // -
      46, // .
      47, // /
      58, // :
      61, // =
      63, // ?
      64, // @
      91, // [
      92, // \
      93, // ]
      95, // _
      123, // {
      125, // }
    ]),
  };

  /**
   * Validate string contains only safe characters (memory-efficient implementation)
   */
  private isValidSafeString(str: string): boolean {
    if (!str || typeof str !== 'string') {
      return false;
    }

    // Security: Length limits to prevent processing very long strings
    const maxLength = 200;
    if (str.length > maxLength) {
      return false;
    }

    // Security: Memory-efficient character validation using character codes
    for (let i = 0; i < str.length; i++) {
      const charCode = str.charCodeAt(i);

      // Check if it's a letter (A-Z or a-z)
      if (
        (charCode >= AuditLogQueueService.ALLOWED_CHAR_CODES.UPPER_A &&
          charCode <= AuditLogQueueService.ALLOWED_CHAR_CODES.UPPER_Z) ||
        (charCode >= AuditLogQueueService.ALLOWED_CHAR_CODES.LOWER_A &&
          charCode <= AuditLogQueueService.ALLOWED_CHAR_CODES.LOWER_Z)
      ) {
        continue;
      }

      // Check if it's a number (0-9)
      if (
        charCode >= AuditLogQueueService.ALLOWED_CHAR_CODES.NUM_0 &&
        charCode <= AuditLogQueueService.ALLOWED_CHAR_CODES.NUM_9
      ) {
        continue;
      }

      // Check if it's a safe special character
      if (AuditLogQueueService.ALLOWED_CHAR_CODES.SAFE_CHARS.has(charCode)) {
        continue;
      }

      // Character not allowed
      return false;
    }

    return true;
  }

  /**
   * Check rate limiting to prevent DoS attacks (with memory leak protection)
   */
  private checkRateLimit(identifier: string): boolean {
    const now = Date.now();
    const userKey = identifier.slice(0, 50); // Limit key size

    // Proactive cleanup to prevent memory leaks
    if (this.requestCounts.size >= this.CLEANUP_THRESHOLD) {
      this.cleanupRateLimits();
    }

    // Security: Prevent unbounded Map growth with aggressive cleanup
    if (
      this.requestCounts.size >= this.MAX_RATE_LIMIT_ENTRIES &&
      !this.requestCounts.has(userKey)
    ) {
      // Force immediate cleanup before rejecting
      this.cleanupRateLimits();

      // If still at capacity after cleanup, reject
      if (this.requestCounts.size >= this.MAX_RATE_LIMIT_ENTRIES) {
        console.warn(
          `Rate limiting Map at capacity after cleanup (${this.requestCounts.size}), rejecting new identifier: ${userKey.substring(0, 20)}...`,
        );

        return false;
      }
    }

    // Get or create rate limit entry
    let entry = this.requestCounts.get(userKey);

    if (!entry || now > entry.resetTime) {
      // Reset or create new entry
      entry = { count: 0, resetTime: now + this.RATE_LIMIT_WINDOW };
      this.requestCounts.set(userKey, entry);
    }

    // Increment and check limit
    entry.count++;

    if (entry.count > this.MAX_REQUESTS_PER_MINUTE) {
      return false; // Rate limit exceeded
    }

    return true;
  }

  /**
   * Clean up old rate limit entries to prevent memory leaks (optimized)
   */
  private cleanupRateLimits(): void {
    const now = Date.now();
    let cleaned = 0;
    const beforeSize = this.requestCounts.size;

    // Direct iteration and deletion (more efficient)
    for (const [key, entry] of this.requestCounts.entries()) {
      if (now > entry.resetTime) {
        this.requestCounts.delete(key);
        cleaned++;
      }
    }

    // If still too large after cleanup, use optimized O(n) LRU eviction
    if (this.requestCounts.size > this.CLEANUP_THRESHOLD) {
      const excessCount = this.requestCounts.size - Math.floor(this.CLEANUP_THRESHOLD / 2);

      // Convert to array and sort by resetTime (oldest first) - O(n log n) but only when needed
      const entries = Array.from(this.requestCounts.entries())
        .sort((a, b) => a[1].resetTime - b[1].resetTime)
        .slice(0, excessCount); // Take only the excess entries

      // Remove oldest entries in single pass - O(n)
      for (const [key] of entries) {
        if (this.requestCounts.delete(key)) {
          cleaned++;
        }
      }
    }

    // Log cleanup results
    if (cleaned > 0) {
      console.warn(
        `Rate limit cleanup: removed ${cleaned} entries (${beforeSize} -> ${this.requestCounts.size})`,
      );
    }

    // Force garbage collection hint if available and cleanup was significant
    if (cleaned > 1000 && global.gc) {
      try {
        global.gc();
      } catch {
        // gc() not available, ignore
      }
    }
  }
}

import { Injectable, Inject } from '@nestjs/common';
import {
  AuditLog,
  AuditLogLevel,
  AuditLogAction,
  IAuditLogMetadata,
} from '@core/entities/audit-log.entity';
import {
  IAuditLogRepository,
  IAuditLogQuery,
  IAuditLogQueryResult,
} from '@core/repositories/audit-log.repository.interface';
import { IUserRepository } from '@core/repositories/user.repository.interface';
import { UserId } from '@core/value-objects/user-id.vo';
import { AUDIT_LOG_REPOSITORY, LOGGER_SERVICE, USER_REPOSITORY } from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';
import { AuditLogQueueService } from './audit-log-queue.service';
import { Request } from 'express';

/**
 * Interface para logging de actividades BOT
 */
export interface IBotActivityLogData {
  userId: string;
  companyId: string | null;
  action: string;
  resource: string;
  details: Record<string, any>;
  ipAddress: string;
  userAgent: string;
}

/**
 * Servicio de dominio para auditoría integral del sistema
 *
 * **Propósito**: Servicio principal que encapsula todas las reglas de negocio
 * relacionadas con el registro de auditoría, proporcionando una API unificada
 * para logging de eventos, consultas y mantenimiento del audit trail.
 *
 * **Responsabilidades**:
 * - Coordina el logging de eventos de diferentes tipos (auth, security, API, etc.)
 * - Gestiona políticas de retención y limpieza de logs
 * - Proporciona APIs de consulta y análisis de logs
 * - Implementa sanitización automática de datos sensibles
 * - Garantiza alta performance mediante queue asíncrono
 * - Mantiene integridad del audit trail ante fallos
 *
 * **Arquitectura**: Servicio de dominio siguiendo DDD
 * - Reside en la capa de dominio (core)
 * - Orquesta múltiples servicios especializados
 * - Usa repository pattern para persistencia
 * - Delega procesamiento asíncrono a AuditLogQueueService
 * - Implementa políticas de negocio (retención, sanitización)
 *
 * **Características de performance**:
 * - Logging asíncrono no-bloqueante para eventos críticos
 * - Queue interno para alta concurrencia
 * - Fallback graceful ante errores de persistencia
 * - Sanitización eficiente de datos sensibles
 *
 * **Integración con sistema**:
 * - Usado por interceptors para logging automático
 * - Integrado con authentication flow para eventos de auth
 * - Conectado con exception filters para error tracking
 * - Soporta análisis y monitoreo via queries
 *
 * @example
 * ```typescript
 * // Logging de autenticación (asíncrono)
 * this.auditService.logAuth('login', 'User login successful', userId, request);
 *
 * // Logging de seguridad (asíncrono)
 * this.auditService.logSecurity('access', 'Unauthorized access attempt', null, request, {}, 'error');
 *
 * // Logging de transacciones (síncrono)
 * await this.auditService.logTransaction('create', 'User created', userId, txId, metadata);
 *
 * // Consulta de logs
 * const logs = await this.auditService.queryLogs({
 *   type: 'auth',
 *   fromDate: yesterday,
 *   toDate: now,
 *   limit: 100
 * });
 *
 * // Limpieza automática (política de 7 días)
 * await this.auditService.performAutomaticCleanup();
 * ```
 *
 * **Políticas implementadas**:
 * - Retención automática de 7 días
 * - Sanitización de campos sensibles (password, token, etc.)
 * - Non-blocking para operaciones críticas
 * - Fallback logging ante errores de persistencia
 */
@Injectable()
export class AuditLogService {
  constructor(
    @Inject(AUDIT_LOG_REPOSITORY)
    private readonly auditLogRepository: IAuditLogRepository,
    @Inject(LOGGER_SERVICE)
    private readonly logger: ILogger,
    @Inject(USER_REPOSITORY)
    private readonly userRepository: IUserRepository,
    private readonly auditLogQueue: AuditLogQueueService,
  ) {
    this.logger.setContext(AuditLogService.name);
  }

  /**
   * Registra eventos de autenticación con alta performance
   *
   * **Propósito**: Método no-bloqueante para registrar todos los eventos
   * relacionados con autenticación (login, logout, registro, etc.)
   *
   * **Características**:
   * - Procesamiento asíncrono via queue
   * - No bloquea operaciones críticas
   * - Extracción automática de contexto HTTP
   * - Sanitización de datos sensibles
   *
   * @param action - Acción específica (login, logout, register, etc.)
   * @param message - Mensaje descriptivo del evento
   * @param userId - Usuario asociado (null para intentos fallidos)
   * @param request - Request HTTP para contexto adicional
   * @param additionalMetadata - Metadata específica del evento
   * @param level - Nivel de severidad ('info' por defecto)
   */
  logAuth(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    request?: Request,
    additionalMetadata?: Partial<IAuditLogMetadata>,
    level: AuditLogLevel = 'info',
  ): void {
    const metadata = this.buildRequestMetadata(request, additionalMetadata);
    this.auditLogQueue.enqueue(level, 'auth', action, message, userId, metadata, 'auth');
  }

  /**
   * Registra eventos de seguridad con alta performance
   *
   * **Propósito**: Método no-bloqueante para registrar eventos críticos
   * de seguridad que requieren monitoreo y análisis inmediato.
   *
   * **Casos de uso**:
   * - Intentos de acceso no autorizado
   * - Violaciones de permisos
   * - Ataques detectados
   * - Anomalías en patrones de uso
   *
   * @param action - Tipo de evento de seguridad
   * @param message - Descripción detallada del evento
   * @param userId - Usuario involucrado (null para ataques anónimos)
   * @param request - Request HTTP para análisis de ataque
   * @param additionalMetadata - Contexto adicional de seguridad
   * @param level - Nivel de severidad ('warn' por defecto)
   */
  logSecurity(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    request?: Request,
    additionalMetadata?: Partial<IAuditLogMetadata>,
    level: AuditLogLevel = 'warn',
  ): void {
    const metadata = this.buildRequestMetadata(request, additionalMetadata);
    this.auditLogQueue.enqueue(level, 'security', action, message, userId, metadata, 'security');
  }

  /**
   * Registra excepciones y errores del sistema
   *
   * **Propósito**: Captura automáticamente información detallada de errores
   * para facilitar debugging y monitoreo de estabilidad del sistema.
   *
   * **Información capturada automáticamente**:
   * - Mensaje de error
   * - Stack trace completo
   * - Código de error (si disponible)
   * - Tipo de excepción
   * - Contexto de usuario y request
   *
   * @param error - Instancia de Error a registrar
   * @param userId - Usuario asociado al contexto del error
   * @param request - Request HTTP donde ocurrió el error
   * @param additionalMetadata - Contexto adicional específico
   */
  logException(
    error: Error,
    userId: UserId | null,
    request?: Request,
    additionalMetadata?: Partial<IAuditLogMetadata>,
  ): void {
    const metadata = this.buildRequestMetadata(request, {
      errorMessage: error.message,
      errorStack: error.stack,
      errorCode: (error as any).code,
      exceptionType: error.constructor.name,
      ...additionalMetadata,
    });

    this.auditLogQueue.enqueue(
      'error',
      'exception',
      'exception',
      `Exception: ${error.message}`,
      userId,
      metadata,
      'exception',
    );
  }

  /**
   * Log API requests and responses (high performance, non-blocking)
   */
  logApi(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    request?: Request,
    responseData?: unknown,
    duration?: number,
    level: AuditLogLevel = 'info',
  ): void {
    const metadata = this.buildRequestMetadata(request, {
      duration,
      responseStatus: (responseData as any)?.statusCode,
      responseSize: responseData ? JSON.stringify(responseData).length : undefined,
    });

    this.auditLogQueue.enqueue(level, 'api', action, message, userId, metadata, 'api');
  }

  /**
   * Log transaction events
   */
  async logTransaction(
    action: AuditLogAction,
    message: string,
    userId: UserId | null,
    transactionId?: string,
    additionalMetadata?: Partial<IAuditLogMetadata>,
  ): Promise<void> {
    const metadata: IAuditLogMetadata = {
      transactionId,
      ...additionalMetadata,
    };

    const auditLog = AuditLog.createTransactionLog(action, message, userId, metadata);

    await this.saveAuditLog(auditLog);
  }

  /**
   * Log user actions (CRUD operations)
   */
  async logUserAction(
    action: AuditLogAction,
    message: string,
    userId: UserId,
    resource: string,
    resourceId?: string,
    previousValue?: unknown,
    newValue?: unknown,
    request?: Request,
  ): Promise<void> {
    const metadata = this.buildRequestMetadata(request, {
      resource,
      resourceId,
      previousValue,
      newValue,
    });

    const auditLog = AuditLog.create('info', 'user', action, message, userId, metadata, 'user');

    await this.saveAuditLog(auditLog);
  }

  /**
   * Query audit logs with filters
   */
  async queryLogs(query: IAuditLogQuery): Promise<IAuditLogQueryResult> {
    return this.auditLogRepository.query(query);
  }

  /**
   * Get recent error logs for monitoring
   */
  async getRecentErrors(hours: number = 24, limit: number = 100): Promise<AuditLog[]> {
    return this.auditLogRepository.findRecentErrors(hours, limit);
  }

  /**
   * Get security logs for a date range
   */
  async getSecurityLogs(fromDate: Date, toDate: Date, limit: number = 1000): Promise<AuditLog[]> {
    return this.auditLogRepository.findSecurityLogs(fromDate, toDate, limit);
  }

  /**
   * Get audit statistics
   */
  async getStatistics(fromDate: Date, toDate: Date) {
    return this.auditLogRepository.getStatistics(fromDate, toDate);
  }

  /**
   * Cleanup old audit logs (retention policy)
   * Default: 7-day retention as requested
   */
  async cleanupOldLogs(retentionDays: number = 7): Promise<number> {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - retentionDays);

    const deletedCount = await this.auditLogRepository.deleteOlderThan(cutoffDate);

    this.logger.log({
      message: 'Audit log cleanup completed',
      deletedCount,
      retentionDays,
      cutoffDate: cutoffDate.toISOString(),
    });

    return deletedCount;
  }

  /**
   * Perform automatic 7-day cleanup (as requested)
   * This method enforces the 7-day retention policy
   */
  async performAutomaticCleanup(): Promise<number> {
    return this.cleanupOldLogs(7);
  }

  /**
   * Consulta específica para logs de actividad BOT
   *
   * @param filters - Filtros específicos para BOT
   * @param limit - Límite de resultados (default: 100)
   * @returns Logs de actividad BOT
   */
  async queryBotActivity(
    filters: {
      botAlias?: string;
      companyId?: string;
      tokenId?: string;
      fromDate?: Date;
      toDate?: Date;
      method?: string;
      path?: string;
      statusCode?: number;
    },
    limit: number = 100,
  ): Promise<AuditLog[]> {
    let userId: string | undefined;

    // If botAlias is provided, find the user with that alias to get their userId
    if (filters.botAlias) {
      try {
        const user = await this.userRepository.findByAlias(filters.botAlias);
        userId = user?.id.getValue();

        if (!userId) {
          // If no user found with this alias, return empty results
          this.logger.debug({
            message: 'No user found with provided alias for bot audit query',
            alias: filters.botAlias,
          });

          return [];
        }
      } catch (error) {
        this.logger.warn({
          message: 'Error finding user by alias in bot audit query',
          alias: filters.botAlias,
          error: error.message,
        });

        return [];
      }
    }

    const query: IAuditLogQuery = {
      filters: {
        type: ['bot'],
        userId,
        fromDate: filters.fromDate,
        toDate: filters.toDate,
        // Additional filters as search text
        search:
          [
            filters.companyId && `companyId:${filters.companyId}`,
            filters.tokenId && `tokenId:${filters.tokenId}`,
            filters.method && `method:${filters.method}`,
            filters.path && `path:${filters.path}`,
            filters.statusCode && `statusCode:${filters.statusCode}`,
          ]
            .filter(Boolean)
            .join(' ') || undefined,
      },
      limit,
    };

    const result = await this.queryLogs(query);

    return result.logs;
  }

  /**
   * Log BOT activity with automatic alias resolution
   */
  async logBotActivity(botData: IBotActivityLogData): Promise<void> {
    try {
      // Get user to resolve alias
      const user = await this.userRepository.findById(botData.userId);
      const botAlias = user?.alias;

      // Queue for asynchronous processing
      await this.auditLogQueue.enqueue(
        'info', // level
        'bot', // type
        botData.action as AuditLogAction, // action
        `BOT Activity: ${botData.action}`, // message
        UserId.fromString(botData.userId), // userId
        {
          ...botData.details,
          botAlias,
          companyId: botData.companyId,
          resource: botData.resource,
          ipAddress: botData.ipAddress,
          userAgent: botData.userAgent,
        }, // metadata
        'bot', // context
      );

      this.logger.debug({
        message: 'BOT activity queued for audit logging',
        userId: botData.userId,
        action: botData.action,
        resource: botData.resource,
        botAlias,
      });
    } catch (error) {
      this.logger.error({
        message: 'Failed to queue BOT activity audit log',
        error: error.message,
        userId: botData.userId,
        action: botData.action,
        resource: botData.resource,
      });

      // Fallback: log critical information synchronously
      this.logger.warn({
        message: 'BOT activity audit logging failed',
        userId: botData.userId,
        action: botData.action,
        resource: botData.resource,
        fallbackLogged: true,
      });

      // No lanzar error para no bloquear operaciones del BOT
      // El BOT debe seguir funcionando aunque falle el logging
    }
  }

  /**
   * Build metadata from HTTP request
   */
  private buildRequestMetadata(
    request?: Request,
    additionalMetadata?: Partial<IAuditLogMetadata>,
  ): IAuditLogMetadata {
    const baseMetadata: IAuditLogMetadata = {
      application: 'nauto-console-backend',
      timestamp: new Date().toISOString(),
      ...additionalMetadata,
    };

    if (!request) {
      return baseMetadata;
    }

    // Extract request information
    const requestMetadata: IAuditLogMetadata = {
      ...baseMetadata,
      method: request.method,
      url: request.url,
      userAgent: request.headers['user-agent'],
      ipAddress: this.extractIpAddress(request),
      headers: this.sanitizeHeaders(request.headers),
      query: request.query,
      params: request.params,
      body: this.sanitizeBody(request.body) as Record<string, unknown>,
    };

    // Extract user/session information if available
    if (request.user) {
      requestMetadata.sessionUser = (request.user as any).id;
      requestMetadata.sessionToken = (request as any).sessionToken;
    }

    // Extract session information from headers or custom properties
    if (request.headers['x-session-id']) {
      requestMetadata.sessionId = request.headers['x-session-id'] as string;
    }

    return requestMetadata;
  }

  /**
   * Extract IP address from request
   */
  private extractIpAddress(request: Request): string {
    return (
      (request.headers['x-forwarded-for'] as string)?.split(',')[0]?.trim() ||
      (request.headers['x-real-ip'] as string) ||
      request.connection?.remoteAddress ||
      request.socket?.remoteAddress ||
      (request as any).ip ||
      'unknown'
    );
  }

  /**
   * Sanitize headers to remove sensitive information
   */
  private sanitizeHeaders(headers: Record<string, unknown>): Record<string, string> {
    const sanitized: Record<string, string> = {};
    const sensitiveHeaders = ['authorization', 'cookie', 'x-api-key', 'x-auth-token'];

    Object.keys(headers).forEach(key => {
      if (sensitiveHeaders.includes(key.toLowerCase())) {
        sanitized[key] = '[REDACTED]';
      } else {
        sanitized[key] = String(headers[key]);
      }
    });

    return sanitized;
  }

  /**
   * Sanitize request body to remove sensitive information
   */
  private sanitizeBody(body: unknown): unknown {
    if (!body || typeof body !== 'object') {
      return body;
    }

    const sanitized = { ...body };
    const sensitiveFields = ['password', 'passwordHash', 'token', 'secret', 'key', 'otp'];

    Object.keys(sanitized).forEach(key => {
      if (sensitiveFields.some(field => key.toLowerCase().includes(field))) {
        sanitized[key] = '[REDACTED]';
      }
    });

    return sanitized;
  }

  /**
   * Save audit log with error handling
   */
  private async saveAuditLog(auditLog: AuditLog): Promise<void> {
    try {
      await this.auditLogRepository.save(auditLog);

      // Also log to application logger for immediate visibility
      this.logger.log({
        message: 'Audit log created',
        auditLogId: auditLog.id.getValue(),
        level: auditLog.level,
        type: auditLog.type,
        action: auditLog.action,
        context: auditLog.context,
        userId: auditLog.userId?.getValue(),
        auditMessage: auditLog.message,
      });
    } catch (error) {
      // Critical: audit logging should not fail the main operation
      // Log the failure but don't throw
      this.logger.error({
        message: 'Failed to save audit log',
        error: error instanceof Error ? error.message : String(error),
        auditLogData: {
          level: auditLog.level,
          type: auditLog.type,
          action: auditLog.action,
          message: auditLog.message,
        },
      });
    }
  }
}

import { Injectable, Inject } from '@nestjs/common';
import { CommandBus } from '@nestjs/cqrs';
import { UserService } from './user.service';
import { AuthService } from './auth.service';
import { SessionService } from './session.service';
import { UserBanService } from './user-ban.service';
import { PermissionCollectionService } from './permission-collection.service';
import { BusinessConfigurationService } from './business-configuration.service';
import { AuditLogService } from './audit-log.service';
import { ITokenProvider } from '@core/interfaces/token-provider.interface';
import {
  TOKEN_PROVIDER,
  LOGGER_SERVICE,
  AUDIT_LOG_SERVICE,
  BOT_TOKEN_REPOSITORY,
} from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';
import {
  AuthFailureReason,
  IAuthValidationResult,
  ILoginFlowResult,
} from '@core/enums/auth-failure-reason.enum';
import { UserId } from '@core/value-objects/user-id.vo';
import { AuthResponse } from '@application/dtos/responses/user.response';
import { UserMapper } from '@application/mappers/user.mapper';
import { User } from '@core/entities/user.entity';
import { SendVerificationEmailCommand } from '@application/commands/auth/send-verification-email.command';
import { RolesEnum } from '@shared/constants/enums';
import { IBotTokenRepository } from '@core/repositories/bot-token.repository.interface';
import { v4 as uuidv4 } from 'uuid';

/**
 * Servicio integral de validación de autenticación
 *
 * **Propósito**: Centraliza y orquesta todo el flujo de validación de login,
 * desde la verificación de credenciales hasta la generación de tokens,
 * incluyendo audit logging automático y manejo de errores robusto.
 *
 * **Responsabilidades**:
 * - Validación completa de credenciales (email, password, estado usuario)
 * - Manejo de flujos multi-paso (email verification, 2FA)
 * - Generación de tokens y sesiones
 * - Audit logging automático de todos los eventos
 * - Manejo granular de errores con información detallada
 * - Aplicación de políticas de negocio (bans, verificaciones, etc.)
 *
 * **Arquitectura**: Servicio de dominio siguiendo Clean Architecture
 * - Reside en la capa de dominio (core)
 * - Orquesta otros servicios de dominio
 * - Independiente de infrastructure y presentation
 * - Usado por application layer (commands)
 *
 * **Beneficios**:
 * - Commands más limpios y enfocados
 * - Reutilización de lógica de validación
 * - Logging consistente y comprehensivo
 * - Manejo de errores centralizado
 * - Fácil testing y mantenimiento
 *
 * @example
 * ```typescript
 * // Uso típico en un command handler
 * const result = await this.authValidationService.validateLoginFlow(
 *   email, password, userAgent, ipAddress
 * );
 *
 * if (!result.success) {
 *   throw new UnauthorizedException();
 * }
 *
 * // Manejo de flujos multi-paso
 * switch (result.nextStep) {
 *   case 'email_verification':
 *     return { requiresEmailVerification: true, ... };
 *   case 'otp_required':
 *     return { requiresOtp: true, ... };
 *   case 'complete':
 *     return result.authResponse;
 * }
 * ```
 *
 * **Dependencias**:
 * - UserService: Validación de credenciales
 * - AuthService: Operaciones de autenticación
 * - SessionService: Manejo de sesiones
 * - UserBanService: Validación de bans
 * - PermissionCollectionService: Recopilación de permisos
 * - BusinessConfigurationService: Políticas de negocio
 * - AuditLogService: Logging de auditoría
 * - TokenProvider: Generación de JWT tokens
 */
@Injectable()
export class AuthenticationValidationService {
  constructor(
    private readonly userService: UserService,
    private readonly authService: AuthService,
    private readonly sessionService: SessionService,
    private readonly userBanService: UserBanService,
    private readonly permissionCollectionService: PermissionCollectionService,
    private readonly businessConfigService: BusinessConfigurationService,
    private readonly commandBus: CommandBus,
    @Inject(TOKEN_PROVIDER)
    private readonly tokenProvider: ITokenProvider,
    @Inject(BOT_TOKEN_REPOSITORY)
    private readonly botTokenRepository: IBotTokenRepository,
    @Inject(LOGGER_SERVICE)
    private readonly logger: ILogger,
    @Inject(AUDIT_LOG_SERVICE)
    private readonly auditLogService: AuditLogService,
  ) {
    this.logger.setContext(AuthenticationValidationService.name);
  }

  /**
   * Ejecuta el flujo completo de validación de login
   *
   * **Propósito**: Método principal que orquesta todo el proceso de autenticación,
   * desde la validación de credenciales hasta la generación de tokens o manejo de flujos multi-paso.
   *
   * **Proceso**:
   * 1. Validación de credenciales básicas (email/password)
   * 2. Validaciones de negocio adicionales (bans, estado usuario)
   * 3. Actualización de último login
   * 4. Verificación de requisitos (email verification, 2FA)
   * 5. Generación de tokens y sesión (si es login completo)
   * 6. Audit logging automático de todo el proceso
   *
   * **Características**:
   * - Manejo exhaustivo de errores con información detallada
   * - Audit logging automático para todos los escenarios
   * - Soporte para flujos multi-paso (email verification, 2FA)
   * - Performance tracking (duración de operaciones)
   * - Context information (IP, user agent) para seguridad
   *
   * @param email - Email del usuario intentando autenticarse
   * @param password - Password en texto plano (será hasheado para comparación)
   * @param userAgent - User agent del cliente (para audit logging y sesiones)
   * @param ipAddress - Dirección IP del cliente (para audit logging y seguridad)
   *
   * @returns Promise<ILoginFlowResult> - Resultado estructurado indicando éxito/fallo y siguiente paso
   *
   * @example
   * ```typescript
   * const result = await this.authValidationService.validateLoginFlow(
   *   'user@example.com',
   *   'plainTextPassword',
   *   'Mozilla/5.0...',
   *   '192.168.1.100'
   * );
   *
   * if (!result.success) {
   *   // Login falló - información detallada en result.failureReason y result.auditDetails
   *   console.log(`Login failed: ${result.failureReason}`);
   *   return;
   * }
   *
   * // Login exitoso - verificar próximo paso
   * switch (result.nextStep) {
   *   case 'complete':
   *     // Login completo - usar result.authResponse
   *     break;
   *   case 'email_verification':
   *     // Requiere verificación de email
   *     break;
   *   case 'otp_required':
   *     // Requiere 2FA
   *     break;
   * }
   * ```
   *
   * **Audit Logging**: Este método automaticamente registra:
   * - Intentos de login exitosos y fallidos
   * - Razones específicas de fallo
   * - Información contextual (IP, user agent, duración)
   * - Detalles técnicos para debugging
   */
  async validateLoginFlow(
    email: string,
    password: string,
    userAgent?: string,
    ipAddress?: string,
  ): Promise<ILoginFlowResult> {
    const startTime = Date.now();

    try {
      // Step 1: Validate credentials
      const validationResult = await this.userService.validateCredentials(email, password);

      if (!validationResult.success) {
        return this.buildFailureResult(
          email,
          validationResult.failureReason!,
          validationResult,
          userAgent,
          ipAddress,
          startTime,
        );
      }

      const user = validationResult.user as User;

      // Step 2: Additional business validations
      const businessValidation = await this.performBusinessValidations(user, email);
      if (!businessValidation.success) {
        return this.buildFailureResult(
          email,
          businessValidation.failureReason!,
          businessValidation,
          userAgent,
          ipAddress,
          startTime,
          user.id,
        );
      }

      // Step 3: Special handling for BOT users
      const isBotUser = user.roles.some(role => role.name === RolesEnum.BOT);
      if (isBotUser) {
        return await this.handleBotLogin(user, email, userAgent, ipAddress, startTime);
      }

      // Step 4: Update last login (regular users only)
      await this.authService.updateLastLogin(user.id.getValue());

      // Step 5: Check email verification requirements
      const emailVerificationResult = await this.checkEmailVerification(user, email);
      if (emailVerificationResult.nextStep === 'email_verification') {
        return await this.buildEmailVerificationResult(
          user,
          email,
          userAgent,
          ipAddress,
          startTime,
        );
      }

      // Step 6: Check OTP requirements
      if (user.otpEnabled) {
        return this.buildOtpRequiredResult(user, email, userAgent, ipAddress, startTime);
      }

      // Step 7: Complete login - generate tokens and session
      const authResponse = await this.completeLogin(user, userAgent, ipAddress);

      // Step 8: Log successful authentication
      await this.logSuccessfulAuth(user, email, userAgent, ipAddress, startTime);

      return {
        success: true,
        nextStep: 'complete',
        authResponse,
        auditDetails: {
          email,
          userAgent,
          ipAddress,
          duration: Date.now() - startTime,
        },
      };
    } catch (error) {
      // System error during login flow
      const errorMessage = error instanceof Error ? error.message : String(error);
      const errorType = error?.constructor?.name || 'Unknown';

      this.logger.error({
        message: 'System error during login flow',
        email,
        error: errorMessage,
        errorType,
        stack: error instanceof Error ? error.stack : undefined,
      });

      return this.buildFailureResult(
        email,
        AuthFailureReason.SYSTEM_ERROR,
        {
          success: false,
          failureReason: AuthFailureReason.SYSTEM_ERROR,
          details: {
            systemError: errorMessage,
            errorType,
          },
        },
        userAgent,
        ipAddress,
        startTime,
      );
    }
  }

  /**
   * Perform additional business validations
   */
  private async performBusinessValidations(
    user: User,
    email: string,
  ): Promise<IAuthValidationResult> {
    try {
      // Validate user is not banned
      this.userBanService.validateUserNotBanned(user);

      return {
        success: true,
        user,
      };
    } catch (_error) {
      // User is banned
      return {
        success: false,
        failureReason: AuthFailureReason.USER_BANNED,
        message: `User ${email} is banned`,
        details: {
          bannedUntil: user.bannedUntil,
          banReason: user.banReason,
        },
      };
    }
  }

  /**
   * Check email verification requirements
   */
  private async checkEmailVerification(user: User, email: string): Promise<{ nextStep?: string }> {
    const emailConfig = this.businessConfigService.getEmailVerificationConfig();
    const isEmailVerified = await this.authService.isEmailVerified(email);

    if (emailConfig.enabled && !isEmailVerified) {
      return { nextStep: 'email_verification' };
    }

    return {};
  }

  /**
   * Complete the login process - generate tokens and session
   */
  private async completeLogin(
    user: User,
    userAgent?: string,
    ipAddress?: string,
  ): Promise<AuthResponse> {
    // Collect user permissions
    const userPermissions = await this.permissionCollectionService.collectUserPermissions(user);

    // Generate session and tokens
    const sessionToken = uuidv4();
    const { accessToken, refreshToken } = await this.tokenProvider.generateTokens(
      user,
      Array.from(userPermissions),
      sessionToken,
    );

    // Register session
    await this.sessionService.createSession(
      user.id.getValue(),
      sessionToken,
      refreshToken,
      userAgent || null,
      ipAddress || '?',
    );

    return {
      accessToken,
      refreshToken,
      user: UserMapper.toAuthResponse(user),
      message: 'Login successful',
    };
  }

  /**
   * Build failure result with audit logging
   */
  private buildFailureResult(
    email: string,
    failureReason: AuthFailureReason,
    validationResult: IAuthValidationResult,
    userAgent?: string,
    ipAddress?: string,
    startTime?: number,
    userId?: UserId,
  ): ILoginFlowResult {
    const duration = startTime ? Date.now() - startTime : 0;

    // Build comprehensive failure message
    const failureMessages = {
      [AuthFailureReason.USER_NOT_FOUND]: 'User not found in database',
      [AuthFailureReason.INVALID_PASSWORD]: 'Invalid password provided',
      [AuthFailureReason.USER_INACTIVE]: 'User account is inactive',
      [AuthFailureReason.USER_BANNED]: `User is banned${validationResult.details?.bannedUntil ? ` until ${validationResult.details.bannedUntil.toISOString()}` : ''}${validationResult.details?.banReason ? ` (Reason: ${validationResult.details.banReason})` : ''}`,
      [AuthFailureReason.INVALID_EMAIL_FORMAT]: 'Invalid email format',
      [AuthFailureReason.SYSTEM_ERROR]: `System error during authentication: ${validationResult.details?.systemError || 'Unknown error'}`,
    };

    const failureMessage = failureMessages[failureReason] || 'Unknown authentication failure';
    const logLevel = [
      AuthFailureReason.USER_BANNED,
      AuthFailureReason.USER_INACTIVE,
      AuthFailureReason.SYSTEM_ERROR,
    ].includes(failureReason)
      ? 'error'
      : 'warn';

    // Log detailed failure information
    this.logger.warn({
      message: 'Login failed',
      email,
      failureReason,
      details: validationResult.details,
      duration,
    });

    // Comprehensive audit log
    this.auditLogService.logSecurity(
      'login',
      `Login failed for ${email}: ${failureMessage}`,
      userId || null,
      undefined,
      {
        email,
        userAgent,
        ipAddress,
        failureReason,
        duration,
        ...validationResult.details,
      },
      logLevel,
    );

    return {
      success: false,
      failureReason,
      message: 'Invalid credentials',
      auditDetails: {
        email,
        userAgent,
        ipAddress,
        duration,
        failureReason,
        ...validationResult.details,
      },
    };
  }

  /**
   * Build email verification required result
   *
   * **NUEVO**: Ahora envía automáticamente el email de verificación y SMS (si aplica)
   * cuando el login requiere verificación de email, como si se hubiera llamado
   * el endpoint POST /api/auth/email/send-verification
   */
  private async buildEmailVerificationResult(
    user: User,
    email: string,
    userAgent?: string,
    ipAddress?: string,
    startTime?: number,
  ): Promise<ILoginFlowResult> {
    const duration = startTime ? Date.now() - startTime : 0;

    // Audit log for email verification requirement
    this.auditLogService.logSecurity(
      'login',
      `Login requires email verification for ${email}`,
      user.id,
      undefined,
      {
        email,
        userAgent,
        ipAddress,
        failureReason: AuthFailureReason.EMAIL_NOT_VERIFIED,
        duration,
      },
      'warn',
    );

    // **NUEVA FUNCIONALIDAD**: Enviar automáticamente email de verificación y SMS
    try {
      const phoneNumber = user.profile?.phone; // Obtener teléfono del perfil del usuario

      // Ejecutar comando de envío de verificación
      // Usando el propio usuario como "currentUser" para evitar problemas de autorización
      const verificationCommand = new SendVerificationEmailCommand(
        {
          email: user.email.getValue(),
          phoneNumber, // Se incluye si existe, sino undefined
        },
        user.id.getValue(), // currentUserId - el propio usuario
        ['admin'], // currentUserRoles - damos permisos admin para poder enviar
        user.companyId?.getValue() || null, // currentUserCompanyId
      );

      await this.commandBus.execute(verificationCommand);

      this.logger.log({
        message: 'Automatic verification email and SMS sent during login flow',
        userId: user.id.getValue(),
        email: user.email.getValue(),
        phoneNumber: phoneNumber ? '***' + phoneNumber.slice(-4) : 'none',
      });
    } catch (error) {
      // Si falla el envío, logueamos el error pero no interrumpimos el flujo de login
      this.logger.error({
        message: 'Failed to send automatic verification during login',
        userId: user.id.getValue(),
        email: user.email.getValue(),
        error: error instanceof Error ? error.message : String(error),
      });
    }

    return {
      success: true,
      nextStep: 'email_verification',
      requiresEmailVerification: true,
      userId: user.id.getValue(),
      email: user.email.getValue(),
      message: 'Verification email sent', // Actualizado para reflejar que se envió
    };
  }

  /**
   * Build OTP required result
   */
  private buildOtpRequiredResult(
    user: User,
    email: string,
    userAgent?: string,
    ipAddress?: string,
    startTime?: number,
  ): ILoginFlowResult {
    const duration = startTime ? Date.now() - startTime : 0;

    // Audit log for OTP requirement
    this.auditLogService.logAuth(
      'login',
      `Login requires 2FA verification for ${email}`,
      user.id,
      undefined,
      {
        email,
        userAgent,
        ipAddress,
        requiresOtp: true,
        duration,
      },
      'info',
    );

    return {
      success: true,
      nextStep: 'otp_required',
      requiresOtp: true,
      userId: user.id.getValue(),
      message: '2FA verification required',
    };
  }

  /**
   * Log successful authentication
   */
  private async logSuccessfulAuth(
    user: User,
    email: string,
    userAgent?: string,
    ipAddress?: string,
    startTime?: number,
  ): Promise<void> {
    const duration = startTime ? Date.now() - startTime : 0;

    this.logger.log({
      message: 'Login successful',
      userId: user.id.getValue(),
      email,
      roles: user.rolesCollection?.roles?.map(r => r.name) || [],
    });

    // Audit log for successful authentication
    this.auditLogService.logAuth(
      'login',
      `User logged in successfully: ${email}`,
      user.id,
      undefined,
      {
        email,
        userAgent,
        ipAddress,
        rolesCount: user.rolesCollection?.roles?.length || 0,
        duration,
      },
      'info',
    );
  }

  /**
   * Handle BOT login - returns existing active token instead of creating new session
   */
  private async handleBotLogin(
    user: User,
    email: string,
    userAgent?: string,
    ipAddress?: string,
    startTime?: number,
  ): Promise<ILoginFlowResult> {
    const duration = startTime ? Date.now() - startTime : 0;

    this.logger.debug({
      message: 'Processing BOT login',
      userId: user.id.getValue(),
      email,
      alias: user.alias,
    });

    // Find active BOT token for this user
    const activeBotTokens = await this.botTokenRepository.findAllActive();
    const userActiveBotToken = activeBotTokens.find(
      token => token.botUserId.getValue() === user.id.getValue(),
    );

    if (!userActiveBotToken) {
      // No active BOT token found - this shouldn't happen for valid BOT users
      this.logger.warn({
        message: 'BOT user has no active token',
        userId: user.id.getValue(),
        email,
        alias: user.alias,
      });

      return this.buildFailureResult(
        email,
        AuthFailureReason.BOT_NO_ACTIVE_TOKEN,
        {
          success: false,
          failureReason: AuthFailureReason.BOT_NO_ACTIVE_TOKEN,
          message: 'BOT user has no active token',
        },
        userAgent,
        ipAddress,
        startTime,
        user.id,
      );
    }

    // Get permissions for the BOT user
    const permissions = await this.permissionCollectionService.collectUserPermissions(user);

    // Create auth response with existing BOT token
    const authResponse: AuthResponse = {
      accessToken: userActiveBotToken.tokenId, // BOT token ID is the access token
      refreshToken: '', // BOTs don't use refresh tokens
      user: UserMapper.toAuthResponse(user),
    };

    // Convert Set to array for the response
    const userResponse = authResponse.user as any;
    userResponse.permissions = Array.from(permissions);

    // Log successful BOT authentication
    this.logger.log({
      message: 'BOT login successful - returned existing token',
      userId: user.id.getValue(),
      email,
      alias: user.alias,
      tokenId: userActiveBotToken.tokenId.substring(0, 8) + '***',
      duration,
    });

    // Audit log for BOT authentication
    this.auditLogService.logAuth(
      'login',
      `BOT user logged in successfully: ${email} (${user.alias})`,
      user.id,
      undefined,
      {
        email,
        alias: user.alias,
        userAgent,
        ipAddress,
        tokenId: userActiveBotToken.tokenId.substring(0, 8) + '***',
        duration,
        botLogin: true,
      },
      'info',
    );

    return {
      success: true,
      nextStep: 'complete',
      authResponse,
      auditDetails: {
        email,
        userAgent,
        ipAddress,
        duration,
      },
    };
  }
}

import { Injectable, Logger, OnModuleInit } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';

/**
 * Domain service responsible for business configuration values
 * Centralizes all business rules and configurable values with security validation
 *
 * **Security Features**:
 * - Input validation and sanitization for all configuration values
 * - Range and type checking to prevent configuration injection
 * - Audit logging for configuration access
 * - Fail-secure defaults for all settings
 * - Runtime validation of critical configuration
 */
@Injectable()
export class BusinessConfigurationService implements OnModuleInit {
  private readonly logger = new Logger(BusinessConfigurationService.name);
  private readonly configCache = new Map<string, any>();
  private readonly CONFIG_ACCESS_LOG = true; // Enable audit logging for config access

  // Security: Define valid ranges for all numeric configurations
  private readonly VALID_RANGES = {
    EMAIL_VERIFICATION_EXPIRY_MINUTES: { min: 1, max: 10080 }, // 1 minute to 1 week
    PASSWORD_SALT_ROUNDS: { min: 8, max: 20 }, // BCrypt recommended range
    PASSWORD_RESET_EXPIRY_MINUTES: { min: 5, max: 1440 }, // 5 minutes to 24 hours
    PASSWORD_MIN_LENGTH: { min: 6, max: 128 },
    RATE_LIMIT_EMAIL_ATTEMPTS: { min: 1, max: 100 },
    RATE_LIMIT_IP_ATTEMPTS: { min: 1, max: 1000 },
    RATE_LIMIT_LOCKOUT_MINUTES: { min: 1, max: 1440 },
    FILE_URL_EXPIRY_HOURS: { min: 1, max: 168 }, // 1 hour to 1 week
    MAX_FILE_SIZE_MB: { min: 1, max: 1000 },
    MAX_ACTIVE_SESSIONS: { min: 1, max: 100 },
    SESSION_INACTIVITY_TIMEOUT: { min: 5, max: 43200 }, // 5 minutes to 30 days (in minutes)
    OTP_EXPIRY_MINUTES: { min: 1, max: 60 },
    OTP_MAX_ATTEMPTS: { min: 1, max: 10 },
    OTP_SECRET_LENGTH: { min: 16, max: 64 },
  };

  // Security: Define valid string patterns
  private readonly VALID_PATTERNS = {
    ALLOWED_FILE_TYPES: /^[a-zA-Z0-9,]+$/, // Only alphanumeric and comma
    DEFAULT_COUNTRY: /^[a-zA-Z\s-]{1,50}$/, // Letters, spaces, hyphens, max 50 chars
    DEFAULT_STATE: /^[a-zA-Z\s-]{1,50}$/, // Letters, spaces, hyphens, max 50 chars
  };

  constructor(private readonly configService: ConfigService) {}

  async onModuleInit() {
    // Security: Validate all critical configurations at startup
    await this.validateCriticalConfiguration();
    this.logger.log('Business configuration service initialized with security validation');
  }

  /**
   * Email verification business configuration (security validated)
   * Business Rule: Email verification requirement can be enabled/disabled per environment
   */
  getEmailVerificationConfig(): {
    enabled: boolean;
    expirationMinutes: number;
  } {
    this.auditConfigAccess('EMAIL_VERIFICATION_CONFIG');

    const enabledValue = this.getValidatedBooleanConfig('EMAIL_VERIFICATION_ENABLED', false);
    const expirationMinutes = this.getValidatedNumericConfig(
      'EMAIL_VERIFICATION_EXPIRY_MINUTES',
      60,
      'EMAIL_VERIFICATION_EXPIRY_MINUTES',
    );

    return {
      enabled: enabledValue,
      expirationMinutes,
    };
  }

  /**
   * Password security configuration
   * Business Rule: Password security requirements vary by security level
   */
  getPasswordSecurityConfig(): {
    saltRounds: number;
    resetExpirationMinutes: number;
    minLength: number;
    requireSpecialCharacters: boolean;
  } {
    return {
      saltRounds: this.configService.get<number>('PASSWORD_SALT_ROUNDS', 12),
      resetExpirationMinutes: this.configService.get<number>('PASSWORD_RESET_EXPIRY_MINUTES', 30),
      minLength: this.configService.get<number>('PASSWORD_MIN_LENGTH', 8),
      requireSpecialCharacters:
        this.configService.get<string>('PASSWORD_REQUIRE_SPECIAL', 'true') === 'true',
    };
  }

  /**
   * Rate limiting business configuration
   * Business Rule: Rate limits prevent abuse and protect system resources
   */
  getRateLimitingConfig(): {
    maxAttemptsPerEmail: number;
    maxAttemptsPerIp: number;
    lockoutDurationMinutes: number;
  } {
    return {
      maxAttemptsPerEmail: this.configService.get<number>('RATE_LIMIT_EMAIL_ATTEMPTS', 3),
      maxAttemptsPerIp: this.configService.get<number>('RATE_LIMIT_IP_ATTEMPTS', 10),
      lockoutDurationMinutes: this.configService.get<number>('RATE_LIMIT_LOCKOUT_MINUTES', 15),
    };
  }

  /**
   * File storage business configuration
   * Business Rule: File access and retention policies
   */
  getFileStorageConfig(): {
    urlExpirationHours: number;
    maxFileSize: number;
    allowedFileTypes: string[];
  } {
    return {
      urlExpirationHours: this.configService.get<number>('FILE_URL_EXPIRY_HOURS', 24),
      maxFileSize: this.configService.get<number>('MAX_FILE_SIZE_MB', 10) * 1024 * 1024,
      allowedFileTypes: this.configService
        .get<string>('ALLOWED_FILE_TYPES', 'jpg,png,pdf,doc,docx')
        .split(','),
    };
  }

  /**
   * Session management business configuration
   * Business Rule: Session policies for security and user experience
   */
  getSessionConfig(): {
    maxActiveSessions: number;
    inactivityTimeoutMinutes: number;
    extendOnActivity: boolean;
  } {
    return {
      maxActiveSessions: this.configService.get<number>('MAX_ACTIVE_SESSIONS', 3),
      inactivityTimeoutMinutes: this.configService.get<number>('SESSION_INACTIVITY_TIMEOUT', 120),
      extendOnActivity:
        this.configService.get<string>('SESSION_EXTEND_ON_ACTIVITY', 'true') === 'true',
    };
  }

  /**
   * Address and localization defaults
   * Business Rule: Default values for address information based on business requirements
   */
  getAddressDefaults(): {
    defaultCountry: string | null;
    defaultState: string | null;
    requireFullAddress: boolean;
  } {
    const defaultCountry = this.configService.get<string>('DEFAULT_COUNTRY', 'México');
    const defaultState = this.configService.get<string>('DEFAULT_STATE', 'Unknown');

    return {
      defaultCountry: defaultCountry === 'null' ? null : defaultCountry,
      defaultState: defaultState === 'null' ? null : defaultState,
      requireFullAddress:
        this.configService.get<string>('REQUIRE_FULL_ADDRESS', 'false') === 'true',
    };
  }

  /**
   * OTP (2FA) business configuration
   * Business Rule: Two-factor authentication policies
   */
  getOtpConfig(): {
    enabled: boolean;
    expirationMinutes: number;
    maxAttempts: number;
    secretLength: number;
  } {
    return {
      enabled: this.configService.get<string>('OTP_ENABLED', 'true') === 'true',
      expirationMinutes: this.configService.get<number>('OTP_EXPIRY_MINUTES', 5),
      maxAttempts: this.configService.get<number>('OTP_MAX_ATTEMPTS', 3),
      secretLength: this.configService.get<number>('OTP_SECRET_LENGTH', 32),
    };
  }

  /**
   * Business feature flags
   * Business Rule: Enable/disable features based on business requirements
   */
  getFeatureFlags(): {
    emailVerificationRequired: boolean;
    otpRequired: boolean;
    auditLoggingEnabled: boolean;
    maintenanceMode: boolean;
  } {
    return {
      emailVerificationRequired: this.getEmailVerificationConfig().enabled,
      otpRequired: this.getOtpConfig().enabled,
      auditLoggingEnabled:
        this.configService.get<string>('AUDIT_LOGGING_ENABLED', 'true') === 'true',
      maintenanceMode: this.configService.get<string>('MAINTENANCE_MODE', 'false') === 'true',
    };
  }

  /**
   * Security: Validate critical configuration at startup
   */
  private async validateCriticalConfiguration(): Promise<void> {
    const criticalConfigs = [
      'EMAIL_VERIFICATION_EXPIRY_MINUTES',
      'PASSWORD_SALT_ROUNDS',
      'PASSWORD_MIN_LENGTH',
      'RATE_LIMIT_EMAIL_ATTEMPTS',
      'MAX_ACTIVE_SESSIONS',
    ];

    const validationErrors: string[] = [];

    for (const configKey of criticalConfigs) {
      try {
        const value = this.configService.get<string>(configKey);
        if (value !== undefined) {
          const numericValue = parseInt(value, 10);
          if (isNaN(numericValue)) {
            validationErrors.push(`${configKey}: Invalid numeric value '${value}'`);
            continue;
          }

          const range = this.VALID_RANGES[configKey as keyof typeof this.VALID_RANGES];
          if (range && (numericValue < range.min || numericValue > range.max)) {
            validationErrors.push(
              `${configKey}: Value ${numericValue} out of valid range [${range.min}, ${range.max}]`,
            );
          }
        }
      } catch (error) {
        validationErrors.push(`${configKey}: Validation error - ${error}`);
      }
    }

    if (validationErrors.length > 0) {
      this.logger.error({
        message: 'Critical configuration validation failed',
        errors: validationErrors,
        action: 'APPLICATION_STARTUP_FAILED',
      });

      throw new Error(`Configuration validation failed: ${validationErrors.join('; ')}`);
    }

    this.logger.log('Critical configuration validation passed');
  }

  /**
   * Security: Get validated boolean configuration value
   */
  private getValidatedBooleanConfig(key: string, defaultValue: boolean): boolean {
    const value = this.configService.get<string>(key, defaultValue.toString());

    // Security: Only accept explicit 'true' or 'false' strings
    if (value !== 'true' && value !== 'false') {
      this.logger.warn({
        message: `Invalid boolean configuration value for ${key}: '${value}', using default: ${defaultValue}`,
        configKey: key,
        invalidValue: value,
        defaultUsed: defaultValue,
      });

      return defaultValue;
    }

    return value === 'true';
  }

  /**
   * Security: Get validated numeric configuration value
   */
  private getValidatedNumericConfig(
    key: string,
    defaultValue: number,
    rangeKey?: keyof typeof this.VALID_RANGES,
  ): number {
    const value = this.configService.get<string>(key, defaultValue.toString());
    const numericValue = parseInt(value, 10);

    // Security: Validate numeric conversion
    if (isNaN(numericValue)) {
      this.logger.warn({
        message: `Invalid numeric configuration value for ${key}: '${value}', using default: ${defaultValue}`,
        configKey: key,
        invalidValue: value,
        defaultUsed: defaultValue,
      });

      return defaultValue;
    }

    // Security: Validate range if specified
    if (rangeKey && this.VALID_RANGES[rangeKey]) {
      const range = this.VALID_RANGES[rangeKey];
      if (numericValue < range.min || numericValue > range.max) {
        this.logger.warn({
          message: `Configuration value for ${key} out of valid range: ${numericValue}, using default: ${defaultValue}`,
          configKey: key,
          invalidValue: numericValue,
          validRange: range,
          defaultUsed: defaultValue,
        });

        return defaultValue;
      }
    }

    return numericValue;
  }

  /**
   * Security: Get validated string configuration value
   */
  private getValidatedStringConfig(
    key: string,
    defaultValue: string,
    patternKey?: keyof typeof this.VALID_PATTERNS,
    maxLength: number = 200,
  ): string {
    const value = this.configService.get<string>(key, defaultValue);

    // Security: Length validation
    if (value.length > maxLength) {
      this.logger.warn({
        message: `Configuration value for ${key} exceeds maximum length: ${value.length} > ${maxLength}, using default`,
        configKey: key,
        valueLength: value.length,
        maxLength,
        defaultUsed: defaultValue,
      });

      return defaultValue;
    }

    // Security: Pattern validation if specified
    if (patternKey && this.VALID_PATTERNS[patternKey]) {
      const pattern = this.VALID_PATTERNS[patternKey];
      if (!pattern.test(value)) {
        this.logger.warn({
          message: `Configuration value for ${key} does not match required pattern, using default`,
          configKey: key,
          invalidValue: value.substring(0, 50) + '...', // Truncate for logging
          defaultUsed: defaultValue,
        });

        return defaultValue;
      }
    }

    return value;
  }

  /**
   * Security: Audit configuration access for monitoring
   */
  private auditConfigAccess(configGroup: string): void {
    if (this.CONFIG_ACCESS_LOG) {
      // In production, this could be sent to an audit log system
      this.logger.debug({
        event: 'CONFIG_ACCESS',
        configGroup,
        timestamp: new Date().toISOString(),
      });
    }
  }
}

import { Injectable, Inject, OnModuleInit, OnModuleDestroy } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { ErrorSanitizationUtil } from '@core/utils/error-sanitization.util';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';
import { Mutex } from 'async-mutex';

/**
 * Circuit breaker states
 */
enum CircuitState {
  CLOSED = 'CLOSED', // Normal operation
  OPEN = 'OPEN', // Circuit is open, failing fast
  HALF_OPEN = 'HALF_OPEN', // Testing if service is back
}

/**
 * Circuit breaker configuration
 */
interface ICircuitBreakerConfig {
  failureThreshold: number;
  timeout: number;
  resetTimeout: number;
  name: string;
  monitoringEnabled: boolean;
}

/**
 * Circuit breaker statistics
 */
interface ICircuitBreakerStats {
  name: string;
  state: CircuitState;
  failures: number;
  successes: number;
  rejections: number;
  lastFailureTime: Date | null;
  lastSuccessTime: Date | null;
  nextAttemptTime: Date | null;
}

/**
 * Circuit breaker implementation for external service resilience
 *
 * **Purpose**: Prevent cascading failures by temporarily stopping calls
 * to failing external services and allowing them time to recover.
 *
 * **Circuit States**:
 * - CLOSED: Normal operation, all requests pass through
 * - OPEN: Service is failing, all requests are rejected immediately
 * - HALF_OPEN: Testing phase, limited requests allowed to test recovery
 *
 * **Key Features**:
 * - Configurable failure thresholds
 * - Automatic recovery testing
 * - Fast-fail for better user experience
 * - Comprehensive statistics and monitoring
 * - Multiple circuit breakers per service type
 *
 * **Use Cases**:
 * - SMS service API calls
 * - Email service API calls
 * - Storage service (S3/MinIO) operations
 * - External authentication services
 * - Payment gateway integrations
 */
@Injectable()
export class CircuitBreakerService implements OnModuleInit, OnModuleDestroy {
  private readonly circuits = new Map<
    string,
    {
      config: ICircuitBreakerConfig;
      state: CircuitState;
      failures: number;
      successes: number;
      rejections: number;
      lastFailureTime: Date | null;
      lastSuccessTime: Date | null;
      nextAttemptTime: Date | null;
    }
  >();

  // Thread-safety: One mutex per circuit for fine-grained locking
  private readonly circuitMutexes = new Map<string, Mutex>();

  private readonly logger: ILogger;

  // Security: Thread-safe metrics collection with mutex protection
  private readonly globalMetrics = {
    totalRequests: 0,
    totalFailures: 0,
    totalRejections: 0,
    totalTimeouts: 0,
    circuitOpenings: 0,
    circuitClosings: 0,
    circuitsCleanedUp: 0,
    lastResetTime: new Date(),
  };

  // Security: Mutex for global metrics to prevent race conditions
  private readonly globalMetricsMutex = new Mutex();

  // Cleanup intervals
  private metricsCleanupInterval: NodeJS.Timeout | null = null;
  private circuitCleanupInterval: NodeJS.Timeout | null = null;
  private readonly METRICS_RESET_INTERVAL = 24 * 60 * 60 * 1000; // 24 hours
  private readonly CIRCUIT_CLEANUP_INTERVAL = 60 * 60 * 1000; // 1 hour
  private readonly CIRCUIT_INACTIVITY_THRESHOLD = 24 * 60 * 60 * 1000; // 24 hours

  // Rate limiting for circuit creation (DoS protection)
  private readonly circuitCreationTracking = new Map<
    string,
    { count: number; resetTime: number }
  >();
  private readonly MAX_CIRCUITS_PER_IP_PER_HOUR = 10;
  private readonly MAX_RATE_LIMIT_ENTRIES = 1000; // Hard limit to prevent unbounded growth
  private readonly EMERGENCY_CLEANUP_THRESHOLD = 950; // Start aggressive cleanup early
  private readonly circuitCreationMutex = new Mutex();
  private cleanupInProgress = false; // Atomic flag to prevent concurrent cleanups

  // Default configuration
  private readonly defaultConfig: Omit<ICircuitBreakerConfig, 'name'>;

  constructor(
    private readonly configService: ConfigService,
    @Inject(LOGGER_SERVICE) logger: ILogger,
  ) {
    this.logger = logger;
    this.logger.setContext(CircuitBreakerService.name);

    // Load default configuration from environment
    this.defaultConfig = {
      failureThreshold: this.configService.get<number>('CIRCUIT_BREAKER_FAILURE_THRESHOLD', 5),
      timeout: this.configService.get<number>('CIRCUIT_BREAKER_TIMEOUT', 10000),
      resetTimeout: this.configService.get<number>('CIRCUIT_BREAKER_RESET_TIMEOUT', 60000),
      monitoringEnabled: this.configService.get<boolean>('CIRCUIT_BREAKER_MONITORING', true),
    };
  }

  // Security: Limit maximum circuits to prevent memory exhaustion attacks
  private static readonly MAX_CIRCUITS = 100;

  /**
   * Get or create a mutex for a specific circuit (Thread-safe with DoS protection)
   */
  private getCircuitMutex(serviceName: string): Mutex {
    // Security check: prevent unbounded Map growth
    if (
      this.circuitMutexes.size >= CircuitBreakerService.MAX_CIRCUITS &&
      !this.circuitMutexes.has(serviceName)
    ) {
      this.logger.error({
        message: 'Circuit breaker limit exceeded - potential DoS attack',
        currentCircuits: this.circuitMutexes.size,
        maxCircuits: CircuitBreakerService.MAX_CIRCUITS,
        rejectedService: serviceName,
      });
      throw new Error('Circuit breaker limit exceeded - service temporarily unavailable');
    }

    if (!this.circuitMutexes.has(serviceName)) {
      this.circuitMutexes.set(serviceName, new Mutex());
    }

    return this.circuitMutexes.get(serviceName)!;
  }

  /**
   * Create or get a circuit breaker for a specific service (with DoS protection)
   */
  getCircuit(
    serviceName: string,
    config?: Partial<ICircuitBreakerConfig>,
    clientIdentifier?: string,
  ): string {
    // Rate limiting check for new circuit creation
    if (!this.circuits.has(serviceName) && clientIdentifier) {
      const rateLimitCheck = this.checkCircuitCreationRateLimit(clientIdentifier);
      if (!rateLimitCheck.allowed) {
        this.logger.warn({
          message: 'Circuit creation rate limit exceeded',
          clientIdentifier,
          reason: rateLimitCheck.reason,
        });
        throw new Error('Too many circuit creation requests - please try again later');
      }
    }

    // Security check: prevent unbounded Map growth
    if (
      this.circuits.size >= CircuitBreakerService.MAX_CIRCUITS &&
      !this.circuits.has(serviceName)
    ) {
      this.logger.error({
        message: 'Maximum circuits exceeded - potential DoS attack',
        currentCircuits: this.circuits.size,
        maxCircuits: CircuitBreakerService.MAX_CIRCUITS,
        rejectedService: serviceName,
      });
      throw new Error('Circuit breaker limit exceeded - service temporarily unavailable');
    }

    if (!this.circuits.has(serviceName)) {
      const circuitConfig: ICircuitBreakerConfig = {
        ...this.defaultConfig,
        ...config,
        name: serviceName,
      };

      this.circuits.set(serviceName, {
        config: circuitConfig,
        state: CircuitState.CLOSED,
        failures: 0,
        successes: 0,
        rejections: 0,
        lastFailureTime: null,
        lastSuccessTime: null,
        nextAttemptTime: null,
      });

      if (circuitConfig.monitoringEnabled) {
        this.logger.log({
          message: `Circuit breaker created for service: ${serviceName}`,
          failureThreshold: circuitConfig.failureThreshold,
          timeout: circuitConfig.timeout,
          resetTimeout: circuitConfig.resetTimeout,
          totalCircuits: this.circuits.size,
        });
      }
    }

    return serviceName;
  }

  /**
   * Execute a function with circuit breaker protection (Thread-safe)
   */
  async execute<T>(
    serviceName: string,
    operation: () => Promise<T>,
    config?: Partial<ICircuitBreakerConfig>,
    clientIdentifier?: string,
  ): Promise<T> {
    const circuitKey = this.getCircuit(serviceName, config, clientIdentifier);
    const circuit = this.circuits.get(circuitKey)!;
    const mutex = this.getCircuitMutex(serviceName);

    // Thread-safe check if circuit allows the request
    const canExecute = await mutex.runExclusive(() => {
      return this.canExecute(circuit);
    });

    if (!canExecute) {
      // Security: Thread-safe rejection count increment with global metrics protection
      await Promise.all([
        mutex.runExclusive(() => {
          circuit.rejections++;
        }),
        this.globalMetricsMutex.runExclusive(() => {
          this.globalMetrics.totalRejections++;
        }),
      ]);

      const error = new Error(`Circuit breaker is OPEN for service: ${serviceName}`);
      error.name = 'CircuitBreakerError';

      if (circuit.config.monitoringEnabled) {
        this.logger.warn({
          message: 'Circuit breaker rejected request',
          service: serviceName,
          state: circuit.state,
          rejections: circuit.rejections,
          nextAttemptTime: circuit.nextAttemptTime,
        });
      }

      throw error;
    }

    try {
      // Security: Thread-safe request tracking
      await this.globalMetricsMutex.runExclusive(() => {
        this.globalMetrics.totalRequests++;
      });

      // Execute the operation
      const result = await Promise.race([
        operation(),
        this.createTimeoutPromise<T>(circuit.config.timeout, serviceName),
      ]);

      // Thread-safe success recording
      await mutex.runExclusive(async () => {
        await this.recordSuccess(circuit);
      });

      return result;
    } catch (error) {
      // Security: Thread-safe failure recording with global metrics protection
      await Promise.all([
        mutex.runExclusive(async () => {
          await this.recordFailure(circuit, error);
        }),
        this.globalMetricsMutex.runExclusive(() => {
          // Check if it's a timeout error
          if (error instanceof Error && error.name === 'CircuitBreakerTimeoutError') {
            this.globalMetrics.totalTimeouts++;
          }
          this.globalMetrics.totalFailures++;
        }),
      ]);
      throw error;
    }
  }

  /**
   * Get statistics for a specific circuit breaker
   */
  getStats(serviceName: string): ICircuitBreakerStats | null {
    const circuit = this.circuits.get(serviceName);
    if (!circuit) {
      return null;
    }

    return {
      name: serviceName,
      state: circuit.state,
      failures: circuit.failures,
      successes: circuit.successes,
      rejections: circuit.rejections,
      lastFailureTime: circuit.lastFailureTime,
      lastSuccessTime: circuit.lastSuccessTime,
      nextAttemptTime: circuit.nextAttemptTime,
    };
  }

  /**
   * Get statistics for all circuit breakers
   */
  getAllStats(): ICircuitBreakerStats[] {
    return Array.from(this.circuits.keys()).map(serviceName => this.getStats(serviceName)!);
  }

  /**
   * Get global circuit breaker metrics for observability (thread-safe)
   */
  async getGlobalMetrics() {
    // Security: Thread-safe access to global metrics
    return await this.globalMetricsMutex.runExclusive(() => {
      const circuitStates = Array.from(this.circuits.values());

      return {
        ...this.globalMetrics,
        totalCircuits: this.circuits.size,
        activeCircuits: circuitStates.filter(c => c.state !== CircuitState.CLOSED).length,
        openCircuits: circuitStates.filter(c => c.state === CircuitState.OPEN).length,
        halfOpenCircuits: circuitStates.filter(c => c.state === CircuitState.HALF_OPEN).length,
        circuitsCleanedUp: this.globalMetrics.circuitsCleanedUp,
        successRate:
          this.globalMetrics.totalRequests > 0
            ? Math.round(
                ((this.globalMetrics.totalRequests - this.globalMetrics.totalFailures) /
                  this.globalMetrics.totalRequests) *
                  100,
              )
            : 100,
        uptime: Math.round((Date.now() - this.globalMetrics.lastResetTime.getTime()) / 1000), // seconds
      };
    });
  }

  onModuleInit() {
    // Start periodic metrics cleanup to prevent memory leaks
    this.metricsCleanupInterval = setInterval(() => {
      this.resetMetrics();
    }, this.METRICS_RESET_INTERVAL);

    // Start periodic circuit cleanup to prevent memory leaks from inactive circuits
    this.circuitCleanupInterval = setInterval(() => {
      this.cleanupInactiveCircuits();
    }, this.CIRCUIT_CLEANUP_INTERVAL);

    // Clean up rate limiting tracking periodically
    setInterval(
      () => {
        this.cleanupRateLimitTracking();
      },
      60 * 60 * 1000,
    ); // Every hour

    this.logger.log({
      message: 'Circuit breaker service initialized',
      metricsResetInterval: `${this.METRICS_RESET_INTERVAL / 1000 / 60 / 60}h`,
      circuitCleanupInterval: `${this.CIRCUIT_CLEANUP_INTERVAL / 1000 / 60}m`,
      inactivityThreshold: `${this.CIRCUIT_INACTIVITY_THRESHOLD / 1000 / 60 / 60}h`,
    });
  }

  onModuleDestroy() {
    if (this.metricsCleanupInterval) {
      clearInterval(this.metricsCleanupInterval);
      this.metricsCleanupInterval = null;
    }
    if (this.circuitCleanupInterval) {
      clearInterval(this.circuitCleanupInterval);
      this.circuitCleanupInterval = null;
    }
    this.logger.log('Circuit breaker service destroyed');
  }

  /**
   * Reset global metrics to prevent memory leaks in long-running applications (thread-safe)
   */
  async resetMetrics(): Promise<void> {
    // Security: Thread-safe metrics reset
    await this.globalMetricsMutex.runExclusive(() => {
      const oldMetrics = { ...this.globalMetrics };

      this.globalMetrics.totalRequests = 0;
      this.globalMetrics.totalFailures = 0;
      this.globalMetrics.totalRejections = 0;
      this.globalMetrics.totalTimeouts = 0;
      this.globalMetrics.circuitOpenings = 0;
      this.globalMetrics.circuitClosings = 0;
      this.globalMetrics.lastResetTime = new Date();

      this.logger.log({
        message: 'Circuit breaker global metrics reset',
        previousMetrics: oldMetrics,
        resetTime: this.globalMetrics.lastResetTime,
      });
    });
  }

  /**
   * Manually reset a circuit breaker (for testing or emergency) (Deadlock-safe)
   */
  async reset(serviceName: string): Promise<boolean> {
    const circuit = this.circuits.get(serviceName);
    if (!circuit) {
      return false;
    }

    try {
      const mutex = this.getCircuitMutex(serviceName);

      // Security: Use try-acquire with timeout to prevent deadlocks
      const RESET_TIMEOUT = 5000; // 5 seconds

      const resetWithTimeout = Promise.race([
        mutex.runExclusive(() => {
          // Atomic reset operation
          circuit.state = CircuitState.CLOSED;
          circuit.failures = 0;
          circuit.successes = 0;
          circuit.rejections = 0;
          circuit.lastFailureTime = null;
          circuit.lastSuccessTime = null;
          circuit.nextAttemptTime = null;

          if (circuit.config.monitoringEnabled) {
            this.logger.log({
              message: 'Circuit breaker manually reset',
              service: serviceName,
              newState: circuit.state,
            });
          }

          return true;
        }),
        new Promise<boolean>((_, reject) =>
          setTimeout(
            () => reject(new Error('Reset operation timeout - potential deadlock detected')),
            RESET_TIMEOUT,
          ),
        ),
      ]);

      const result = await resetWithTimeout;

      return result;
    } catch (error) {
      const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'circuit-reset');
      this.logger.error({
        message: 'Circuit breaker reset failed',
        service: serviceName,
        error: sanitizedError.message,
        isTimeout: error instanceof Error && error.message.includes('timeout'),
      });

      return false;
    }
  }

  /**
   * Synchronous reset fallback (for backward compatibility, but discouraged)
   */
  resetSync(serviceName: string): boolean {
    const circuit = this.circuits.get(serviceName);
    if (!circuit) {
      return false;
    }

    try {
      // Only attempt if mutex is not locked to avoid deadlocks
      const mutex = this.getCircuitMutex(serviceName);
      if (mutex.isLocked()) {
        this.logger.warn({
          message: 'Circuit breaker reset skipped - mutex locked (potential deadlock averted)',
          service: serviceName,
        });

        return false;
      }

      // Immediate reset without waiting for mutex (less safe but non-blocking)
      circuit.state = CircuitState.CLOSED;
      circuit.failures = 0;
      circuit.successes = 0;
      circuit.rejections = 0;
      circuit.lastFailureTime = null;
      circuit.lastSuccessTime = null;
      circuit.nextAttemptTime = null;

      if (circuit.config.monitoringEnabled) {
        this.logger.log(`Circuit breaker force reset (sync) for service: ${serviceName}`);
      }

      return true;
    } catch (error) {
      const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'circuit-sync-reset');
      this.logger.error({
        message: 'Circuit breaker sync reset error',
        service: serviceName,
        error: sanitizedError.message,
      });

      return false;
    }
  }

  /**
   * Check if the circuit allows request execution
   */
  private canExecute(circuit: any): boolean {
    const now = new Date();

    switch (circuit.state) {
      case CircuitState.CLOSED:
        return true;

      case CircuitState.OPEN:
        // Check if it's time to try again (half-open)
        if (circuit.nextAttemptTime && now >= circuit.nextAttemptTime) {
          circuit.state = CircuitState.HALF_OPEN;
          if (circuit.config.monitoringEnabled) {
            this.logger.log(`Circuit breaker entering HALF_OPEN state: ${circuit.config.name}`);
          }

          return true;
        }

        return false;

      case CircuitState.HALF_OPEN:
        return true;

      default:
        return false;
    }
  }

  /**
   * Record a successful operation
   */
  private async recordSuccess(circuit: any): Promise<void> {
    circuit.successes++;
    circuit.lastSuccessTime = new Date();

    if (circuit.state === CircuitState.HALF_OPEN) {
      // Service appears to be recovered, close the circuit
      circuit.state = CircuitState.CLOSED;
      circuit.failures = 0; // Reset failure counter

      // Security: Thread-safe global metrics update - await to ensure order
      try {
        await this.globalMetricsMutex.runExclusive(() => {
          this.globalMetrics.circuitClosings++;
        });
      } catch (error) {
        this.logger.error({
          message: 'Failed to update global metrics for circuit closing',
          error: error instanceof Error ? error.message : String(error),
        });
      }

      if (circuit.config.monitoringEnabled) {
        this.logger.log({
          message: `Circuit breaker recovered and closed: ${circuit.config.name}`,
          totalSuccesses: circuit.successes,
        });
      }
    }
  }

  /**
   * Record a failed operation
   */
  private async recordFailure(circuit: any, error: any): Promise<void> {
    circuit.failures++;
    circuit.lastFailureTime = new Date();

    if (circuit.config.monitoringEnabled) {
      const sanitizedError = ErrorSanitizationUtil.forLogging(error, 'circuit-failure');
      this.logger.warn({
        message: `Circuit breaker recorded failure: ${circuit.config.name}`,
        error: sanitizedError.message,
        failures: circuit.failures,
        threshold: circuit.config.failureThreshold,
      });
    }

    // Check if we should open the circuit
    if (circuit.failures >= circuit.config.failureThreshold) {
      circuit.state = CircuitState.OPEN;
      circuit.nextAttemptTime = new Date(Date.now() + circuit.config.resetTimeout);

      // Security: Thread-safe global metrics update - await to ensure order
      try {
        await this.globalMetricsMutex.runExclusive(() => {
          this.globalMetrics.circuitOpenings++;
        });
      } catch (error) {
        this.logger.error({
          message: 'Failed to update global metrics for circuit opening',
          error: error instanceof Error ? error.message : String(error),
        });
      }

      if (circuit.config.monitoringEnabled) {
        this.logger.error({
          message: `Circuit breaker OPENED for service: ${circuit.config.name}`,
          failures: circuit.failures,
          threshold: circuit.config.failureThreshold,
          nextAttemptTime: circuit.nextAttemptTime,
        });
      }
    }
  }

  /**
   * Create a timeout promise for operations
   */
  private createTimeoutPromise<T>(timeout: number, serviceName: string): Promise<T> {
    return new Promise((_, reject) => {
      setTimeout(() => {
        const error = new Error(`Operation timeout for service: ${serviceName} (${timeout}ms)`);
        error.name = 'CircuitBreakerTimeoutError';
        reject(error);
      }, timeout);
    });
  }

  /**
   * Clean up inactive circuits to prevent memory leaks (Thread-safe)
   */
  private async cleanupInactiveCircuits(): Promise<void> {
    try {
      const now = Date.now();
      const circuitsToRemove: string[] = [];

      // Identify inactive circuits
      for (const [name, circuit] of this.circuits.entries()) {
        const lastActivityTime = Math.max(
          circuit.lastFailureTime?.getTime() || 0,
          circuit.lastSuccessTime?.getTime() || 0,
        );

        // If circuit has been inactive for threshold period and is in CLOSED state
        if (
          now - lastActivityTime > this.CIRCUIT_INACTIVITY_THRESHOLD &&
          circuit.state === CircuitState.CLOSED
        ) {
          circuitsToRemove.push(name);
        }
      }

      // Remove inactive circuits (thread-safe)
      let cleanedCount = 0;
      for (const name of circuitsToRemove) {
        try {
          const mutex = this.circuitMutexes.get(name);

          // Only clean up if mutex is not locked (circuit not in use)
          if (mutex && !mutex.isLocked()) {
            this.circuits.delete(name);
            this.circuitMutexes.delete(name);
            cleanedCount++;
          }
        } catch (error) {
          this.logger.warn({
            message: 'Error cleaning up circuit',
            circuit: name,
            error: error instanceof Error ? error.message : String(error),
          });
        }
      }

      // Update metrics if circuits were cleaned
      if (cleanedCount > 0) {
        await this.globalMetricsMutex.runExclusive(() => {
          this.globalMetrics.circuitsCleanedUp += cleanedCount;
        });

        this.logger.log({
          message: 'Inactive circuits cleaned up',
          cleanedCount,
          totalCircuits: this.circuits.size,
          inactivityThreshold: `${this.CIRCUIT_INACTIVITY_THRESHOLD / 1000 / 60 / 60}h`,
        });
      }
    } catch (error) {
      this.logger.error({
        message: 'Error during circuit cleanup',
        error: error instanceof Error ? error.message : String(error),
      });
    }
  }

  /**
   * Check rate limit for circuit creation (DoS protection)
   */
  private checkCircuitCreationRateLimit(clientIdentifier: string): {
    allowed: boolean;
    reason?: string;
  } {
    const now = Date.now();
    const hourInMs = 60 * 60 * 1000;

    // Enhanced protection against unbounded Map growth
    if (this.circuitCreationTracking.size >= this.EMERGENCY_CLEANUP_THRESHOLD) {
      // Atomic cleanup to prevent race conditions
      if (!this.cleanupInProgress) {
        this.cleanupInProgress = true;
        try {
          this.cleanupRateLimitTracking();
        } finally {
          this.cleanupInProgress = false;
        }
      }
    }

    // Hard limit enforcement after cleanup
    if (
      this.circuitCreationTracking.size >= this.MAX_RATE_LIMIT_ENTRIES &&
      !this.circuitCreationTracking.has(clientIdentifier)
    ) {
      return {
        allowed: false,
        reason: 'Rate limiting at capacity - service temporarily unavailable',
      };
    }

    // Get or create tracking entry
    let tracking = this.circuitCreationTracking.get(clientIdentifier);

    if (!tracking || now > tracking.resetTime) {
      // Reset tracking for this client
      tracking = {
        count: 0,
        resetTime: now + hourInMs,
      };
      this.circuitCreationTracking.set(clientIdentifier, tracking);
    }

    // Check if limit exceeded
    if (tracking.count >= this.MAX_CIRCUITS_PER_IP_PER_HOUR) {
      return {
        allowed: false,
        reason: `Exceeded limit of ${this.MAX_CIRCUITS_PER_IP_PER_HOUR} circuits per hour`,
      };
    }

    // Increment count
    tracking.count++;

    return { allowed: true };
  }

  /**
   * Clean up old rate limit tracking entries to prevent memory leaks (race-condition safe)
   */
  private cleanupRateLimitTracking(): void {
    // Prevent concurrent cleanups
    if (this.cleanupInProgress) {
      return;
    }

    try {
      const now = Date.now();
      let removedCount = 0;
      const entriesToRemove: string[] = [];

      // First pass: identify entries to remove (atomic read)
      for (const [clientId, tracking] of this.circuitCreationTracking.entries()) {
        if (now > tracking.resetTime) {
          entriesToRemove.push(clientId);
        }
      }

      // Second pass: atomic removal
      for (const clientId of entriesToRemove) {
        if (this.circuitCreationTracking.delete(clientId)) {
          removedCount++;
        }
      }

      // If still over threshold, remove oldest entries aggressively
      if (this.circuitCreationTracking.size >= this.EMERGENCY_CLEANUP_THRESHOLD) {
        const entries = Array.from(this.circuitCreationTracking.entries()).sort(
          (a, b) => a[1].resetTime - b[1].resetTime,
        );

        const excessCount =
          this.circuitCreationTracking.size - Math.floor(this.EMERGENCY_CLEANUP_THRESHOLD / 2);
        for (let i = 0; i < excessCount && i < entries.length; i++) {
          if (this.circuitCreationTracking.delete(entries[i][0])) {
            removedCount++;
          }
        }
      }

      if (removedCount > 0) {
        this.logger.debug({
          message: 'Cleaned up rate limit tracking entries',
          removedCount,
          remainingEntries: this.circuitCreationTracking.size,
          wasEmergencyCleanup: removedCount > entriesToRemove.length,
        });
      }
    } catch (error) {
      this.logger.error({
        message: 'Error cleaning up rate limit tracking',
        error: error instanceof Error ? error.message : String(error),
      });
    }
  }
}

