import { Injectable, OnModuleInit, OnModuleDestroy, Inject } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';
import { Mutex } from 'async-mutex';

/**
 * Memory monitoring service for application stability
 *
 * **Purpose**: Proactive memory management to prevent OOM crashes
 * and maintain application performance under high load.
 *
 * **Key Features**:
 * - Real-time heap memory monitoring
 * - Configurable warning and critical thresholds
 * - Automatic garbage collection triggers
 * - Emergency memory cleanup procedures
 * - Memory usage statistics and alerts
 *
 * **Monitoring Intervals**:
 * - Memory check every 30 seconds
 * - Emergency checks every 5 seconds when critical
 * - Immediate alerts on threshold breaches
 *
 * **Integration Points**:
 * - AuditLogQueueService for queue size management
 * - General application memory pressure
 * - Database connection pool sizing
 */
@Injectable()
export class MemoryMonitorService implements OnModuleInit, OnModuleDestroy {
  private readonly logger: ILogger;
  private monitoringInterval: NodeJS.Timeout | null = null;
  private emergencyInterval: NodeJS.Timeout | null = null;
  private isEmergencyMode = false;
  private secondaryGcTimeout: NodeJS.Timeout | null = null; // Track timeout for cleanup

  // Thread-safety primitives with atomic flags
  private readonly checkMutex = new Mutex();
  private readonly cleanupMutex = new Mutex();
  private isChecking = false;
  private mutexAcquired = false; // Atomic flag to prevent race conditions

  // Configuration
  private readonly monitoringEnabled: boolean;
  private readonly warningThreshold: number;
  private readonly criticalThreshold: number;
  private readonly maxOldSpaceSize: number;
  private readonly maxSemiSpaceSize: number;

  // Monitoring intervals
  private readonly NORMAL_CHECK_INTERVAL = 30000; // 30 seconds
  private readonly EMERGENCY_CHECK_INTERVAL = 5000; // 5 seconds
  private readonly GC_COOLDOWN = 10000; // 10 seconds between forced GC

  private lastGcTime = 0;

  constructor(
    private readonly configService: ConfigService,
    @Inject(LOGGER_SERVICE) logger: ILogger,
  ) {
    this.logger = logger;
    this.logger.setContext(MemoryMonitorService.name);

    // Load configuration with environment-specific defaults
    this.monitoringEnabled = this.configService.get<boolean>('MEMORY_MONITORING_ENABLED', true);

    // Environment-aware threshold configuration
    const nodeEnv = this.configService.get<string>('NODE_ENV', 'development');
    const environmentThresholds = this.getEnvironmentThresholds(nodeEnv);

    this.warningThreshold = this.configService.get<number>(
      'MEMORY_WARNING_THRESHOLD',
      environmentThresholds.warning,
    );
    this.criticalThreshold = this.configService.get<number>(
      'MEMORY_CRITICAL_THRESHOLD',
      environmentThresholds.critical,
    );
    this.maxOldSpaceSize = this.configService.get<number>(
      'NODE_MAX_OLD_SPACE_SIZE',
      environmentThresholds.maxHeap,
    );
    this.maxSemiSpaceSize = this.configService.get<number>(
      'NODE_MAX_SEMI_SPACE_SIZE',
      environmentThresholds.maxSemi,
    );
  }

  /**
   * Get environment-specific memory thresholds
   */
  private getEnvironmentThresholds(nodeEnv: string): {
    warning: number;
    critical: number;
    maxHeap: number;
    maxSemi: number;
  } {
    switch (nodeEnv) {
      case 'production':
        return {
          warning: 80, // More aggressive monitoring in production
          critical: 90, // Critical threshold remains high
          maxHeap: 4096, // 4GB heap for production
          maxSemi: 128, // 128MB semi-space
        };

      case 'test':
        return {
          warning: 60, // Lower thresholds for testing (limited resources)
          critical: 75, // Earlier critical detection in tests
          maxHeap: 1024, // 1GB heap for tests
          maxSemi: 32, // 32MB semi-space
        };

      case 'staging':
        return {
          warning: 75, // Balanced for staging environment
          critical: 85, // Moderate critical threshold
          maxHeap: 2048, // 2GB heap for staging
          maxSemi: 64, // 64MB semi-space
        };

      case 'development':
      default:
        return {
          warning: 70, // Lower threshold for development (frequent GC warnings help detect issues)
          critical: 85, // Still need protection from OOM
          maxHeap: 2048, // 2GB heap for development
          maxSemi: 64, // 64MB semi-space
        };
    }
  }

  onModuleInit() {
    if (this.monitoringEnabled) {
      // Check if memory options are actually applied
      const currentStats = this.getMemoryStats();

      this.startMonitoring();
      // Security: Sanitize Node.js environment information to prevent information disclosure
      const nodeOptionsInfo = this.sanitizeNodeOptions(process.env.NODE_OPTIONS);
      const sanitizedV8Stats = this.sanitizeV8Statistics(process.memoryUsage());

      this.logger.log({
        message: 'Memory monitoring started',
        configuredMaxHeapMB: this.maxOldSpaceSize,
        actualHeapMB: currentStats.heapTotalMB,
        warningThreshold: `${this.warningThreshold}%`,
        criticalThreshold: `${this.criticalThreshold}%`,
        nodeOptions: nodeOptionsInfo,
        v8HeapStatistics: sanitizedV8Stats,
      });
    }
  }

  onModuleDestroy() {
    this.stopMonitoring();
    // Clean up any pending secondary GC timeout
    if (this.secondaryGcTimeout) {
      clearTimeout(this.secondaryGcTimeout);
      this.secondaryGcTimeout = null;
    }
  }

  /**
   * Get current memory statistics
   */
  getMemoryStats(): {
    heapUsed: number;
    heapTotal: number;
    heapUsedMB: number;
    heapTotalMB: number;
    heapUsagePercent: number;
    heapUsagePercentOfMax: number;
    maxHeapMB: number;
    external: number;
    rss: number;
    isWarning: boolean;
    isCritical: boolean;
  } {
    const memUsage = process.memoryUsage();
    const heapUsedMB = Math.round(memUsage.heapUsed / 1024 / 1024);
    const heapTotalMB = Math.round(memUsage.heapTotal / 1024 / 1024);

    // Calculate percentage based on current heap allocation
    const heapUsagePercent = Math.round((memUsage.heapUsed / memUsage.heapTotal) * 100);

    // Calculate percentage based on configured maximum heap size
    const maxHeapBytes = this.maxOldSpaceSize * 1024 * 1024;
    const heapUsagePercentOfMax = Math.round((memUsage.heapUsed / maxHeapBytes) * 100);

    return {
      heapUsed: memUsage.heapUsed,
      heapTotal: memUsage.heapTotal,
      heapUsedMB,
      heapTotalMB,
      heapUsagePercent,
      heapUsagePercentOfMax,
      maxHeapMB: this.maxOldSpaceSize,
      external: memUsage.external,
      rss: memUsage.rss,
      // Use percentage of configured max for thresholds, not current heap
      isWarning: heapUsagePercentOfMax >= this.warningThreshold,
      isCritical: heapUsagePercentOfMax >= this.criticalThreshold,
    };
  }

  /**
   * Force garbage collection if available
   */
  forceGarbageCollection(): boolean {
    const now = Date.now();

    // Cooldown check to prevent excessive GC calls
    if (now - this.lastGcTime < this.GC_COOLDOWN) {
      return false;
    }

    if (global.gc) {
      try {
        // Schedule GC with proper delay to avoid blocking event loop
        setTimeout(() => {
          try {
            if (global.gc) global.gc();
            this.logger.log('Forced garbage collection executed');
          } catch (error) {
            this.logger.error('GC execution failed:', error);
          }
        }, 10); // Small delay to prevent blocking
        this.lastGcTime = now;

        return true;
      } catch (error) {
        this.logger.error({
          message: 'Failed to force garbage collection',
          error: error instanceof Error ? error.message : String(error),
        });

        return false;
      }
    } else {
      this.logger.warn('Garbage collection not available - start Node.js with --expose-gc flag');

      return false;
    }
  }

  /**
   * Start memory monitoring
   */
  private startMonitoring(): void {
    this.monitoringInterval = setInterval(async () => {
      try {
        await this.checkMemoryUsage();
      } catch (error) {
        this.logger.error({
          message: 'Error in memory monitoring interval',
          error: error instanceof Error ? error.message : String(error),
        });
      }
    }, this.NORMAL_CHECK_INTERVAL);
  }

  /**
   * Sanitize Node.js options to prevent information disclosure
   */
  private sanitizeNodeOptions(nodeOptions?: string): string {
    if (!nodeOptions) {
      return 'Not configured';
    }

    // Security: Only expose safe memory-related options, hide potentially sensitive ones
    const safeOptions = [];
    const options = nodeOptions.split(' ').filter(opt => opt.trim());

    for (const option of options) {
      const trimmed = option.trim();
      if (trimmed.startsWith('--max-old-space-size=')) {
        safeOptions.push('--max-old-space-size=[CONFIGURED]');
      } else if (trimmed.startsWith('--max-semi-space-size=')) {
        safeOptions.push('--max-semi-space-size=[CONFIGURED]');
      } else if (trimmed === '--expose-gc') {
        safeOptions.push('--expose-gc');
      } else if (trimmed.startsWith('--')) {
        // Hide other potentially sensitive options
        safeOptions.push('[OPTION_HIDDEN]');
      }
    }

    return safeOptions.length > 0 ? safeOptions.join(' ') : 'No memory options configured';
  }

  /**
   * Sanitize V8 heap statistics to show only essential memory info
   */
  private sanitizeV8Statistics(memUsage: NodeJS.MemoryUsage): Record<string, string | number> {
    return {
      heapUsedMB: Math.round(memUsage.heapUsed / 1024 / 1024),
      heapTotalMB: Math.round(memUsage.heapTotal / 1024 / 1024),
      rssMB: Math.round(memUsage.rss / 1024 / 1024),
      // Hide external and arrayBuffers as they could reveal internal details
      externalMB: memUsage.external ? Math.round(memUsage.external / 1024 / 1024) : 'N/A',
      arrayBuffersMB: (memUsage as any).arrayBuffers
        ? Math.round((memUsage as any).arrayBuffers / 1024 / 1024)
        : 'N/A',
    };
  }

  /**
   * Stop memory monitoring
   */
  private stopMonitoring(): void {
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }

    if (this.emergencyInterval) {
      clearInterval(this.emergencyInterval);
      this.emergencyInterval = null;
    }
  }

  /**
   * Start emergency monitoring (more frequent checks)
   */
  private startEmergencyMonitoring(): void {
    if (this.emergencyInterval) {
      return; // Already in emergency mode
    }

    this.isEmergencyMode = true;
    this.emergencyInterval = setInterval(async () => {
      try {
        await this.checkMemoryUsage(true);
      } catch (error) {
        this.logger.error({
          message: 'Error in emergency memory monitoring interval',
          error: error instanceof Error ? error.message : String(error),
        });
      }
    }, this.EMERGENCY_CHECK_INTERVAL);

    this.logger.warn('Emergency memory monitoring activated');
  }

  /**
   * Stop emergency monitoring
   */
  private stopEmergencyMonitoring(): void {
    if (this.emergencyInterval) {
      clearInterval(this.emergencyInterval);
      this.emergencyInterval = null;
      this.isEmergencyMode = false;
      this.logger.log('Emergency memory monitoring deactivated');
    }
  }

  /**
   * Check current memory usage and take action if needed (Thread-safe)
   */
  private async checkMemoryUsage(isEmergencyCheck = false): Promise<void> {
    // Atomic check to prevent race conditions
    if (this.isChecking || this.mutexAcquired) {
      return;
    }

    // Add timeout with proper cleanup
    const MUTEX_TIMEOUT = 1000; // 1 second
    let release: (() => void) | null = null;
    let timeoutId: NodeJS.Timeout | null = null;

    try {
      // Atomic flag to prevent multiple acquisitions
      this.mutexAcquired = true;

      const mutexPromise = this.checkMutex.acquire();
      const timeoutPromise = new Promise<never>((_, reject) => {
        timeoutId = setTimeout(() => {
          reject(new Error('Mutex timeout'));
        }, MUTEX_TIMEOUT);
      });

      release = await Promise.race([mutexPromise, timeoutPromise]);

      // Clear timeout if mutex acquired successfully
      if (timeoutId) {
        clearTimeout(timeoutId);
        timeoutId = null;
      }
    } catch (_error) {
      this.mutexAcquired = false;
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
      this.logger.warn('Memory check skipped due to mutex timeout');

      return;
    }

    try {
      if (this.isChecking) {
        return; // Double-check in case of race condition
      }

      this.isChecking = true;
      const stats = this.getMemoryStats();

      // Log current usage (debug level for normal checks, info for emergency)
      const memoryLogData = {
        message: 'Memory usage check',
        heapUsedMB: stats.heapUsedMB,
        heapTotalMB: stats.heapTotalMB,
        heapMaxMB: stats.maxHeapMB,
        heapUsagePercent: `${stats.heapUsagePercent}%`,
        heapUsageOfMaxPercent: `${stats.heapUsagePercentOfMax}%`,
        rss: Math.round(stats.rss / 1024 / 1024) + 'MB',
        isEmergencyMode: this.isEmergencyMode,
      };

      if (isEmergencyCheck) {
        this.logger.log(memoryLogData);
      } else {
        this.logger.debug(memoryLogData);
      }

      // Handle critical memory usage
      if (stats.isCritical) {
        await this.handleCriticalMemory(stats);

        if (!this.isEmergencyMode) {
          this.startEmergencyMonitoring();
        }
      }
      // Handle warning level memory usage
      else if (stats.isWarning) {
        await this.handleWarningMemory(stats);
      }
      // Memory is back to normal
      else if (this.isEmergencyMode) {
        this.stopEmergencyMonitoring();
        this.logger.log({
          message: 'Memory usage back to normal levels',
          heapUsageOfMaxPercent: `${stats.heapUsagePercentOfMax}%`,
          heapUsedMB: stats.heapUsedMB,
          maxHeapMB: stats.maxHeapMB,
        });
      }
    } catch (error) {
      this.logger.error({
        message: 'Error during memory usage check',
        isEmergencyCheck,
        error: error instanceof Error ? error.message : String(error),
      });
    } finally {
      this.isChecking = false;
      this.mutexAcquired = false;
      if (release) {
        release();
      }
    }
  }

  /**
   * Handle warning level memory usage
   */
  private async handleWarningMemory(stats: ReturnType<typeof this.getMemoryStats>): Promise<void> {
    this.logger.warn({
      message: 'Memory usage warning threshold reached',
      heapUsageOfMaxPercent: `${stats.heapUsagePercentOfMax}%`,
      threshold: `${this.warningThreshold}%`,
      heapUsedMB: stats.heapUsedMB,
      maxHeapMB: stats.maxHeapMB,
      recommendation: 'Consider optimizing memory usage or increasing heap size',
    });

    // Trigger gentle garbage collection
    this.forceGarbageCollection();
  }

  /**
   * Handle critical memory usage
   */
  private async handleCriticalMemory(stats: ReturnType<typeof this.getMemoryStats>): Promise<void> {
    this.logger.error({
      message: 'CRITICAL: Memory usage critical threshold reached',
      heapUsageOfMaxPercent: `${stats.heapUsagePercentOfMax}%`,
      threshold: `${this.criticalThreshold}%`,
      heapUsedMB: stats.heapUsedMB,
      maxHeapMB: stats.maxHeapMB,
      actualHeapMB: stats.heapTotalMB,
      action: 'Initiating emergency memory cleanup',
    });

    // Emergency actions
    await this.emergencyMemoryCleanup();
  }

  /**
   * Emergency memory cleanup procedures (Thread-safe)
   */
  private async emergencyMemoryCleanup(): Promise<void> {
    // Use mutex with timeout to prevent deadlocks
    const MUTEX_TIMEOUT = 2000; // 2 seconds
    let release;
    try {
      release = await Promise.race([
        this.cleanupMutex.acquire(),
        new Promise((_, reject) =>
          setTimeout(() => reject(new Error('Cleanup mutex timeout')), MUTEX_TIMEOUT),
        ),
      ]);
    } catch (_error) {
      this.logger.error('Emergency cleanup aborted due to mutex timeout');

      return;
    }

    try {
      this.logger.warn('Starting emergency memory cleanup');

      const beforeUsage = process.memoryUsage();

      // 1. Force garbage collection (safe operation)
      const gcSuccess = this.forceGarbageCollection();

      // 2. REMOVED: Cache clearing is too dangerous and can break the application
      // Instead, just force GC multiple times if needed
      if (global.gc && gcSuccess) {
        // Clear any existing timeout first
        if (this.secondaryGcTimeout) {
          clearTimeout(this.secondaryGcTimeout);
          this.secondaryGcTimeout = null;
        }

        // Multiple GC passes for thorough cleanup (with proper delay)
        this.secondaryGcTimeout = setTimeout(() => {
          try {
            if (global.gc) {
              // Execute in next tick to avoid blocking
              setImmediate(() => {
                try {
                  if (global.gc) global.gc();
                } catch (error) {
                  this.logger.warn('Secondary GC failed:', error);
                }
              });
            }
          } catch (error) {
            this.logger.warn({
              message: 'Secondary garbage collection setup failed',
              error: error instanceof Error ? error.message : String(error),
            });
          } finally {
            this.secondaryGcTimeout = null;
          }
        }, 200); // Increased delay for better spacing
      }

      // 3. Log emergency cleanup completion
      const postStats = this.getMemoryStats();
      this.logger.warn({
        message: 'Emergency memory cleanup completed',
        gcExecuted: gcSuccess,
        beforeMB: Math.round(beforeUsage.heapUsed / 1024 / 1024),
        afterMB: postStats.heapUsedMB,
        newUsageOfMaxPercent: `${postStats.heapUsagePercentOfMax}%`,
        actualHeapMB: postStats.heapTotalMB,
        maxHeapMB: postStats.maxHeapMB,
      });
    } catch (error) {
      this.logger.error({
        message: 'Error during emergency memory cleanup',
        error: error instanceof Error ? error.message : String(error),
      });
    } finally {
      release();
    }
  }
}

import { Injectable, Inject, OnModuleInit, OnModuleDestroy, Optional } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';
import { MemoryMonitorService } from './memory-monitor.service';
import { CircuitBreakerService } from './circuit-breaker.service';
import { AuditLogQueueService } from './audit-log-queue.service';

/**
 * Health check status for monitoring services
 */
enum HealthStatus {
  HEALTHY = 'HEALTHY',
  WARNING = 'WARNING',
  CRITICAL = 'CRITICAL',
  UNKNOWN = 'UNKNOWN',
}

/**
 * Health check result interface
 */
interface IHealthCheckResult {
  service: string;
  status: HealthStatus;
  message: string;
  details?: Record<string, any>;
  timestamp: Date;
  responseTimeMs?: number;
}

/**
 * System health summary
 */
interface ISystemHealthSummary {
  overallStatus: HealthStatus;
  healthyServices: number;
  warningServices: number;
  criticalServices: number;
  totalServices: number;
  lastCheckTime: Date;
  checks: IHealthCheckResult[];
}

/**
 * Comprehensive health monitoring for all monitoring services
 *
 * **Purpose**: Proactive detection of issues in monitoring infrastructure
 * to ensure the stability systems themselves are working correctly.
 *
 * **Key Features**:
 * - Health checks for all monitoring services
 * - Automatic issue detection and alerting
 * - Performance monitoring of monitoring systems
 * - Consolidated health status reporting
 * - Early warning system for infrastructure problems
 *
 * **Health Check Areas**:
 * - Memory monitoring service responsiveness
 * - Circuit breaker service state consistency
 * - Audit log queue processing health
 * - Thread safety and mutex performance
 * - Configuration validation
 */
@Injectable()
export class MonitoringHealthService implements OnModuleInit, OnModuleDestroy {
  private readonly logger: ILogger;
  private healthCheckInterval: NodeJS.Timeout | null = null;

  // Configuration
  private readonly healthCheckEnabled: boolean;
  private readonly healthCheckIntervalMs: number;
  private readonly responseTimeThresholdMs: number;
  private activeTimeouts = new Set<NodeJS.Timeout>(); // Track active timeouts for cleanup

  constructor(
    private readonly configService: ConfigService,
    @Inject(LOGGER_SERVICE) logger: ILogger,
    @Optional() private readonly memoryMonitorService?: MemoryMonitorService,
    @Optional() private readonly circuitBreakerService?: CircuitBreakerService,
    @Optional() private readonly auditLogQueueService?: AuditLogQueueService,
  ) {
    this.logger = logger;
    this.logger.setContext(MonitoringHealthService.name);

    // Load configuration
    this.healthCheckEnabled = this.configService.get<boolean>('MONITORING_HEALTH_ENABLED', true);
    this.healthCheckIntervalMs = this.configService.get<number>(
      'MONITORING_HEALTH_INTERVAL',
      60000,
    ); // 1 minute
    this.responseTimeThresholdMs = this.configService.get<number>(
      'MONITORING_HEALTH_RESPONSE_THRESHOLD',
      1000,
    ); // 1 second
  }

  onModuleInit() {
    if (this.healthCheckEnabled) {
      // Validate monitoring service startup sequence
      this.validateMonitoringServices();

      this.startHealthChecks();
      this.logger.log({
        message: 'Monitoring health service started',
        intervalMs: this.healthCheckIntervalMs,
        responseThresholdMs: this.responseTimeThresholdMs,
        availableServices: {
          memoryMonitor: !!this.memoryMonitorService,
          circuitBreaker: !!this.circuitBreakerService,
          auditLogQueue: !!this.auditLogQueueService,
        },
      });
    }
  }

  /**
   * Validate that monitoring services are available and functioning
   */
  private validateMonitoringServices(): void {
    const validationResults = [];

    // Check Memory Monitor Service
    if (this.memoryMonitorService) {
      try {
        const stats = this.memoryMonitorService.getMemoryStats();
        if (stats && typeof stats.heapUsedMB === 'number') {
          validationResults.push({
            service: 'MemoryMonitor',
            status: 'OK',
            heap: stats.heapUsedMB,
          });
        } else {
          validationResults.push({
            service: 'MemoryMonitor',
            status: 'INVALID_RESPONSE',
            response: stats,
          });
        }
      } catch (error) {
        validationResults.push({
          service: 'MemoryMonitor',
          status: 'ERROR',
          error: error instanceof Error ? error.message : String(error),
        });
      }
    } else {
      validationResults.push({ service: 'MemoryMonitor', status: 'NOT_AVAILABLE' });
    }

    // Check Circuit Breaker Service
    if (this.circuitBreakerService) {
      try {
        const stats = this.circuitBreakerService.getAllStats();
        validationResults.push({ service: 'CircuitBreaker', status: 'OK', circuits: stats.length });
      } catch (error) {
        validationResults.push({
          service: 'CircuitBreaker',
          status: 'ERROR',
          error: error instanceof Error ? error.message : String(error),
        });
      }
    } else {
      validationResults.push({ service: 'CircuitBreaker', status: 'NOT_AVAILABLE' });
    }

    // Check Audit Log Queue Service
    if (this.auditLogQueueService) {
      try {
        const stats = this.auditLogQueueService.getQueueStats();
        if (stats && typeof stats.queueSize === 'number') {
          validationResults.push({
            service: 'AuditLogQueue',
            status: 'OK',
            queueSize: stats.queueSize,
          });
        } else {
          validationResults.push({
            service: 'AuditLogQueue',
            status: 'INVALID_RESPONSE',
            response: stats,
          });
        }
      } catch (error) {
        validationResults.push({
          service: 'AuditLogQueue',
          status: 'ERROR',
          error: error instanceof Error ? error.message : String(error),
        });
      }
    } else {
      validationResults.push({ service: 'AuditLogQueue', status: 'NOT_AVAILABLE' });
    }

    // Log validation results
    const errors = validationResults.filter(r => r.status === 'ERROR');
    const warnings = validationResults.filter(
      r => r.status === 'NOT_AVAILABLE' || r.status === 'INVALID_RESPONSE',
    );

    if (errors.length > 0) {
      this.logger.error({
        message: 'Monitoring service validation found critical errors',
        errors,
        allResults: validationResults,
      });
    } else if (warnings.length > 0) {
      this.logger.warn({
        message: 'Monitoring service validation found warnings',
        warnings,
        allResults: validationResults,
      });
    } else {
      this.logger.log({
        message: 'All monitoring services validated successfully',
        results: validationResults,
      });
    }
  }

  onModuleDestroy() {
    this.stopHealthChecks();

    // Clean up all active timeouts
    for (const timeoutId of this.activeTimeouts) {
      clearTimeout(timeoutId);
    }
    this.activeTimeouts.clear();

    this.logger.log('Monitoring health service destroyed');
  }

  /**
   * Get comprehensive system health status
   */
  async getSystemHealth(): Promise<ISystemHealthSummary> {
    const checks = await this.performAllHealthChecks();

    const healthyServices = checks.filter(c => c.status === HealthStatus.HEALTHY).length;
    const warningServices = checks.filter(c => c.status === HealthStatus.WARNING).length;
    const criticalServices = checks.filter(c => c.status === HealthStatus.CRITICAL).length;

    let overallStatus = HealthStatus.HEALTHY;
    if (criticalServices > 0) {
      overallStatus = HealthStatus.CRITICAL;
    } else if (warningServices > 0) {
      overallStatus = HealthStatus.WARNING;
    }

    return {
      overallStatus,
      healthyServices,
      warningServices,
      criticalServices,
      totalServices: checks.length,
      lastCheckTime: new Date(),
      checks,
    };
  }

  /**
   * Perform health checks for all monitoring services (with timeout protection)
   */
  private async performAllHealthChecks(): Promise<IHealthCheckResult[]> {
    const checks: Promise<IHealthCheckResult>[] = [
      this.checkSystemResourceHealth(), // Always available
    ];

    // Only check services that are available
    if (this.memoryMonitorService) {
      checks.push(this.checkMemoryMonitorHealth());
    }
    if (this.circuitBreakerService) {
      checks.push(this.checkCircuitBreakerHealth());
    }
    if (this.auditLogQueueService) {
      checks.push(this.checkAuditLogQueueHealth());
    }

    try {
      // Add timeout protection with proper cleanup
      const healthCheckTimeout = this.configService.get<number>('MONITORING_HEALTH_TIMEOUT', 5000); // 5 seconds

      let timeoutId: NodeJS.Timeout | null = null;
      const timeoutPromise = new Promise<IHealthCheckResult[]>((_, reject) => {
        timeoutId = setTimeout(() => {
          reject(new Error('Health check timeout'));
        }, healthCheckTimeout);
        // Track timeout for cleanup
        if (timeoutId) {
          this.activeTimeouts.add(timeoutId);
        }
      });

      try {
        const result = await Promise.race([Promise.all(checks), timeoutPromise]);

        return result;
      } finally {
        // Clean up timeout whether it fired or not
        if (timeoutId) {
          clearTimeout(timeoutId);
          this.activeTimeouts.delete(timeoutId);
        }
      }
    } catch (error) {
      this.logger.error({
        message: 'Error performing health checks',
        error: error instanceof Error ? error.message : String(error),
        isTimeout: error instanceof Error && error.message === 'Health check timeout',
      });

      return [
        {
          service: 'health-check-system',
          status: HealthStatus.CRITICAL,
          message:
            error instanceof Error && error.message === 'Health check timeout'
              ? 'Health check system timeout - checks taking too long'
              : 'Health check system failure',
          details: { error: error instanceof Error ? error.message : String(error) },
          timestamp: new Date(),
        },
      ];
    }
  }

  /**
   * Health check for memory monitoring service
   */
  private async checkMemoryMonitorHealth(): Promise<IHealthCheckResult> {
    const startTime = Date.now();
    const serviceName = 'memory-monitor';

    try {
      // Check if service is available
      if (!this.memoryMonitorService) {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: 'Memory monitor service not available',
          timestamp: new Date(),
          responseTimeMs: Date.now() - startTime,
        };
      }

      // Test memory stats retrieval
      const memoryStats = this.memoryMonitorService.getMemoryStats();
      const responseTime = Date.now() - startTime;

      // Validate response structure
      if (!memoryStats || typeof memoryStats.heapUsedMB !== 'number') {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: 'Memory monitor returning invalid data structure',
          details: { memoryStats },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      // Check response time
      if (responseTime > this.responseTimeThresholdMs) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `Memory monitor response time high: ${responseTime}ms`,
          details: { responseTimeMs: responseTime, threshold: this.responseTimeThresholdMs },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      // Check for concerning memory levels
      if (memoryStats.isCritical) {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: `Critical memory usage detected: ${memoryStats.heapUsagePercentOfMax}%`,
          details: {
            heapUsedMB: memoryStats.heapUsedMB,
            heapUsagePercent: memoryStats.heapUsagePercentOfMax,
            maxHeapMB: memoryStats.maxHeapMB,
          },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      if (memoryStats.isWarning) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `Warning memory usage: ${memoryStats.heapUsagePercentOfMax}%`,
          details: {
            heapUsedMB: memoryStats.heapUsedMB,
            heapUsagePercent: memoryStats.heapUsagePercentOfMax,
          },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      return {
        service: serviceName,
        status: HealthStatus.HEALTHY,
        message: `Memory monitoring healthy - ${memoryStats.heapUsedMB}MB used (${memoryStats.heapUsagePercentOfMax}%)`,
        details: {
          heapUsedMB: memoryStats.heapUsedMB,
          heapUsagePercent: memoryStats.heapUsagePercentOfMax,
        },
        timestamp: new Date(),
        responseTimeMs: responseTime,
      };
    } catch (error) {
      return {
        service: serviceName,
        status: HealthStatus.CRITICAL,
        message: 'Memory monitor health check failed',
        details: { error: error instanceof Error ? error.message : String(error) },
        timestamp: new Date(),
        responseTimeMs: Date.now() - startTime,
      };
    }
  }

  /**
   * Health check for circuit breaker service
   */
  private async checkCircuitBreakerHealth(): Promise<IHealthCheckResult> {
    const startTime = Date.now();
    const serviceName = 'circuit-breaker';

    try {
      // Check if service is available
      if (!this.circuitBreakerService) {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: 'Circuit breaker service not available',
          timestamp: new Date(),
          responseTimeMs: Date.now() - startTime,
        };
      }

      // Get all circuit breaker stats
      const allStats = this.circuitBreakerService.getAllStats();
      const responseTime = Date.now() - startTime;

      // Check response time
      if (responseTime > this.responseTimeThresholdMs) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `Circuit breaker response time high: ${responseTime}ms`,
          details: { responseTimeMs: responseTime, circuitCount: allStats.length },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      // Analyze circuit states
      const openCircuits = allStats.filter(c => c.state === 'OPEN');
      const halfOpenCircuits = allStats.filter(c => c.state === 'HALF_OPEN');
      const totalFailures = allStats.reduce((sum, c) => sum + c.failures, 0);
      const totalRejections = allStats.reduce((sum, c) => sum + c.rejections, 0);

      if (openCircuits.length > 0) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `${openCircuits.length} circuit(s) in OPEN state`,
          details: {
            openCircuits: openCircuits.map(c => c.name),
            totalCircuits: allStats.length,
            totalFailures,
            totalRejections,
          },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      if (totalRejections > 100) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `High rejection count: ${totalRejections}`,
          details: {
            totalRejections,
            totalCircuits: allStats.length,
            halfOpenCircuits: halfOpenCircuits.length,
          },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      return {
        service: serviceName,
        status: HealthStatus.HEALTHY,
        message: `Circuit breaker healthy - ${allStats.length} circuits monitored`,
        details: {
          totalCircuits: allStats.length,
          openCircuits: openCircuits.length,
          halfOpenCircuits: halfOpenCircuits.length,
          totalFailures,
        },
        timestamp: new Date(),
        responseTimeMs: responseTime,
      };
    } catch (error) {
      return {
        service: serviceName,
        status: HealthStatus.CRITICAL,
        message: 'Circuit breaker health check failed',
        details: { error: error instanceof Error ? error.message : String(error) },
        timestamp: new Date(),
        responseTimeMs: Date.now() - startTime,
      };
    }
  }

  /**
   * Health check for audit log queue service
   */
  private async checkAuditLogQueueHealth(): Promise<IHealthCheckResult> {
    const startTime = Date.now();
    const serviceName = 'audit-log-queue';

    try {
      // Check if service is available
      if (!this.auditLogQueueService) {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: 'Audit log queue service not available',
          timestamp: new Date(),
          responseTimeMs: Date.now() - startTime,
        };
      }

      // Get queue stats
      const queueStats = this.auditLogQueueService.getQueueStats();
      const responseTime = Date.now() - startTime;

      // Validate response
      if (typeof queueStats.queueSize !== 'number') {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: 'Audit log queue returning invalid stats',
          details: { queueStats },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      // Check response time
      if (responseTime > this.responseTimeThresholdMs) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `Audit log queue response time high: ${responseTime}ms`,
          details: { responseTimeMs: responseTime },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      // Check queue size thresholds
      const queueSizePercent = (queueStats.queueSize / queueStats.maxQueueSize) * 100;

      if (queueSizePercent > 90) {
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: `Audit log queue near capacity: ${queueSizePercent.toFixed(1)}%`,
          details: {
            queueSize: queueStats.queueSize,
            maxQueueSize: queueStats.maxQueueSize,
            queueSizePercent: queueSizePercent.toFixed(1),
            isProcessing: queueStats.isProcessing,
          },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      if (queueSizePercent > 75) {
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `Audit log queue size high: ${queueSizePercent.toFixed(1)}%`,
          details: {
            queueSize: queueStats.queueSize,
            maxQueueSize: queueStats.maxQueueSize,
            queueSizePercent: queueSizePercent.toFixed(1),
            isProcessing: queueStats.isProcessing,
          },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      return {
        service: serviceName,
        status: HealthStatus.HEALTHY,
        message: `Audit log queue healthy - ${queueStats.queueSize}/${queueStats.maxQueueSize} (${queueSizePercent.toFixed(1)}%)`,
        details: {
          queueSize: queueStats.queueSize,
          queueSizePercent: queueSizePercent.toFixed(1),
          isProcessing: queueStats.isProcessing,
        },
        timestamp: new Date(),
        responseTimeMs: responseTime,
      };
    } catch (error) {
      return {
        service: serviceName,
        status: HealthStatus.CRITICAL,
        message: 'Audit log queue health check failed',
        details: { error: error instanceof Error ? error.message : String(error) },
        timestamp: new Date(),
        responseTimeMs: Date.now() - startTime,
      };
    }
  }

  /**
   * Health check for system resources
   */
  private async checkSystemResourceHealth(): Promise<IHealthCheckResult> {
    const startTime = Date.now();
    const serviceName = 'system-resources';

    try {
      const memUsage = process.memoryUsage();
      const uptime = process.uptime();
      const responseTime = Date.now() - startTime;

      // Check for memory pressure at OS level
      const rssMB = Math.round(memUsage.rss / 1024 / 1024);
      const externalMB = Math.round(memUsage.external / 1024 / 1024);

      // Simple thresholds for system health
      if (rssMB > 8192) {
        // 8GB RSS
        return {
          service: serviceName,
          status: HealthStatus.CRITICAL,
          message: `Very high RSS memory usage: ${rssMB}MB`,
          details: { rssMB, externalMB, uptimeHours: (uptime / 3600).toFixed(1) },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      if (rssMB > 4096) {
        // 4GB RSS
        return {
          service: serviceName,
          status: HealthStatus.WARNING,
          message: `High RSS memory usage: ${rssMB}MB`,
          details: { rssMB, externalMB, uptimeHours: (uptime / 3600).toFixed(1) },
          timestamp: new Date(),
          responseTimeMs: responseTime,
        };
      }

      return {
        service: serviceName,
        status: HealthStatus.HEALTHY,
        message: `System resources healthy - RSS: ${rssMB}MB, Uptime: ${(uptime / 3600).toFixed(1)}h`,
        details: {
          rssMB,
          externalMB,
          uptimeHours: (uptime / 3600).toFixed(1),
          heapUsedMB: Math.round(memUsage.heapUsed / 1024 / 1024),
        },
        timestamp: new Date(),
        responseTimeMs: responseTime,
      };
    } catch (error) {
      return {
        service: serviceName,
        status: HealthStatus.CRITICAL,
        message: 'System resource health check failed',
        details: { error: error instanceof Error ? error.message : String(error) },
        timestamp: new Date(),
        responseTimeMs: Date.now() - startTime,
      };
    }
  }

  /**
   * Start periodic health checks
   */
  private startHealthChecks(): void {
    // Clear any existing interval to prevent memory leaks
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
      this.healthCheckInterval = null;
    }

    // Track if check is in progress to prevent stacking
    let isCheckInProgress = false;

    this.healthCheckInterval = setInterval(async () => {
      // Skip if previous check is still running
      if (isCheckInProgress) {
        this.logger.warn('Health check skipped - previous check still running');

        return;
      }

      isCheckInProgress = true;
      try {
        const healthSummary = await this.getSystemHealth();

        // Log health status based on severity
        if (healthSummary.overallStatus === HealthStatus.CRITICAL) {
          this.logger.error({
            message: 'CRITICAL: Monitoring system health issues detected',
            overallStatus: healthSummary.overallStatus,
            criticalServices: healthSummary.criticalServices,
            warningServices: healthSummary.warningServices,
            totalServices: healthSummary.totalServices,
            criticalChecks: healthSummary.checks.filter(c => c.status === HealthStatus.CRITICAL),
          });
        } else if (healthSummary.overallStatus === HealthStatus.WARNING) {
          this.logger.warn({
            message: 'WARNING: Monitoring system health warnings',
            overallStatus: healthSummary.overallStatus,
            warningServices: healthSummary.warningServices,
            totalServices: healthSummary.totalServices,
            warningChecks: healthSummary.checks.filter(c => c.status === HealthStatus.WARNING),
          });
        } else {
          this.logger.debug({
            message: 'Monitoring system health check passed',
            overallStatus: healthSummary.overallStatus,
            healthyServices: healthSummary.healthyServices,
            totalServices: healthSummary.totalServices,
          });
        }
      } catch (error) {
        // Never let health check errors crash the application
        try {
          this.logger.error({
            message: 'CRITICAL: Health check system failure - monitoring may be compromised',
            error: error instanceof Error ? error.message : String(error),
            stack: error instanceof Error ? error.stack : undefined,
            timestamp: new Date().toISOString(),
          });
        } catch (logError) {
          // If even logging fails, use console as last resort
          console.error('CRITICAL: Health check and logging system failure:', {
            originalError: error,
            logError: logError,
            timestamp: new Date().toISOString(),
          });
        }
      } finally {
        isCheckInProgress = false;
      }
    }, this.healthCheckIntervalMs);
  }

  /**
   * Stop periodic health checks (with error handling)
   */
  private stopHealthChecks(): void {
    try {
      if (this.healthCheckInterval) {
        clearInterval(this.healthCheckInterval);
        this.healthCheckInterval = null;
        this.logger.log('Monitoring health checks stopped successfully');
      }
    } catch (error) {
      // If logging fails, use console as fallback
      try {
        this.logger.error({
          message: 'Error stopping health checks',
          error: error instanceof Error ? error.message : String(error),
        });
      } catch (logError) {
        console.error('Failed to stop health checks:', { error, logError });
      }
    }
  }
}

/**
 * Security utility for sanitizing error information to prevent information disclosure
 *
 * **Purpose**: Centralized error sanitization to prevent sensitive information
 * from being leaked through error messages, stack traces, and logs.
 *
 * **Security Considerations**:
 * - Remove file system paths that could reveal server structure
 * - Sanitize database connection strings and credentials
 * - Remove internal server details and configurations
 * - Prevent disclosure of user data in error messages
 * - Sanitize stack traces for production environments
 *
 * **Features**:
 * - Environment-aware sanitization (stricter in production)
 * - Configurable sanitization levels
 * - Safe fallbacks for unknown error types
 * - Performance-optimized for high-throughput logging
 */

export interface ISanitizedError {
  message: string;
  stack?: string;
  type?: string;
  code?: string;
  sanitized: boolean;
}

export enum SanitizationLevel {
  NONE = 'none', // No sanitization (development only)
  BASIC = 'basic', // Basic sanitization (staging)
  STRICT = 'strict', // Full sanitization (production)
}

export class ErrorSanitizationUtil {
  private static readonly SENSITIVE_PATTERNS = [
    // File system paths (more specific and performance-safe)
    /[A-Za-z]:\\(?:[\w-]+\\){2,}[\w.-]+/g, // Windows paths (3+ segments)
    /\/(?:[\w-]+\/){3,}[\w.-]+/g, // Unix paths (4+ segments to avoid false positives)
    /\/home\/[\w-]+\/[\w\/-]+/g, // Home directory paths
    /\/var\/[\w-]+\/[\w\/-]+/g, // System paths
    /\/usr\/[\w-]+\/[\w\/-]+/g, // User paths
    /\/opt\/[\w-]+\/[\w\/-]+/g, // Optional software paths
    /\/tmp\/[\w\/-]+/g, // Temp paths

    // Database and connection strings (more comprehensive)
    /(?:mongodb|postgresql|mysql|redis):\/\/[^@\s]+@[^\s\/]+/g, // Connection URIs
    /jdbc:[\w:\/]+/g, // JDBC URLs
    /Server=[\w.-]+;Database=[\w]+/gi, // SQL Server connection strings

    // Environment variables and secrets (stricter patterns)
    /[A-Z_]{4,}=[^\s]{8,}/g, // Long ENV vars (4+ chars = 8+ value)
    /(?:api[_-]?key|secret|token|password)['":\s=]*[^\s'",;]{10,}/gi, // Credentials (10+ chars)
    /Bearer\s+[a-zA-Z0-9+\/=]{20,}/g, // Bearer tokens
    /Basic\s+[a-zA-Z0-9+\/=]{10,}/g, // Basic auth tokens

    // Network information (more precise)
    /(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?):\d{1,5}/g, // Valid IP:PORT
    /localhost:\d{1,5}/g, // localhost:port
    /127\.0\.0\.1:\d{1,5}/g, // Loopback with port
    /0\.0\.0\.0:\d{1,5}/g, // All interfaces with port

    // User data patterns (more specific)
    /user[_-]?id['":\s=]*[\w\d-]{10,}/gi, // User IDs (10+ chars)
    /\b[\w.-]+@[\w.-]+\.[a-z]{2,}\b/gi, // Email addresses
    /\b\d{4}-\d{4}-\d{4}-\d{4}\b/g, // Credit card patterns
    /\b\d{3}-\d{2}-\d{4}\b/g, // SSN patterns

    // Internal server details
    /node_modules[\\\/][\w\\\/.-]{10,}/g, // Node modules paths (10+ chars)
    /at\s+[\w.]+\s+\([^)]{20,}\)/g, // Long stack trace locations
    /Error:\s+[^\n]{50,}/g, // Long error messages

    // System information
    /pid:\s*\d+/gi, // Process IDs
    /port:\s*\d+/gi, // Port numbers in logs
    /version:\s*[\d.]+/gi, // Version numbers
  ];

  private static readonly REPLACEMENT_TEXT = '[REDACTED]';
  private static readonly MAX_MESSAGE_LENGTH = 200;
  private static readonly MAX_STACK_LENGTH = 1000;

  /**
   * Sanitize error information based on environment and level
   */
  static sanitizeError(
    error: unknown,
    level: SanitizationLevel = SanitizationLevel.STRICT,
    _context?: string,
  ): ISanitizedError {
    if (level === SanitizationLevel.NONE) {
      // No sanitization - development only
      return this.convertErrorWithoutSanitization(error);
    }

    try {
      const baseError = this.convertToBaseError(error);

      return {
        message: this.sanitizeMessage(baseError.message, level),
        stack:
          level === SanitizationLevel.STRICT
            ? undefined
            : this.sanitizeStack(baseError.stack, level),
        type: this.sanitizeErrorType(baseError.type),
        code: this.sanitizeErrorCode(baseError.code),
        sanitized: true,
      };
    } catch {
      // Fallback if sanitization itself fails
      return {
        message: 'An error occurred (details sanitized)',
        type: 'UnknownError',
        sanitized: true,
      };
    }
  }

  /**
   * Sanitize just the error message (lightweight version with ReDoS protection)
   */
  static sanitizeMessage(
    message: string,
    level: SanitizationLevel = SanitizationLevel.STRICT,
  ): string {
    if (!message || typeof message !== 'string') {
      return 'Invalid error message';
    }

    let sanitized = message;

    // Security: Prevent ReDoS attacks - truncate BEFORE regex processing
    if (sanitized.length > this.MAX_MESSAGE_LENGTH) {
      sanitized = sanitized.substring(0, this.MAX_MESSAGE_LENGTH) + '...';
    }

    if (level === SanitizationLevel.BASIC || level === SanitizationLevel.STRICT) {
      // Security: Apply sanitization patterns with timeout protection
      try {
        // Use timeout wrapper to prevent ReDoS attacks
        sanitized = this.applySanitizationWithTimeout(sanitized, 50); // 50ms timeout
      } catch {
        // If sanitization times out (potential ReDoS), return generic message
        return 'Error occurred (details sanitized due to security)';
      }

      // Additional strict sanitization (with safe patterns)
      if (level === SanitizationLevel.STRICT) {
        // Use character-by-character validation instead of complex regex
        sanitized = this.sanitizeInternalDetails(sanitized);

        // Generic error messages for common cases (safe string operations)
        if (
          sanitized.toLowerCase().includes('enoent') ||
          sanitized.toLowerCase().includes('permission denied')
        ) {
          sanitized = 'File system access error';
        }
        if (
          sanitized.toLowerCase().includes('econnrefused') ||
          sanitized.toLowerCase().includes('timeout')
        ) {
          sanitized = 'External service unavailable';
        }
        if (
          sanitized.toLowerCase().includes('duplicate') &&
          sanitized.toLowerCase().includes('key')
        ) {
          sanitized = 'Data constraint violation';
        }
      }
    }

    // Final cleanup (safe operations only)
    sanitized = sanitized.replace(/\s+/g, ' ').trim();

    return sanitized || 'Error occurred';
  }

  /**
   * Apply sanitization patterns with ReDoS protection (character-by-character approach)
   */
  private static applySanitizationWithTimeout(text: string, maxTimeMs: number): string {
    const startTime = Date.now();

    // Security: Use character-by-character pattern matching instead of regex
    const sanitized = this.applySanitizationSafely(text);

    // Check overall timeout
    if (Date.now() - startTime > maxTimeMs) {
      throw new Error('Sanitization timeout - potential ReDoS attack');
    }

    return sanitized;
  }

  /**
   * Apply sanitization using safe string operations instead of regex
   */
  private static applySanitizationSafely(text: string): string {
    let result = text;

    // Security: Replace sensitive patterns using safe string operations
    result = this.sanitizeFilePaths(result);
    result = this.sanitizeConnectionStrings(result);
    result = this.sanitizeCredentials(result);
    result = this.sanitizeNetworkInfo(result);
    result = this.sanitizeUserData(result);
    result = this.sanitizeSystemInfo(result);

    return result;
  }

  /**
   * Sanitize file paths using safe string operations
   */
  private static sanitizeFilePaths(text: string): string {
    let result = text;

    // Windows paths (safe replacement)
    const windowsPathIndicators = ['C:\\', 'D:\\', 'E:\\', 'F:\\'];
    for (const indicator of windowsPathIndicators) {
      if (result.includes(indicator)) {
        const segments = result.split(indicator);
        for (let i = 1; i < segments.length; i++) {
          const pathEnd = segments[i].indexOf(' ');
          if (pathEnd > 10) {
            // Only replace long paths
            segments[i] = this.REPLACEMENT_TEXT + segments[i].substring(pathEnd);
          }
        }
        result = segments.join(indicator);
      }
    }

    // Unix paths (safe replacement)
    const unixPathIndicators = ['/home/', '/var/', '/usr/', '/opt/', '/tmp/'];
    for (const indicator of unixPathIndicators) {
      if (result.includes(indicator)) {
        const parts = result.split(indicator);
        for (let i = 1; i < parts.length; i++) {
          const spaceIndex = parts[i].indexOf(' ');
          const pathLength = spaceIndex === -1 ? parts[i].length : spaceIndex;
          if (pathLength > 10) {
            // Only replace long paths
            const replacement =
              spaceIndex === -1
                ? this.REPLACEMENT_TEXT
                : this.REPLACEMENT_TEXT + parts[i].substring(spaceIndex);
            parts[i] = replacement;
          }
        }
        result = parts.join(indicator);
      }
    }

    return result;
  }

  /**
   * Sanitize connection strings using safe string operations
   */
  private static sanitizeConnectionStrings(text: string): string {
    let result = text;

    const connectionPrefixes = ['mongodb://', 'postgresql://', 'mysql://', 'redis://', 'jdbc:'];
    for (const prefix of connectionPrefixes) {
      if (result.includes(prefix)) {
        const parts = result.split(prefix);
        for (let i = 1; i < parts.length; i++) {
          // Find the end of the connection string
          const spaceIndex = parts[i].indexOf(' ');
          const endIndex =
            spaceIndex === -1 ? Math.min(parts[i].length, 100) : Math.min(spaceIndex, 100);

          if (endIndex > 5) {
            const replacement =
              this.REPLACEMENT_TEXT + (spaceIndex === -1 ? '' : parts[i].substring(spaceIndex));
            parts[i] = replacement;
          }
        }
        result = parts.join(prefix);
      }
    }

    return result;
  }

  /**
   * Sanitize credentials using safe string operations
   */
  private static sanitizeCredentials(text: string): string {
    let result = text;

    const credentialTerms = [
      'api_key',
      'apikey',
      'secret',
      'token',
      'password',
      'Bearer ',
      'Basic ',
    ];

    for (const term of credentialTerms) {
      let searchIndex = 0;
      while (true) {
        const index = result.toLowerCase().indexOf(term.toLowerCase(), searchIndex);
        if (index === -1) break;

        // Find the credential value (after = or : or space)
        let valueStart = index + term.length;
        while (
          valueStart < result.length &&
          (result[valueStart] === '=' ||
            result[valueStart] === ':' ||
            result[valueStart] === ' ' ||
            result[valueStart] === '"' ||
            result[valueStart] === "'")
        ) {
          valueStart++;
        }

        if (valueStart < result.length) {
          // Find the end of the credential
          let valueEnd = valueStart;
          while (
            valueEnd < result.length &&
            result[valueEnd] !== ' ' &&
            result[valueEnd] !== '\n' &&
            result[valueEnd] !== '\r' &&
            result[valueEnd] !== '"' &&
            result[valueEnd] !== "'" &&
            result[valueEnd] !== ',' &&
            result[valueEnd] !== ';'
          ) {
            valueEnd++;
          }

          if (valueEnd - valueStart > 5) {
            // Only replace long values
            result =
              result.substring(0, valueStart) + this.REPLACEMENT_TEXT + result.substring(valueEnd);
          }
        }

        searchIndex = index + term.length;
      }
    }

    return result;
  }

  /**
   * Sanitize network information using safe string operations
   */
  private static sanitizeNetworkInfo(text: string): string {
    const result = text;

    // Simple IP:PORT pattern detection
    const words = result.split(' ');
    for (let i = 0; i < words.length; i++) {
      const word = words[i];
      if (this.looksLikeIpPort(word)) {
        words[i] = this.REPLACEMENT_TEXT;
      }
    }

    return words.join(' ');
  }

  /**
   * Check if a word looks like an IP:PORT combination
   */
  private static looksLikeIpPort(text: string): boolean {
    if (text.length < 9 || text.length > 21) return false; // Basic length check

    const colonIndex = text.lastIndexOf(':');
    if (colonIndex === -1) return false;

    const ip = text.substring(0, colonIndex);
    const port = text.substring(colonIndex + 1);

    // Check if port is numeric and in valid range
    const portNum = parseInt(port, 10);
    if (isNaN(portNum) || portNum < 1 || portNum > 65535) return false;

    // Check if IP looks valid (simple check)
    const ipParts = ip.split('.');
    if (ipParts.length !== 4) return false;

    for (const part of ipParts) {
      const num = parseInt(part, 10);
      if (isNaN(num) || num < 0 || num > 255) return false;
    }

    return true;
  }

  /**
   * Sanitize user data using safe string operations
   */
  private static sanitizeUserData(text: string): string {
    const result = text;

    // Email pattern detection (simple)
    const words = result.split(' ');
    for (let i = 0; i < words.length; i++) {
      const word = words[i];
      if (this.looksLikeEmail(word)) {
        const atIndex = word.indexOf('@');
        const localPart = word.substring(0, atIndex);
        const domainPart = word.substring(atIndex);
        words[i] = localPart.substring(0, 2) + '***' + domainPart;
      }
    }

    return words.join(' ');
  }

  /**
   * Check if text looks like an email address
   */
  private static looksLikeEmail(text: string): boolean {
    if (text.length < 5 || text.length > 254) return false;

    const atIndex = text.indexOf('@');
    if (atIndex <= 0 || atIndex >= text.length - 1) return false;

    const dotIndex = text.lastIndexOf('.');
    if (dotIndex <= atIndex || dotIndex >= text.length - 1) return false;

    return true;
  }

  /**
   * Sanitize system information using safe string operations
   */
  private static sanitizeSystemInfo(text: string): string {
    let result = text;

    const systemTerms = ['pid:', 'port:', 'version:', 'node_modules'];

    for (const term of systemTerms) {
      if (result.toLowerCase().includes(term.toLowerCase())) {
        let searchIndex = 0;
        while (true) {
          const index = result.toLowerCase().indexOf(term.toLowerCase(), searchIndex);
          if (index === -1) break;

          let endIndex = result.indexOf(' ', index);
          if (endIndex === -1) endIndex = result.length;

          result = result.substring(0, index) + this.REPLACEMENT_TEXT + result.substring(endIndex);
          searchIndex = index + this.REPLACEMENT_TEXT.length;
        }
      }
    }

    return result;
  }

  /**
   * Sanitize internal details using safe character-by-character approach
   */
  private static sanitizeInternalDetails(text: string): string {
    let result = '';
    let internalTokenBuffer = '';
    let numberBuffer = '';

    for (let i = 0; i < text.length; i++) {
      const char = text[i];

      // Track potential internal identifiers (starts with uppercase, has underscores/numbers)
      if (/[A-Z]/.test(char)) {
        if (internalTokenBuffer.length === 0) {
          internalTokenBuffer = char;
        } else {
          internalTokenBuffer += char;
        }
      } else if (internalTokenBuffer.length > 0 && /[a-zA-Z0-9_]/.test(char)) {
        internalTokenBuffer += char;
      } else {
        // End of potential internal token
        if (internalTokenBuffer.length > 10 && /[A-Z].*[_0-9]/.test(internalTokenBuffer)) {
          result += '[INTERNAL]';
        } else {
          result += internalTokenBuffer;
        }
        internalTokenBuffer = '';
        result += char;
      }

      // Track long numbers
      if (/\d/.test(char)) {
        numberBuffer += char;
      } else {
        if (numberBuffer.length >= 10) {
          result = result.slice(0, result.length - numberBuffer.length) + '[NUMBER]' + char;
        } else {
          result += char;
        }
        numberBuffer = '';
      }
    }

    // Handle buffers at end of string
    if (internalTokenBuffer.length > 10 && /[A-Z].*[_0-9]/.test(internalTokenBuffer)) {
      result += '[INTERNAL]';
    } else {
      result += internalTokenBuffer;
    }

    if (numberBuffer.length >= 10) {
      result = result.slice(0, result.length - numberBuffer.length) + '[NUMBER]';
    }

    return result;
  }

  /**
   * Sanitize stack trace
   */
  private static sanitizeStack(
    stack?: string,
    _level: SanitizationLevel = SanitizationLevel.BASIC,
  ): string | undefined {
    if (!stack || typeof stack !== 'string') {
      return undefined;
    }

    let sanitized = stack;

    // Truncate if too long
    if (sanitized.length > this.MAX_STACK_LENGTH) {
      sanitized = sanitized.substring(0, this.MAX_STACK_LENGTH) + '\n... [truncated]';
    }

    // Apply sanitization patterns
    for (const pattern of this.SENSITIVE_PATTERNS) {
      sanitized = sanitized.replace(pattern, this.REPLACEMENT_TEXT);
    }

    // Remove absolute paths from stack traces
    sanitized = sanitized.replace(
      /\s+at\s+.*[\\\/]node_modules[\\\/].*/g,
      '\n    at [node_modules]',
    );
    sanitized = sanitized.replace(/\s+at\s+.*[\\\/]src[\\\/].*/g, '\n    at [application]');

    return sanitized;
  }

  /**
   * Sanitize error type
   */
  private static sanitizeErrorType(type?: string): string | undefined {
    if (!type || typeof type !== 'string') {
      return undefined;
    }

    // Only allow known safe error types
    const safeTypes = [
      'Error',
      'TypeError',
      'ReferenceError',
      'SyntaxError',
      'RangeError',
      'ValidationError',
      'AuthenticationError',
      'AuthorizationError',
      'NotFoundError',
      'ConflictError',
      'TimeoutError',
    ];

    return safeTypes.includes(type) ? type : 'ApplicationError';
  }

  /**
   * Sanitize error code
   */
  private static sanitizeErrorCode(code?: string): string | undefined {
    if (!code || typeof code !== 'string') {
      return undefined;
    }

    // Only allow alphanumeric error codes
    if (/^[A-Z0-9_]{1,20}$/i.test(code)) {
      return code;
    }

    return undefined;
  }

  /**
   * Convert unknown error to base error structure
   */
  private static convertToBaseError(error: unknown): {
    message: string;
    stack?: string;
    type?: string;
    code?: string;
  } {
    if (error instanceof Error) {
      return {
        message: error.message || 'Unknown error',
        stack: error.stack,
        type: error.constructor.name,
        code: (error as any).code,
      };
    }

    if (typeof error === 'string') {
      return {
        message: error,
        type: 'StringError',
      };
    }

    if (typeof error === 'object' && error !== null) {
      const obj = error as any;

      return {
        message: obj.message || obj.toString?.() || 'Object error',
        stack: obj.stack,
        type: obj.constructor?.name || 'ObjectError',
        code: obj.code,
      };
    }

    return {
      message: String(error) || 'Unknown error occurred',
      type: 'UnknownError',
    };
  }

  /**
   * Convert error without sanitization (development only)
   */
  private static convertErrorWithoutSanitization(error: unknown): ISanitizedError {
    const baseError = this.convertToBaseError(error);

    return {
      message: baseError.message,
      stack: baseError.stack,
      type: baseError.type,
      code: baseError.code,
      sanitized: false,
    };
  }

  /**
   * Get appropriate sanitization level based on environment
   */
  static getSanitizationLevelFromEnv(nodeEnv?: string): SanitizationLevel {
    const env = (nodeEnv || process.env.NODE_ENV || 'production').toLowerCase();

    switch (env) {
      case 'development':
      case 'dev':
      case 'local':
        return SanitizationLevel.NONE;
      case 'test':
      case 'testing':
      case 'staging':
        return SanitizationLevel.BASIC;
      case 'production':
      case 'prod':
      default:
        return SanitizationLevel.STRICT;
    }
  }

  /**
   * Create sanitized error for logging (convenience method)
   */
  static forLogging(error: unknown, _context?: string): { message: string; stack?: string } {
    const level = this.getSanitizationLevelFromEnv();
    const sanitized = this.sanitizeError(error, level, _context);

    return {
      message: sanitized.message,
      stack: sanitized.stack,
    };
  }
}

/**
 * Secure Exception Handler Utility
 *
 * **Purpose**: Centralized exception handling that prevents information disclosure
 * while maintaining debugging capabilities for development environments.
 *
 * **Security Features**:
 * - Environment-aware exception sanitization
 * - Structured error responses without sensitive data
 * - Audit trail for security-relevant exceptions
 * - Prevention of stack trace leakage in production
 * - Standardized error codes and messages
 */

import { Logger } from '@nestjs/common';
import { ErrorSanitizationUtil, SanitizationLevel } from './error-sanitization.util';

export interface ISecureExceptionResponse {
  message: string;
  code: string;
  statusCode: number;
  timestamp: string;
  path?: string;
  correlationId?: string;
  details?: Record<string, any>;
}

export interface ISecureExceptionOptions {
  sanitizationLevel?: SanitizationLevel;
  includeStack?: boolean;
  logLevel?: 'error' | 'warn' | 'debug';
  auditRequired?: boolean;
  correlationId?: string;
  userId?: string;
  context?: string;
}

export enum SecureErrorCode {
  // Authentication & Authorization
  INVALID_CREDENTIALS = 'AUTH_001',
  ACCESS_DENIED = 'AUTH_002',
  TOKEN_EXPIRED = 'AUTH_003',
  SESSION_INVALID = 'AUTH_004',

  // Validation & Input
  INVALID_INPUT = 'INPUT_001',
  VALIDATION_FAILED = 'INPUT_002',
  MALFORMED_REQUEST = 'INPUT_003',

  // Business Logic
  BUSINESS_RULE_VIOLATION = 'BUSINESS_001',
  RESOURCE_NOT_FOUND = 'BUSINESS_002',
  OPERATION_NOT_ALLOWED = 'BUSINESS_003',
  QUOTA_EXCEEDED = 'BUSINESS_004',

  // System & Infrastructure
  SERVICE_UNAVAILABLE = 'SYSTEM_001',
  DATABASE_ERROR = 'SYSTEM_002',
  EXTERNAL_SERVICE_ERROR = 'SYSTEM_003',
  CONFIGURATION_ERROR = 'SYSTEM_004',

  // Security
  SECURITY_VIOLATION = 'SECURITY_001',
  RATE_LIMIT_EXCEEDED = 'SECURITY_002',
  SUSPICIOUS_ACTIVITY = 'SECURITY_003',
  INTEGRITY_CHECK_FAILED = 'SECURITY_004',

  // Generic
  INTERNAL_ERROR = 'INTERNAL_001',
  TIMEOUT = 'TIMEOUT_001',
  UNKNOWN_ERROR = 'UNKNOWN_001',
}

export class SecureExceptionHandler {
  private static readonly logger = new Logger('SecureExceptionHandler');

  // Security: Map of safe error messages by error code
  private static readonly SAFE_ERROR_MESSAGES = new Map<SecureErrorCode, string>([
    // Authentication & Authorization
    [SecureErrorCode.INVALID_CREDENTIALS, 'The provided credentials are invalid'],
    [SecureErrorCode.ACCESS_DENIED, 'Access to this resource is denied'],
    [SecureErrorCode.TOKEN_EXPIRED, 'Authentication token has expired'],
    [SecureErrorCode.SESSION_INVALID, 'Session is no longer valid'],

    // Validation & Input
    [SecureErrorCode.INVALID_INPUT, 'The provided input is invalid'],
    [SecureErrorCode.VALIDATION_FAILED, 'Input validation failed'],
    [SecureErrorCode.MALFORMED_REQUEST, 'The request format is incorrect'],

    // Business Logic
    [SecureErrorCode.BUSINESS_RULE_VIOLATION, 'Operation violates business rules'],
    [SecureErrorCode.RESOURCE_NOT_FOUND, 'The requested resource was not found'],
    [SecureErrorCode.OPERATION_NOT_ALLOWED, 'Operation is not allowed'],
    [SecureErrorCode.QUOTA_EXCEEDED, 'Usage quota has been exceeded'],

    // System & Infrastructure
    [SecureErrorCode.SERVICE_UNAVAILABLE, 'Service is temporarily unavailable'],
    [SecureErrorCode.DATABASE_ERROR, 'Data processing error occurred'],
    [SecureErrorCode.EXTERNAL_SERVICE_ERROR, 'External service error occurred'],
    [SecureErrorCode.CONFIGURATION_ERROR, 'System configuration error'],

    // Security
    [SecureErrorCode.SECURITY_VIOLATION, 'Security policy violation detected'],
    [SecureErrorCode.RATE_LIMIT_EXCEEDED, 'Too many requests - please try again later'],
    [SecureErrorCode.SUSPICIOUS_ACTIVITY, 'Suspicious activity detected'],
    [SecureErrorCode.INTEGRITY_CHECK_FAILED, 'Data integrity check failed'],

    // Generic
    [SecureErrorCode.INTERNAL_ERROR, 'An internal error occurred'],
    [SecureErrorCode.TIMEOUT, 'Operation timed out'],
    [SecureErrorCode.UNKNOWN_ERROR, 'An unknown error occurred'],
  ]);

  /**
   * Handle exception securely with environment-appropriate response
   */
  static handleException(
    error: unknown,
    options: ISecureExceptionOptions = {},
  ): ISecureExceptionResponse {
    const {
      sanitizationLevel = ErrorSanitizationUtil.getSanitizationLevelFromEnv(),
      includeStack: _includeStack = false,
      logLevel = 'error',
      auditRequired = false,
      correlationId = this.generateCorrelationId(),
      userId,
      context = 'unknown',
    } = options;

    try {
      // Extract error information safely
      const errorInfo = this.extractErrorInfo(error);

      // Determine appropriate error code and status
      const { code, statusCode } = this.categorizeError(errorInfo);

      // Get safe message for response
      const safeMessage =
        this.SAFE_ERROR_MESSAGES.get(code) ||
        this.SAFE_ERROR_MESSAGES.get(SecureErrorCode.INTERNAL_ERROR)!;

      // Sanitize error details based on environment
      const sanitizedDetails = this.sanitizeErrorDetails(errorInfo, sanitizationLevel);

      // Log error securely
      this.logError(errorInfo, {
        code,
        correlationId,
        userId,
        context,
        logLevel,
        sanitizationLevel,
      });

      // Audit security-relevant errors
      if (auditRequired || this.requiresAudit(code)) {
        this.auditSecurityError(errorInfo, { code, correlationId, userId, context });
      }

      // Build secure response
      const response: ISecureExceptionResponse = {
        message: safeMessage,
        code,
        statusCode,
        timestamp: new Date().toISOString(),
        correlationId,
      };

      // Include sanitized details in development
      if (sanitizationLevel === SanitizationLevel.NONE && sanitizedDetails) {
        response.details = sanitizedDetails;
      }

      return response;
    } catch (handlerError) {
      // Fallback if exception handling itself fails
      this.logger.error('Critical: Exception handler failed', {
        originalError: ErrorSanitizationUtil.forLogging(error, 'exception-handler-failure'),
        handlerError: ErrorSanitizationUtil.forLogging(handlerError, 'exception-handler-internal'),
        correlationId,
      });

      return {
        message: 'An error occurred while processing your request',
        code: SecureErrorCode.INTERNAL_ERROR,
        statusCode: 500,
        timestamp: new Date().toISOString(),
        correlationId,
      };
    }
  }

  /**
   * Extract error information safely from unknown error
   */
  private static extractErrorInfo(error: unknown): {
    message: string;
    stack?: string;
    name: string;
    statusCode?: number;
    code?: string;
    originalError: unknown;
  } {
    if (error instanceof Error) {
      return {
        message: error.message || 'Unknown error',
        stack: error.stack,
        name: error.constructor.name,
        statusCode: (error as any).statusCode || (error as any).status,
        code: (error as any).code,
        originalError: error,
      };
    }

    if (typeof error === 'string') {
      return {
        message: error,
        name: 'StringError',
        originalError: error,
      };
    }

    if (typeof error === 'object' && error !== null) {
      const obj = error as any;

      return {
        message: obj.message || obj.toString?.() || 'Object error',
        stack: obj.stack,
        name: obj.constructor?.name || 'ObjectError',
        statusCode: obj.statusCode || obj.status,
        code: obj.code,
        originalError: error,
      };
    }

    return {
      message: 'Unknown error occurred',
      name: 'UnknownError',
      originalError: error,
    };
  }

  /**
   * Categorize error to appropriate secure error code and HTTP status
   */
  private static categorizeError(errorInfo: any): { code: SecureErrorCode; statusCode: number } {
    const { message, name, statusCode, code } = errorInfo;
    const lowerMessage = message.toLowerCase();
    const lowerName = name.toLowerCase();

    // Security-related errors
    if (
      lowerMessage.includes('unauthorized') ||
      lowerMessage.includes('forbidden') ||
      lowerMessage.includes('access denied') ||
      statusCode === 401 ||
      statusCode === 403
    ) {
      return { code: SecureErrorCode.ACCESS_DENIED, statusCode: 403 };
    }

    if (lowerMessage.includes('invalid credentials') || lowerMessage.includes('authentication')) {
      return { code: SecureErrorCode.INVALID_CREDENTIALS, statusCode: 401 };
    }

    if (
      lowerMessage.includes('token') &&
      (lowerMessage.includes('expired') || lowerMessage.includes('invalid'))
    ) {
      return { code: SecureErrorCode.TOKEN_EXPIRED, statusCode: 401 };
    }

    // Rate limiting
    if (
      lowerMessage.includes('rate limit') ||
      lowerMessage.includes('too many requests') ||
      statusCode === 429
    ) {
      return { code: SecureErrorCode.RATE_LIMIT_EXCEEDED, statusCode: 429 };
    }

    // Validation errors
    if (
      lowerMessage.includes('validation') ||
      lowerMessage.includes('invalid input') ||
      lowerName.includes('validation') ||
      statusCode === 400
    ) {
      return { code: SecureErrorCode.VALIDATION_FAILED, statusCode: 400 };
    }

    // Not found
    if (
      lowerMessage.includes('not found') ||
      lowerMessage.includes('does not exist') ||
      statusCode === 404
    ) {
      return { code: SecureErrorCode.RESOURCE_NOT_FOUND, statusCode: 404 };
    }

    // Timeout
    if (
      lowerMessage.includes('timeout') ||
      lowerMessage.includes('timed out') ||
      statusCode === 408
    ) {
      return { code: SecureErrorCode.TIMEOUT, statusCode: 408 };
    }

    // Service unavailable
    if (
      lowerMessage.includes('unavailable') ||
      lowerMessage.includes('service') ||
      statusCode === 503
    ) {
      return { code: SecureErrorCode.SERVICE_UNAVAILABLE, statusCode: 503 };
    }

    // Database errors
    if (
      lowerMessage.includes('database') ||
      lowerMessage.includes('connection') ||
      lowerMessage.includes('query') ||
      code === 'ECONNREFUSED'
    ) {
      return { code: SecureErrorCode.DATABASE_ERROR, statusCode: 500 };
    }

    // Configuration errors
    if (
      lowerMessage.includes('configuration') ||
      lowerMessage.includes('config') ||
      lowerMessage.includes('environment variable')
    ) {
      return { code: SecureErrorCode.CONFIGURATION_ERROR, statusCode: 500 };
    }

    // Default to internal error
    return { code: SecureErrorCode.INTERNAL_ERROR, statusCode: statusCode || 500 };
  }

  /**
   * Sanitize error details based on environment
   */
  private static sanitizeErrorDetails(
    errorInfo: any,
    level: SanitizationLevel,
  ): Record<string, any> | null {
    if (level === SanitizationLevel.STRICT) {
      return null; // No details in production
    }

    const details: Record<string, any> = {};

    if (level === SanitizationLevel.BASIC) {
      // Limited details for staging
      details.type = errorInfo.name;
      if (errorInfo.code && typeof errorInfo.code === 'string' && errorInfo.code.length < 20) {
        details.code = errorInfo.code;
      }
    } else if (level === SanitizationLevel.NONE) {
      // Full details for development
      details.type = errorInfo.name;
      details.originalMessage = ErrorSanitizationUtil.sanitizeMessage(errorInfo.message);
      if (errorInfo.stack) {
        details.stack = ErrorSanitizationUtil.sanitizeMessage(errorInfo.stack);
      }
      if (errorInfo.code) {
        details.code = errorInfo.code;
      }
    }

    return Object.keys(details).length > 0 ? details : null;
  }

  /**
   * Log error securely with appropriate level
   */
  private static logError(
    errorInfo: any,
    options: {
      code: SecureErrorCode;
      correlationId: string;
      userId?: string;
      context: string;
      logLevel: 'error' | 'warn' | 'debug';
      sanitizationLevel: SanitizationLevel;
    },
  ): void {
    const { code, correlationId, userId, context, logLevel, sanitizationLevel } = options;

    const logData = {
      correlationId,
      errorCode: code,
      context,
      userId,
      message: ErrorSanitizationUtil.sanitizeMessage(errorInfo.message, sanitizationLevel),
      errorType: errorInfo.name,
      timestamp: new Date().toISOString(),
    };

    // Include stack in development
    if (sanitizationLevel === SanitizationLevel.NONE && errorInfo.stack) {
      logData['stack'] = ErrorSanitizationUtil.sanitizeMessage(errorInfo.stack, sanitizationLevel);
    }

    switch (logLevel) {
      case 'error':
        this.logger.error(logData);
        break;
      case 'warn':
        this.logger.warn(logData);
        break;
      case 'debug':
        this.logger.debug(logData);
        break;
    }
  }

  /**
   * Audit security-relevant errors
   */
  private static auditSecurityError(
    errorInfo: any,
    options: {
      code: SecureErrorCode;
      correlationId: string;
      userId?: string;
      context: string;
    },
  ): void {
    // This would integrate with the audit log system
    // For now, just ensure it's logged as a security event
    this.logger.warn({
      event: 'SECURITY_ERROR',
      correlationId: options.correlationId,
      errorCode: options.code,
      context: options.context,
      userId: options.userId,
      sanitizedMessage: ErrorSanitizationUtil.sanitizeMessage(errorInfo.message),
      timestamp: new Date().toISOString(),
    });
  }

  /**
   * Check if error requires security audit
   */
  private static requiresAudit(code: SecureErrorCode): boolean {
    const auditRequiredCodes = [
      SecureErrorCode.SECURITY_VIOLATION,
      SecureErrorCode.SUSPICIOUS_ACTIVITY,
      SecureErrorCode.RATE_LIMIT_EXCEEDED,
      SecureErrorCode.INTEGRITY_CHECK_FAILED,
      SecureErrorCode.ACCESS_DENIED,
    ];

    return auditRequiredCodes.includes(code);
  }

  /**
   * Generate correlation ID for error tracking
   */
  private static generateCorrelationId(): string {
    return `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Create standardized HTTP exception with secure error handling
   */
  static createHttpException(
    error: unknown,
    defaultStatusCode: number = 500,
    options: ISecureExceptionOptions = {},
  ): any {
    const secureResponse = this.handleException(error, {
      ...options,
      auditRequired: true,
    });

    // Return object that can be used with NestJS HttpException
    return {
      message: secureResponse.message,
      statusCode: secureResponse.statusCode || defaultStatusCode,
      error: secureResponse.code,
      timestamp: secureResponse.timestamp,
      correlationId: secureResponse.correlationId,
      ...(secureResponse.details && { details: secureResponse.details }),
    };
  }
}

import { Injectable, Inject, OnModuleInit } from '@nestjs/common';
import { randomUUID } from 'crypto';
import { ConfigService } from '@nestjs/config';
import { JwtService } from '@nestjs/jwt';
import { ILogger } from '@core/interfaces/logger.interface';
import { IBotTokenProvider } from '@core/interfaces/bot-token-provider.interface';
import { IBotTokenRepository } from '@core/repositories/bot-token.repository.interface';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { RolesEnum } from '@shared/constants/enums';
import { BOT_SPECIAL_PERMISSIONS } from '@shared/constants/bot-permissions';
import { BotTokenCacheService } from '@infrastructure/cache/bot-token-cache.service';
import { BotToken } from '@core/entities/bot-token.entity';
import { UserId } from '@core/value-objects/user-id.vo';
import { CompanyId } from '@core/value-objects/company-id.vo';
import { BotTokenMapper } from '@application/mappers/bot-token.mapper';

/**
 * Infrastructure implementation of BOT Token Provider
 * Optimized for HIGH CONCURRENCY and performance
 *
 * Key optimizations:
 * - In-memory cache for O(1) token validation
 * - No database calls during validation
 * - Minimal logging in hot paths
 * - Pre-loaded blacklist on startup
 */
@Injectable()
export class BotTokenProvider implements IBotTokenProvider, OnModuleInit {
  constructor(
    private readonly jwtService: JwtService,
    private readonly configService: ConfigService,
    @Inject(LOGGER_SERVICE)
    private readonly logger: ILogger,
    private readonly tokenCache: BotTokenCacheService,
    private readonly botTokenRepository: IBotTokenRepository,
  ) {
    this.logger.setContext(BotTokenProvider.name);
  }

  async onModuleInit() {
    // Load revoked and active tokens from database on startup
    // This prevents database calls during runtime validation
    try {
      const [revokedTokenIds, activeTokens] = await Promise.all([
        this.botTokenRepository.getRevokedTokenIds(),
        this.botTokenRepository.getActiveTokensForCache(),
      ]);

      await this.tokenCache.loadFromDatabase(revokedTokenIds, activeTokens);

      this.logger.debug({
        message: 'BOT token cache initialized from database',
        revokedCount: revokedTokenIds.length,
        activeCount: activeTokens.length,
      });
    } catch (error) {
      this.logger.error('Failed to initialize BOT token cache from database', error);
      // Initialize with empty cache as fallback
      await this.tokenCache.loadFromDatabase([], []);
    }
  }

  /**
   * Generate a BOT token (technical implementation)
   * Business validation must be done before calling this method
   */
  async generateToken(params: {
    botUserId: string;
    botEmail: string;
    tokenId: string;
    companyId?: string;
    issuedBy: string;
  }): Promise<{
    accessToken: string;
    expiresIn: string;
    tokenId: string;
  }> {
    const { botUserId, botEmail, tokenId, companyId, issuedBy } = params;

    // Generar session token (jti) para el BOT
    const sessionTokenId = randomUUID();

    // Create domain entity for persistence
    const botTokenEntity = BotToken.create(
      tokenId,
      sessionTokenId,
      UserId.fromString(botUserId),
      botEmail,
      UserId.fromString(issuedBy),
      companyId ? CompanyId.fromString(companyId) : undefined,
    );

    // Save to database
    await this.botTokenRepository.save(botTokenEntity);

    // Construir payload del token BOT con permisos especiales OCULTOS
    const botPayload = {
      sub: botUserId,
      email: botEmail,
      emailVerified: true,
      isActive: true,
      roles: [RolesEnum.BOT, RolesEnum.ROOT], // BOT + ROOT roles
      permissions: [BOT_SPECIAL_PERMISSIONS.ALL_ACCESS], // Permiso oculto y no asignable
      tenantId: companyId || null,
      companyId: companyId || null,
      tokenType: 'bot',
      tokenId,
      jti: sessionTokenId, // Session token para validación de sesión activa
      iat: Math.floor(Date.now() / 1000),
    };

    // Generar token SIN expiración (infinito)
    const accessToken = this.jwtService.sign(botPayload, {
      secret: this.configService.get('jwt.secret'),
      algorithm: this.configService.get('JWT_ALGORITHM', 'HS512'),
      // Sin expiresIn = token infinito
    });

    // Add to active tokens cache (security check included)
    const cacheAdded = this.tokenCache.addActiveToken(tokenId, botUserId, companyId);
    if (!cacheAdded) {
      // This should never happen with proper UUID generation,
      // but provides security against token ID collision with revoked tokens
      this.logger.error({
        message: 'Failed to add BOT token to cache - token may be revoked',
        tokenId,
      });
    }

    // Minimal logging - avoid in hot path if possible
    if (this.configService.get('logging.verbose', false)) {
      this.logger.debug({
        message: 'BOT token generated',
        tokenId,
        companyId,
        expiresIn: 'never',
      });
    }

    return {
      accessToken,
      expiresIn: 'never', // Token sin expiración
      tokenId,
    };
  }

  /**
   * Validate a BOT token - OPTIMIZED for high concurrency
   * This is the HOT PATH - called on every BOT request
   */
  async validateToken(token: string): Promise<any | null> {
    try {
      // First decode without verification (fast)
      const decoded = this.jwtService.decode(token) as any;

      // Quick checks before expensive verification
      if (!decoded || decoded.tokenType !== 'bot' || !decoded.tokenId) {
        return null;
      }

      // Check if token is revoked (O(1) in-memory lookup)
      if (this.tokenCache.isTokenRevoked(decoded.tokenId)) {
        return null; // Token is blacklisted
      }

      // Now do the expensive verification
      const verified = this.jwtService.verify(token, {
        secret: this.configService.get('jwt.secret'),
        ignoreExpiration: true, // BOT tokens don't expire
      });

      // Final validation
      if (
        !verified.roles?.includes(RolesEnum.BOT) ||
        !verified.permissions?.includes(BOT_SPECIAL_PERMISSIONS.ALL_ACCESS)
      ) {
        return null;
      }

      // NO LOGGING in hot path - this kills performance at scale
      // If needed, use sampling (e.g., log 1 in 1000 requests)

      return verified;
    } catch (error) {
      // Only log errors, not successful validations
      if (this.configService.get('logging.logTokenErrors', false)) {
        this.logger.error({
          message: 'BOT token validation failed',
          error: error.message,
        });
      }

      return null;
    }
  }

  /**
   * Revoke a BOT token (technical implementation)
   */
  async revokeToken(tokenId: string, revokedBy?: string): Promise<boolean> {
    try {
      // Find the token first
      const botToken = await this.botTokenRepository.findByTokenId(tokenId);
      if (!botToken || !botToken.isValid()) {
        return false; // Token not found or already revoked
      }

      // Revoke in database
      const revokedByUserId = revokedBy ? UserId.fromString(revokedBy) : botToken.issuedBy;
      await this.botTokenRepository.revoke(tokenId, revokedByUserId, new Date());

      // Update cache immediately for high-performance validation
      this.tokenCache.forceRevokeToken(tokenId);

      this.logger.debug({
        message: 'BOT token revoked successfully',
        tokenId,
        revokedBy: revokedByUserId.getValue(),
      });

      return true;
    } catch (error) {
      this.logger.error({
        message: 'Failed to revoke BOT token',
        tokenId,
        error: error.message,
      });

      return false;
    }
  }

  /**
   * List active BOT tokens (technical implementation)
   */
  async listActiveTokens(): Promise<
    Array<{
      tokenId: string;
      botUserId: string;
      companyId?: string;
      createdAt: Date;
    }>
  > {
    try {
      // Get from database for accurate data
      const activeTokens = await this.botTokenRepository.findAllActive();

      return activeTokens.map(token => BotTokenMapper.toCacheFormat(token));
    } catch (error) {
      this.logger.error({
        message: 'Failed to list active BOT tokens',
        error: error.message,
      });

      return [];
    }
  }

  /**
   * Refresh cache from database
   * Called by ROOT users to update cache manually
   */
  async refreshTokenCache(): Promise<{
    previousCounts: { revoked: number; active: number };
    newCounts: { revoked: number; active: number };
    refreshedAt: Date;
  }> {
    return this.tokenCache.refreshCacheFromDatabase(async () => {
      const [revokedTokenIds, activeTokens] = await Promise.all([
        this.botTokenRepository.getRevokedTokenIds(),
        this.botTokenRepository.getActiveTokensForCache(),
      ]);

      return { revokedTokenIds, activeTokens };
    });
  }

  /**
   * Get cache stats for monitoring
   */
  getCacheStats() {
    return this.tokenCache.getCacheStats();
  }

  /**
   * Manual cleanup of old revoked tokens from database and cache
   * Only ROOT users should be able to call this
   * Keeps active tokens indefinitely - only cleans old revoked tokens
   */
  async cleanupOldRevokedTokens(): Promise<{
    deletedFromDb: number;
    removedFromCache: number;
    remainingRevoked: number;
    remainingActive: number;
  }> {
    return this.tokenCache.manualCleanup(async (olderThan: Date) => {
      // Delete old revoked tokens from database
      const deletedFromDb = await this.botTokenRepository.deleteOldRevokedTokens(olderThan);

      // Get remaining revoked tokens that should be kept in cache
      const revokedTokensToKeep = await this.botTokenRepository.getRevokedTokenIds();

      return {
        deletedFromDb,
        revokedTokensToKeep,
      };
    });
  }
}

import { Injectable, Inject } from '@nestjs/common';
import { PrismaService } from '../database/prisma/prisma.service';
import { TransactionContextService } from '../database/prisma/transaction-context.service';
import {
  IAuditLogRepository,
  IAuditLogQuery,
  IAuditLogQueryResult,
  IAuditLogFilters,
} from '@core/repositories/audit-log.repository.interface';
import { AuditLog, AuditLogLevel, AuditLogType } from '@core/entities/audit-log.entity';
import { AuditLogId } from '@core/value-objects/audit-log-id.vo';
import { UserId } from '@core/value-objects/user-id.vo';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { ILogger } from '@core/interfaces/logger.interface';

/**
 * Audit Log Repository Implementation using Prisma
 * Infrastructure layer - implements domain interface
 */
@Injectable()
export class AuditLogRepository implements IAuditLogRepository {
  constructor(
    private readonly prisma: PrismaService,
    private readonly transactionContext: TransactionContextService,
    @Inject(LOGGER_SERVICE) private readonly logger: ILogger,
  ) {
    this.logger.setContext(AuditLogRepository.name);
  }

  async save(auditLog: AuditLog, useTransaction = false): Promise<AuditLog> {
    // IMPORTANT: Audit logs should be saved OUTSIDE of transactions by default
    // to ensure they are persisted even if the main operation fails
    // Only use transaction context if explicitly requested
    const client = useTransaction
      ? this.transactionContext.getTransactionClient() || this.prisma
      : this.prisma;

    try {
      const data = await client.auditLog.create({
        data: {
          id: auditLog.id.getValue(),
          level: auditLog.level,
          type: auditLog.type,
          action: auditLog.action,
          message: auditLog.message,
          userId: auditLog.userId?.getValue() || null,
          metadata: auditLog.metadata as any,
          timestamp: auditLog.timestamp,
          context: auditLog.context,
        },
      });

      return this.toDomain(data);
    } catch (error) {
      this.logger.error({
        message: 'Failed to save audit log',
        auditLogId: auditLog.id.getValue(),
        error: error instanceof Error ? error.message : String(error),
        useTransaction,
      });
      throw error;
    }
  }

  async findById(id: AuditLogId): Promise<AuditLog | null> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const data = await client.auditLog.findUnique({
      where: { id: id.getValue() },
    });

    return data ? this.toDomain(data) : null;
  }

  async query(query: IAuditLogQuery): Promise<IAuditLogQueryResult> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const { filters = {}, page = 1, limit = 50, sortBy = 'timestamp', sortOrder = 'desc' } = query;

    // Build where clause
    const where = this.buildWhereClause(filters);

    // Build order by clause
    const orderBy = this.buildOrderByClause(sortBy, sortOrder);

    // Calculate pagination
    const skip = (page - 1) * limit;

    // Execute queries in parallel
    const [logs, total] = await Promise.all([
      client.auditLog.findMany({
        where,
        orderBy,
        skip,
        take: limit,
      }),
      client.auditLog.count({ where }),
    ]);

    const totalPages = Math.ceil(total / limit);

    return {
      logs: logs.map(log => this.toDomain(log)),
      total,
      page,
      limit,
      totalPages,
    };
  }

  async findByUserId(userId: UserId, limit: number = 100): Promise<AuditLog[]> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const logs = await client.auditLog.findMany({
      where: { userId: userId.getValue() },
      orderBy: { timestamp: 'desc' },
      take: limit,
    });

    return logs.map(log => this.toDomain(log));
  }

  async findByContext(context: string, limit: number = 100): Promise<AuditLog[]> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const logs = await client.auditLog.findMany({
      where: { context },
      orderBy: { timestamp: 'desc' },
      take: limit,
    });

    return logs.map(log => this.toDomain(log));
  }

  async findByTypeAndLevel(
    type: AuditLogType,
    level: AuditLogLevel,
    limit: number = 100,
  ): Promise<AuditLog[]> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const logs = await client.auditLog.findMany({
      where: { type, level },
      orderBy: { timestamp: 'desc' },
      take: limit,
    });

    return logs.map(log => this.toDomain(log));
  }

  async findRecentErrors(hours: number, limit: number = 100): Promise<AuditLog[]> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;
    const fromDate = new Date();
    fromDate.setHours(fromDate.getHours() - hours);

    const logs = await client.auditLog.findMany({
      where: {
        level: 'error',
        timestamp: {
          gte: fromDate,
        },
      },
      orderBy: { timestamp: 'desc' },
      take: limit,
    });

    return logs.map(log => this.toDomain(log));
  }

  async findSecurityLogs(fromDate: Date, toDate: Date, limit: number = 1000): Promise<AuditLog[]> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const logs = await client.auditLog.findMany({
      where: {
        OR: [{ type: 'security' }, { type: 'auth' }, { context: 'security' }],
        timestamp: {
          gte: fromDate,
          lte: toDate,
        },
      },
      orderBy: { timestamp: 'desc' },
      take: limit,
    });

    return logs.map(log => this.toDomain(log));
  }

  async count(filters: IAuditLogFilters = {}): Promise<number> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;
    const where = this.buildWhereClause(filters);

    return client.auditLog.count({ where });
  }

  async deleteOlderThan(date: Date): Promise<number> {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const result = await client.auditLog.deleteMany({
      where: {
        timestamp: {
          lt: date,
        },
      },
    });

    return result.count;
  }

  async getStatistics(fromDate: Date, toDate: Date) {
    const client = this.transactionContext.getTransactionClient() || this.prisma;

    const totalLogs = await client.auditLog.count({
      where: {
        timestamp: {
          gte: fromDate,
          lte: toDate,
        },
      },
    });

    // Get statistics by level
    const levelStats = await client.auditLog.groupBy({
      by: ['level'],
      where: {
        timestamp: {
          gte: fromDate,
          lte: toDate,
        },
      },
      _count: true,
    });

    // Get statistics by type
    const typeStats = await client.auditLog.groupBy({
      by: ['type'],
      where: {
        timestamp: {
          gte: fromDate,
          lte: toDate,
        },
      },
      _count: true,
    });

    // Get statistics by context
    const contextStats = await client.auditLog.groupBy({
      by: ['context'],
      where: {
        timestamp: {
          gte: fromDate,
          lte: toDate,
        },
      },
      _count: true,
    });

    return {
      totalLogs,
      byLevel: levelStats.reduce(
        (acc, stat) => {
          acc[stat.level as AuditLogLevel] = stat._count;

          return acc;
        },
        {} as Record<AuditLogLevel, number>,
      ),
      byType: typeStats.reduce(
        (acc, stat) => {
          acc[stat.type as AuditLogType] = stat._count;

          return acc;
        },
        {} as Record<AuditLogType, number>,
      ),
      byContext: contextStats.reduce(
        (acc, stat) => {
          acc[stat.context] = stat._count;

          return acc;
        },
        {} as Record<string, number>,
      ),
    };
  }

  /**
   * Build WHERE clause from filters
   */
  private buildWhereClause(filters: IAuditLogFilters): any {
    const where: any = {};

    if (filters.level && filters.level.length > 0) {
      where.level = { in: filters.level };
    }

    if (filters.type && filters.type.length > 0) {
      where.type = { in: filters.type };
    }

    if (filters.userId) {
      where.userId = filters.userId;
    }

    if (filters.context && filters.context.length > 0) {
      where.context = { in: filters.context };
    }

    if (filters.fromDate || filters.toDate) {
      where.timestamp = {};
      if (filters.fromDate) {
        where.timestamp.gte = filters.fromDate;
      }
      if (filters.toDate) {
        where.timestamp.lte = filters.toDate;
      }
    }

    if (filters.ipAddress) {
      where.metadata = {
        path: ['ipAddress'],
        equals: filters.ipAddress,
      };
    }

    if (filters.search) {
      where.OR = [
        { message: { contains: filters.search, mode: 'insensitive' } },
        { context: { contains: filters.search, mode: 'insensitive' } },
        { type: { contains: filters.search, mode: 'insensitive' } },
        { action: { contains: filters.search, mode: 'insensitive' } },
      ];
    }

    return where;
  }

  /**
   * Build ORDER BY clause
   */
  private buildOrderByClause(sortBy: string, sortOrder: 'asc' | 'desc'): any {
    const validSortFields = ['timestamp', 'level', 'type', 'context'];
    const field = validSortFields.includes(sortBy) ? sortBy : 'timestamp';

    return { [field]: sortOrder };
  }

  /**
   * Convert Prisma model to domain entity
   */
  private toDomain(data: any): AuditLog {
    return AuditLog.create(
      data.level,
      data.type,
      data.action,
      data.message,
      data.userId ? UserId.fromString(data.userId) : null,
      data.metadata || {},
      data.context,
    );
  }
}

import { Injectable } from '@nestjs/common';
import { User } from '@core/entities/user.entity';
import { IUserRepository } from '@core/repositories/user.repository.interface';
import { PrismaService } from '@infrastructure/database/prisma/prisma.service';
import { TransactionContextService } from '@infrastructure/database/prisma/transaction-context.service';
import { Role } from '@core/entities/role.entity';
import { Permission } from '@core/entities/permission.entity';
import {
  User as PrismaUser,
  UserRole as PrismaUserRole,
  RolePermission as PrismaRolePermission,
  Role as PrismaRole,
  Permission as PrismaPermission,
  UserProfile as PrismaUserProfile,
  UserAddress as PrismaUserAddress,
} from '@prisma/client';
import { ResourceAction } from '@core/value-objects/resource-action.vo';
import { ActionType } from '@shared/constants/enums';
import { BaseRepository } from './base.repository';
import { BusinessConfigurationService } from '@core/services/business-configuration.service';

// Define a type for User with its relations (roles with nested permissions)
type UserWithRelations = PrismaUser & {
  roles: (PrismaUserRole & {
    role: PrismaRole & {
      permissions: (PrismaRolePermission & {
        permission: PrismaPermission;
      })[];
    };
  })[];
  profile?: PrismaUserProfile | null;
  address?: PrismaUserAddress | null;
};

@Injectable()
export class UserRepository extends BaseRepository<User> implements IUserRepository {
  constructor(
    private readonly prisma: PrismaService,
    private readonly transactionContext: TransactionContextService,
    private readonly businessConfigService: BusinessConfigurationService,
  ) {
    super();
  }

  private get client() {
    return this.transactionContext.getTransactionClient() || this.prisma;
  }

  async findAllByCompanyId(companyId: string): Promise<User[]> {
    return this.executeWithErrorHandling('findAll', async () => {
      const userRecords = await this.client.user.findMany({
        where: {
          companyId: companyId,
        },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      return userRecords.map(record => this.mapToModel(record as UserWithRelations));
    });
  }

  async findById(id: string): Promise<User | null> {
    return this.executeWithErrorHandling('findById', async () => {
      const userRecord = await this.client.user.findUnique({
        where: { id },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      if (!userRecord) {
        return null;
      }

      return this.mapToModel(userRecord as UserWithRelations);
    });
  }

  async findByEmail(email: string): Promise<User | null> {
    return this.executeWithErrorHandling('findByEmail', async () => {
      const userRecord = await this.client.user.findUnique({
        where: { email },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      if (!userRecord) {
        return null;
      }

      return this.mapToModel(userRecord as UserWithRelations);
    });
  }

  async findByAlias(alias: string): Promise<User | null> {
    return this.executeWithErrorHandling('findByAlias', async () => {
      const userRecord = await this.client.user.findUnique({
        where: { alias },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      if (!userRecord) {
        return null;
      }

      return this.mapToModel(userRecord as UserWithRelations);
    });
  }

  async findByAgentPhoneAndCompany(agentPhone: string, companyId: string): Promise<User | null> {
    return this.executeWithErrorHandling('findByAgentPhoneAndCompany', async () => {
      const userRecord = await this.client.user.findFirst({
        where: {
          agentPhone,
          companyId,
        },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      if (!userRecord) {
        return null;
      }

      return this.mapToModel(userRecord as UserWithRelations);
    });
  }

  async findAll(): Promise<User[]> {
    return this.executeWithErrorHandling('findAll', async () => {
      const userRecords = await this.client.user.findMany({
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      return userRecords.map(record => this.mapToModel(record as UserWithRelations));
    });
  }

  async findUsersByRoleId(roleId: string): Promise<User[]> {
    return this.executeWithErrorHandling('findUsersByRoleId', async () => {
      const userRecords = await this.client.user.findMany({
        where: {
          roles: {
            some: {
              roleId,
            },
          },
        },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      return userRecords.map(record => this.mapToModel(record as UserWithRelations));
    });
  }

  async getUserPhoneCountryCode(userId: string): Promise<string | null> {
    return this.executeWithErrorHandling('getUserPhoneCountryCode', async () => {
      const userWithProfile = await this.client.user.findUnique({
        where: { id: userId },
        include: {
          profile: true,
        },
      });

      if (!userWithProfile?.profile?.phoneCountryCode) {
        return null;
      }

      // Return phone country code directly (without + sign for SMS API)
      return userWithProfile.profile.phoneCountryCode.replace('+', '');
    });
  }

  async create(user: User): Promise<User> {
    return this.executeWithErrorHandling('create', async () => {
      const createdUser = await this.client.user.create({
        data: {
          id: user.id.getValue(),
          email: user.email.getValue(),
          passwordHash: user.passwordHash,
          firstName: user.firstName.getValue(),
          lastName: user.lastName.getValue(),
          secondLastName: user.secondLastName?.getValue(),
          alias: user.alias,
          isActive: user.isActive,
          emailVerified: user.emailVerified,
          otpEnabled: user.otpEnabled,
          otpSecret: user.otpSecret,
          lastLoginAt: user.lastLoginAt,
          bannedUntil: user.bannedUntil,
          banReason: user.banReason,
          agentPhone: user.agentPhone?.getValue(),
          agentPhoneCountryCode: user.agentPhone?.getCountryCode(),
          companyId: user.companyId?.getValue() || null,
          roles: {
            create: user.roles.map(role => ({
              role: {
                connect: { id: role.id.getValue() },
              },
            })),
          },
          profile: user.profile
            ? {
                create: {
                  phone: user.profile.phone,
                  avatarUrl: user.profile.avatarUrl,
                  bio: user.profile.bio,
                  birthdate: user.profile.birthDate,
                },
              }
            : undefined,
          address: user.address
            ? {
                create: {
                  city: user.address.city,
                  street: user.address.street,
                  exteriorNumber: user.address.exteriorNumber,
                  interiorNumber: user.address.interiorNumber,
                  postalCode: user.address.postalCode,
                },
              }
            : undefined,
        },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      return this.mapToModel(createdUser as UserWithRelations);
    });
  }

  async update(user: User): Promise<User> {
    return this.executeWithErrorHandling('update', async () => {
      // First, delete all role associations to recreate them
      await this.client.userRole.deleteMany({
        where: {
          userId: user.id.getValue(),
        },
      });

      // Update the user with all fields including missing ones
      await this.client.user.update({
        where: { id: user.id.getValue() },
        data: {
          email: user.email.getValue(),
          passwordHash: user.passwordHash,
          firstName: user.firstName.getValue(),
          lastName: user.lastName.getValue(),
          secondLastName: user.secondLastName?.getValue() || null,
          alias: user.alias || null,
          isActive: user.isActive,
          emailVerified: user.emailVerified,
          otpEnabled: user.otpEnabled,
          otpSecret: user.otpSecret,
          lastLoginAt: user.lastLoginAt,
          companyId: user.companyId?.getValue() || null,
          bannedUntil: user.bannedUntil || null,
          banReason: user.banReason || null,
          agentPhone: user.agentPhone?.getValue() || null,
          agentPhoneCountryCode: user.agentPhone?.getCountryCode() || null,
          roles: {
            create: user.roles.map(role => ({
              role: {
                connect: { id: role.id.getValue() },
              },
            })),
          },
        },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      // Handle profile update/upsert
      if (user.profile) {
        await this.client.userProfile.upsert({
          where: { userId: user.id.getValue() },
          update: {
            phone: user.profile.phone || null,
            phoneCountryCode: user.profile.phoneCountryCode || null,
            avatarUrl: user.profile.avatarUrl || null,
            bio: user.profile.bio || null,
            birthdate: user.profile.birthDate || null,
          },
          create: {
            userId: user.id.getValue(),
            phone: user.profile.phone || null,
            phoneCountryCode: user.profile.phoneCountryCode || null,
            avatarUrl: user.profile.avatarUrl || null,
            bio: user.profile.bio || null,
            birthdate: user.profile.birthDate || null,
          },
        });
      }

      // Handle address update/upsert
      if (user.address) {
        // First find country and state IDs
        const countryRecord = user.address.country
          ? await this.client.country.findFirst({
              where: { name: user.address.country },
            })
          : null;

        const stateRecord =
          user.address.state && countryRecord
            ? await this.client.state.findFirst({
                where: {
                  name: user.address.state,
                  countryId: countryRecord.id,
                },
              })
            : null;

        await this.client.userAddress.upsert({
          where: { userId: user.id.getValue() },
          update: {
            countryId: countryRecord?.id || null,
            stateId: stateRecord?.id || null,
            city: user.address.city || null,
            street: user.address.street || null,
            exteriorNumber: user.address.exteriorNumber || null,
            interiorNumber: user.address.interiorNumber || null,
            postalCode: user.address.postalCode || null,
          },
          create: {
            userId: user.id.getValue(),
            countryId: countryRecord?.id || null,
            stateId: stateRecord?.id || null,
            city: user.address.city || null,
            street: user.address.street || null,
            exteriorNumber: user.address.exteriorNumber || null,
            interiorNumber: user.address.interiorNumber || null,
            postalCode: user.address.postalCode || null,
          },
        });
      }

      // Fetch the complete updated user with all relations
      const finalUser = await this.client.user.findUnique({
        where: { id: user.id.getValue() },
        include: {
          roles: {
            include: {
              role: {
                include: {
                  permissions: {
                    include: {
                      permission: true,
                    },
                  },
                },
              },
            },
          },
          profile: true,
          address: true,
        },
      });

      return this.mapToModel(finalUser as UserWithRelations);
    });
  }

  async delete(id: string): Promise<boolean> {
    return this.executeWithErrorHandling(
      'delete',
      async () => {
        await this.client.user.delete({
          where: { id },
        });

        return true;
      },
      false,
    );
  }

  private mapToModel(record: UserWithRelations): User {
    // Map roles first
    const roles = record.roles.map(roleRelation => {
      const roleRecord = roleRelation.role;

      // Map permissions
      const permissions =
        roleRecord.permissions?.map(permissionRelation => {
          const permissionRecord = permissionRelation.permission;

          // Create the ResourceAction value object
          const resourceAction = new ResourceAction(
            permissionRecord.resource,
            permissionRecord.action as ActionType,
          );

          return Permission.fromData({
            id: permissionRecord.id,
            resourceAction,
            description: permissionRecord.description,
            createdAt: permissionRecord.createdAt,
            updatedAt: permissionRecord.updatedAt,
          });
        }) || [];

      return Role.fromData({
        id: roleRecord.id,
        name: roleRecord.name,
        description: roleRecord.description,
        hierarchyLevel: roleRecord.hierarchyLevel,
        isDefault: roleRecord.isDefault,
        isDefaultAppRole: roleRecord.isDefaultAppRole,
        permissions,
        createdAt: roleRecord.createdAt,
        updatedAt: roleRecord.updatedAt,
      });
    });

    const user = User.fromData({
      id: record.id,
      email: record.email,
      passwordHash: record.passwordHash,
      firstName: record.firstName,
      lastName: record.lastName,
      secondLastName: record.secondLastName || undefined,
      alias: record.alias || undefined,
      isActive: record.isActive,
      emailVerified: record.emailVerified,
      otpEnabled: record.otpEnabled,
      otpSecret: record.otpSecret || undefined,
      roles,
      lastLoginAt: record.lastLoginAt || undefined,
      bannedUntil: record.bannedUntil || undefined,
      banReason: record.banReason || undefined,
      agentPhone: record.agentPhone || undefined,
      agentPhoneCountryCode: record.agentPhoneCountryCode || undefined,
      profile: record.profile
        ? {
            phone: record.profile.phone || undefined,
            phoneCountryCode: record.profile.phoneCountryCode || undefined,
            avatarUrl: record.profile.avatarUrl || undefined,
            bio: record.profile.bio || undefined,
            birthDate: record.profile.birthdate || undefined,
          }
        : undefined,
      address:
        record.address && this.hasValidAddressData(record.address)
          ? {
              country: this.businessConfigService.getAddressDefaults().defaultCountry || 'Unknown',
              state: this.businessConfigService.getAddressDefaults().defaultState || 'Unknown',
              city: record.address.city || '',
              street: record.address.street || '',
              exteriorNumber: record.address.exteriorNumber || '',
              interiorNumber: record.address.interiorNumber || undefined,
              postalCode: record.address.postalCode || '',
            }
          : undefined,
      createdAt: record.createdAt,
      updatedAt: record.updatedAt,
      companyId: record.companyId || undefined,
    });

    return user;
  }

  /**
   * Check if address data is valid for creating Address value object
   */
  private hasValidAddressData(address: any): boolean {
    // Check if the address has the minimum required fields
    const defaults = this.businessConfigService.getAddressDefaults();
    const hasCountry = defaults.defaultCountry && defaults.defaultCountry.trim() !== '';
    const hasState = defaults.defaultState && defaults.defaultState.trim() !== '';
    const hasCity = address.city && address.city.trim() !== '';
    const hasStreet = address.street && address.street.trim() !== '';
    const hasExteriorNumber = address.exteriorNumber && address.exteriorNumber.trim() !== '';
    const hasPostalCode = address.postalCode && address.postalCode.trim() !== '';

    // Return true only if we have all required fields
    return hasCountry && hasState && hasCity && hasStreet && hasExteriorNumber && hasPostalCode;
  }
}

import { NestFactory } from '@nestjs/core';
import { AppModule } from './app.module';
import { ValidationPipe } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { AllExceptionsFilter } from '@presentation/filters/all-exceptions.filter';
import { DocumentBuilder, SwaggerModule } from '@nestjs/swagger';
import * as basicAuth from 'express-basic-auth';
import helmet from 'helmet';
import { LoggerService } from '@infrastructure/logger/logger.service';
import { useContainer } from 'class-validator';

async function bootstrap() {
  // =========================================================================
  // MEMORY CONFIGURATION (must be set before app creation)
  // =========================================================================
  const maxOldSpaceSize = process.env.NODE_MAX_OLD_SPACE_SIZE;
  const maxSemiSpaceSize = process.env.NODE_MAX_SEMI_SPACE_SIZE;

  if (maxOldSpaceSize) {
    console.warn(`Memory limit configured: ${maxOldSpaceSize}MB max heap`);
  }
  if (maxSemiSpaceSize) {
    console.warn(`Semi space configured: ${maxSemiSpaceSize}MB`);
  }

  // =========================================================================
  // APPLICATION SETUP
  // =========================================================================
  const app = await NestFactory.create(AppModule);
  const configService = app.get(ConfigService);
  const logger = await app.resolve(LoggerService);

  logger.setContext('Application');

  // Enable class-validator to use NestJS dependency injection
  useContainer(app.select(AppModule), { fallbackOnErrors: true });

  // =========================================================================
  // SECURITY MIDDLEWARE
  // =========================================================================
  app.use(
    helmet({
      contentSecurityPolicy: {
        directives: {
          defaultSrc: ["'self'"],
          styleSrc: ["'self'", "'unsafe-inline'"],
          scriptSrc: ["'self'", "'unsafe-inline'"], // Added unsafe-inline for custom JS
          imgSrc: ["'self'", 'data:', 'https:'],
        },
      },
    }),
  );

  // =========================================================================
  // CUSTOM MIDDLEWARE
  // =========================================================================
  // Handle ngrok and preflight requests
  app.use((req, res, next) => {
    // Handle ngrok browser warning
    if (req.headers['ngrok-skip-browser-warning']) {
      res.setHeader('ngrok-skip-browser-warning', 'true');
    }

