      .swagger-ui button,
      .swagger-ui .opblock-summary-control,
      .swagger-ui .authorization__btn {
        user-select: none !important;
        cursor: pointer !important;
      }
    `,

    // =========================================================================
    // CUSTOM JAVASCRIPT FOR STATUS CODE COLORS
    // =========================================================================
    customJs: `
      // Enhanced status code color application
      function applyStatusCodeColors() {
        try {
          // Find all status code elements with multiple selectors
          const statusElements = document.querySelectorAll(
            '.swagger-ui .response-col_status, ' +
            '.swagger-ui td.response-col_status, ' +
            '.swagger-ui .live-responses-table .response-col_status, ' +
            '.swagger-ui .responses-table .response-col_status'
          );

          statusElements.forEach(element => {
            const text = (element.textContent || element.innerText || '').trim();

            // Remove any existing status code classes
            element.classList.remove('status-200', 'status-error');

            // Apply base styling
            element.style.setProperty('font-weight', '700', 'important');
            element.style.setProperty('border-radius', '4px', 'important');
            element.style.setProperty('padding', '4px 8px', 'important');
            element.style.setProperty('font-size', '12px', 'important');
            element.style.setProperty('display', 'inline-block', 'important');
            element.style.setProperty('min-width', '50px', 'important');
            element.style.setProperty('text-align', 'center', 'important');

            // Default: RED background for all status codes
            element.style.setProperty('background-color', '#ef4444', 'important');
            element.style.setProperty('color', '#ffffff', 'important');

            // Special case: GREEN only for 200 status codes
            if (text === '200' || text === '200 OK' || text.match(/^200\\s/)) {
              element.style.setProperty('background-color', '#10b981', 'important');
              element.style.setProperty('color', '#ffffff', 'important');
              element.classList.add('status-200');
            } else {
              element.classList.add('status-error');
            }
          });
        } catch (error) {
          console.warn('Status code color application failed:', error);
        }
      }

      // Apply colors immediately
      applyStatusCodeColors();

      // Apply when DOM is ready
      if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', applyStatusCodeColors);
      } else {
        // DOM is already ready, apply immediately
        setTimeout(applyStatusCodeColors, 100);
      }

      // Create a more aggressive observer for dynamic content
      const observer = new MutationObserver(function(mutations) {
        let shouldApply = false;

        mutations.forEach(function(mutation) {
          // Check for added nodes
          if (mutation.addedNodes.length > 0) {
            shouldApply = true;
          }

          // Check for text changes
          if (mutation.type === 'characterData') {
            shouldApply = true;
          }

          // Check for attribute changes on response elements
          if (mutation.type === 'attributes' && 
              mutation.target.classList && 
              mutation.target.classList.contains('response-col_status')) {
            shouldApply = true;
          }
        });

        if (shouldApply) {
          setTimeout(applyStatusCodeColors, 10);
        }
      });

      // Start observing with comprehensive options
      observer.observe(document.body, {
        childList: true,
        subtree: true,
        characterData: true,
        attributes: true,
        attributeFilter: ['class', 'style']
      });

      // Additional aggressive intervals to catch any missed updates
      setInterval(applyStatusCodeColors, 250);

      // Apply on window focus (in case of missed updates)
      window.addEventListener('focus', applyStatusCodeColors);

      // Apply on click events (when user interacts with Swagger)
      document.addEventListener('click', function() {
        setTimeout(applyStatusCodeColors, 100);
      });
    `,
  });

  // =========================================================================
  // SERVER STARTUP
  // =========================================================================
  const port = configService.get<number>('port', 3000);
  const nodeEnv = configService.get<string>('env', 'development');

  await app.listen(port);
  const appUrl = await app.getUrl();

  // Log startup information
  logger.log({
    message: 'Application started',
    port,
    environment: nodeEnv,
    url: appUrl,
  });

  logger.log({
    message: 'Swagger documentation available',
    url: `${appUrl}/docs`,
  });
}

// =========================================================================
// GRACEFUL SHUTDOWN HANDLERS
// =========================================================================
let app: any = null;

// Handle graceful shutdown on SIGTERM (Kubernetes, Docker, PM2)
process.on('SIGTERM', async () => {
  console.warn('SIGTERM signal received: closing HTTP server gracefully');

  if (app) {
    try {
      // Give ongoing requests 10 seconds to complete
      const shutdownTimeout = setTimeout(() => {
        console.error('Forced shutdown after timeout');
        process.exit(1);
      }, 10000);

      // Close NestJS application
      await app.close();
      clearTimeout(shutdownTimeout);

      console.warn('HTTP server closed successfully');
      process.exit(0);
    } catch (error) {
      console.error('Error during graceful shutdown:', error);
      process.exit(1);
    }
  } else {
    process.exit(0);
  }
});

// Handle graceful shutdown on SIGINT (Ctrl+C)
process.on('SIGINT', async () => {
  console.warn('\nSIGINT signal received: closing HTTP server gracefully');

  if (app) {
    try {
      await app.close();
      console.warn('HTTP server closed successfully');
      process.exit(0);
    } catch (error) {
      console.error('Error during graceful shutdown:', error);
      process.exit(1);
    }
  } else {
    process.exit(0);
  }
});

// Handle uncaught exceptions
process.on('uncaughtException', error => {
  console.error('Uncaught Exception:', error);
  // Log the error but keep the process running
  // In production, you might want to restart the process
});

// Handle unhandled promise rejections
process.on('unhandledRejection', (reason, promise) => {
  console.error('Unhandled Rejection at:', promise, 'reason:', reason);
  // Log the error but keep the process running
});

// =========================================================================
// APPLICATION BOOTSTRAP
// =========================================================================
bootstrap()
  .then(nestApp => {
    app = nestApp; // Store app reference for graceful shutdown
  })
  .catch(err => {
    console.error('Error starting application:', err);
    process.exit(1);
  });

import { Injectable, NestInterceptor, ExecutionContext, CallHandler, Inject } from '@nestjs/common';
import { Observable, throwError } from 'rxjs';
import { tap, catchError } from 'rxjs/operators';
import { Request, Response } from 'express';
import { AuditLogService } from '@core/services/audit-log.service';
import { UserId } from '@core/value-objects/user-id.vo';
import { AUDIT_LOG_SERVICE } from '@shared/constants/tokens';

/**
 * Interceptor de Audit Log - Captura automática de requests/responses HTTP
 *
 * **Propósito**: Interceptor de capa de presentación que captura automáticamente
 * TODOS los requests HTTP para crear un audit trail comprehensivo sin requerir
 * modificaciones en controllers individuales.
 *
 * **Responsabilidades**:
 * - Captura automática de requests HTTP entrantes
 * - Logging de responses exitosos con métricas de performance
 * - Captura de errores y excepciones con contexto completo
 * - Extracción de información de usuario y sesión
 * - Filtering inteligente de endpoints no-críticos
 * - Categorización automática por status codes
 *
 * **Arquitectura NestJS Interceptor**:
 * - Implementa NestInterceptor interface
 * - Usa RxJS operators para manejo asíncrono
 * - Se ejecuta antes y después de route handlers
 * - Acceso completo a ExecutionContext y response data
 * - Error handling que no interrumpe flujo principal
 *
 * **Características de performance**:
 * - **Ultra-fast logging**: Usa queue asíncrono, no bloquea requests
 * - **Smart filtering**: Skip automático de health checks y assets
 * - **Minimal overhead**: <1ms latencia adicional por request
 * - **Non-blocking**: Failures de audit no afectan response
 * - **Memory efficient**: No almacena payloads grandes
 *
 * **Información capturada automáticamente**:
 * - **Request**: method, URL, headers, body, params, query
 * - **Response**: status code, duration, response size
 * - **User context**: user ID, session token, IP address
 * - **Errors**: exception details, stack traces, error types
 * - **Performance**: request duration, response size
 *
 * **Políticas implementadas**:
 * - Skip de endpoints no-críticos (health, metrics, static assets)
 * - Categorización automática de log level por status code
 * - Doble logging para errores (API + Exception)
 * - Extracción inteligente de IP (considera proxies)
 * - Sanitización automática via AuditLogService
 *
 * @example
 * ```typescript
 * // Se aplica automáticamente a todos los endpoints:
 * @Controller('users')
 * @UseInterceptors(AuditLogInterceptor)  // O globalmente en main.ts
 * export class UsersController {
 *   @Get()
 *   findAll() {
 *     // Automáticamente logged como:
 *     // "GET /api/users" with user context, duration, etc.
 *   }
 *
 *   @Post()
 *   create(@Body() data: any) {
 *     // Automáticamente logged con request body (sanitizado)
 *   }
 * }
 * ```
 *
 * **Integración con sistema**:
 * - Registrado globalmente en main.ts o por controller
 * - Integrado con sistema de autenticación para user context
 * - Usa AuditLogService para persistencia asíncrona
 * - Compatible con guards, filters y otros interceptors
 */
@Injectable()
export class AuditLogInterceptor implements NestInterceptor {
  constructor(
    @Inject(AUDIT_LOG_SERVICE)
    private readonly auditLogService: AuditLogService,
  ) {}

  intercept(context: ExecutionContext, next: CallHandler): Observable<any> {
    const startTime = Date.now();
    const ctx = context.switchToHttp();
    const request = ctx.getRequest<Request>();
    const response = ctx.getResponse<Response>();

    // Extract request information
    const { method, url } = request;

    // Extract user information if available
    const user = (request as any).user;
    const userId = user?.id ? UserId.fromString(user.id) : null;
    const sessionToken = (request as any).sessionToken;

    // Skip audit logging for health checks and non-essential endpoints
    if (this.shouldSkipAudit(url, method)) {
      return next.handle();
    }

    return next.handle().pipe(
      tap(data => {
        const duration = Date.now() - startTime;
        const statusCode = response.statusCode;

        // Log successful requests
        this.logApiRequest(
          'access',
          `${method} ${url}`,
          userId,
          request,
          {
            statusCode,
            duration,
            responseSize: data ? JSON.stringify(data).length : 0,
          },
          this.getLogLevelForStatus(statusCode),
        );
      }),
      catchError(error => {
        const duration = Date.now() - startTime;
        const statusCode = error.status || response.statusCode || 500;

        // Log failed requests and exceptions
        this.logApiRequest(
          'error',
          `${method} ${url} - ERROR: ${error.message || 'Unknown error'}`,
          userId,
          request,
          {
            statusCode,
            duration,
            errorMessage: error.message,
            errorStack: error.stack,
            errorName: error.constructor.name,
          },
          'error',
        );

        // Also log the exception separately for detailed tracking (ultra-fast)
        this.auditLogService.logException(error, userId, request, {
          endpoint: `${method} ${url}`,
          duration,
          statusCode,
          sessionToken,
        });

        return throwError(() => error);
      }),
    );
  }

  /**
   * Log API request with audit service (ultra-fast, non-blocking)
   */
  private logApiRequest(
    action: 'access' | 'error',
    message: string,
    userId: UserId | null,
    request: Request,
    additionalMetadata: any,
    level: 'info' | 'warn' | 'error',
  ): void {
    // Direct queue insertion - ultra fast, no promises, no async
    this.auditLogService.logApi(
      action,
      message,
      userId,
      request,
      additionalMetadata,
      undefined, // duration will be in additionalMetadata
      level,
    );
  }

  /**
   * Extract IP address from request
   */
  private extractIpAddress(request: Request): string {
    return (
      (request.headers['x-forwarded-for'] as string)?.split(',')[0]?.trim() ||
      (request.headers['x-real-ip'] as string) ||
      request.connection?.remoteAddress ||
      request.socket?.remoteAddress ||
      (request as any).ip ||
      'unknown'
    );
  }

  /**
   * Determine if audit logging should be skipped for this request
   */
  private shouldSkipAudit(url: string, method: string): boolean {
    const skipPatterns = [
      '/health',
      '/metrics',
      '/favicon.ico',
      '/robots.txt',
      '/.well-known',
      '/api/health',
      '/api/metrics',
    ];

    // Skip GET requests to static assets and health checks
    if (method === 'GET' && skipPatterns.some(pattern => url.includes(pattern))) {
      return true;
    }

    // Skip swagger documentation requests
    if (url.includes('/docs') || url.includes('/api-json')) {
      return true;
    }

    return false;
  }

  /**
   * Get appropriate log level based on HTTP status code
   */
  private getLogLevelForStatus(statusCode: number): 'info' | 'warn' | 'error' {
    if (statusCode >= 500) {
      return 'error';
    } else if (statusCode >= 400) {
      return 'warn';
    } else {
      return 'info';
    }
  }
}

import { Injectable, NestInterceptor, ExecutionContext, CallHandler, Inject } from '@nestjs/common';
import { Observable } from 'rxjs';
import { tap, catchError } from 'rxjs/operators';
import { Request, Response } from 'express';
import { ILogger } from '@core/interfaces/logger.interface';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { BOT_SPECIAL_PERMISSIONS } from '@shared/constants/bot-permissions';
import { IJwtPayload } from '@application/dtos/responses/user.response';
import { AuditLogService } from '@core/services/audit-log.service';

/**
 * Interceptor de Auditoría para BOT
 *
 * Registra TODOS los accesos del BOT en:
 * - Logs de archivos (para debugging inmediato)
 * - Base de datos (para consultas y reportes)
 *
 * Incluye:
 * - Método HTTP y endpoint
 * - Headers relevantes
 * - Body/payload de requests
 * - Respuestas (tamaño y status)
 * - Timing de operaciones
 * - IP y User-Agent
 * - Parámetros de query y ruta
 */
@Injectable()
export class BotAuditInterceptor implements NestInterceptor {
  constructor(
    @Inject(LOGGER_SERVICE)
    private readonly logger: ILogger,
    private readonly auditLogService: AuditLogService,
  ) {
    this.logger.setContext(BotAuditInterceptor.name);
  }

  intercept(context: ExecutionContext, next: CallHandler): Observable<any> {
    const request = context.switchToHttp().getRequest<Request>();
    const response = context.switchToHttp().getResponse<Response>();
    const user = request.user as IJwtPayload;

    // Solo auditar si es un token BOT
    const isBotToken = user?.permissions?.includes(BOT_SPECIAL_PERMISSIONS.ALL_ACCESS);

    if (!isBotToken) {
      return next.handle();
    }

    const startTime = Date.now();
    const requestId = this.generateRequestId();

    // Log de inicio de request BOT
    this.logBotRequestStart(request, user, requestId, startTime);

    return next.handle().pipe(
      tap(async responseData => {
        // Log de request exitoso
        await this.logBotRequestSuccess(
          request,
          response,
          responseData,
          user,
          requestId,
          startTime,
        );
      }),
      catchError(async error => {
        // Log de request con error
        await this.logBotRequestError(request, response, error, user, requestId, startTime);
        throw error;
      }),
    );
  }

  private async logBotRequestStart(
    request: Request,
    user: IJwtPayload,
    requestId: string,
    startTime: number,
  ): Promise<void> {
    const auditData = {
      event: 'BOT_REQUEST_START',
      requestId,
      timestamp: new Date(startTime).toISOString(),

      // Información del BOT
      botInfo: {
        userId: user.sub,
        email: user.email,
        tokenId: user.jti || 'no-jti',
        companyId: user.companyId,
        tenantId: user.tenantId,
      },

      // Información de la request
      request: {
        method: request.method,
        url: request.url,
        path: request.path,
        originalUrl: request.originalUrl,

        // Parámetros
        query: this.sanitizeObject(request.query),
        params: this.sanitizeObject(request.params),

        // Headers relevantes (sin Authorization)
        headers: this.sanitizeHeaders(request.headers),

        // Body/payload (sanitizado)
        body: this.sanitizeRequestBody(request.body, request.method),

        // Información de red
        ip: this.getClientIP(request),
        userAgent: request.headers['user-agent'] || 'unknown',

        // Tamaño de contenido
        contentLength: request.headers['content-length'] || '0',
        contentType: request.headers['content-type'] || 'unknown',
      },

      // Contexto técnico
      technical: {
        nodeEnv: process.env.NODE_ENV,
        requestStartTime: startTime,
      },
    };

    // Log en archivos
    this.logger.warn(auditData);

    // Guardar en base de datos
    try {
      await this.auditLogService.logBotActivity({
        userId: user.sub,
        companyId: user.companyId || null,
        action: 'BOT_REQUEST_START',
        resource: `${request.method} ${request.path}`,
        details: {
          requestId,
          method: request.method,
          path: request.path,
          query: this.sanitizeObject(request.query),
          params: this.sanitizeObject(request.params),
          body: this.sanitizeRequestBody(request.body, request.method),
          ip: this.getClientIP(request),
          userAgent: request.headers['user-agent'] || 'unknown',
          tokenId: user.jti,
          timestamp: new Date(startTime).toISOString(),
        },
        ipAddress: this.getClientIP(request),
        userAgent: request.headers['user-agent'] || 'unknown',
      });
    } catch (error) {
      this.logger.error({
        message: 'Failed to save BOT audit log to database',
        error: error.message,
        requestId,
      });
    }
  }

  private async logBotRequestSuccess(
    request: Request,
    response: Response,
    responseData: any,
    user: IJwtPayload,
    requestId: string,
    startTime: number,
  ): Promise<void> {
    const endTime = Date.now();
    const duration = endTime - startTime;

    const auditData = {
      event: 'BOT_REQUEST_SUCCESS',
      requestId,
      timestamp: new Date(endTime).toISOString(),

      // Información del BOT
      botInfo: {
        userId: user.sub,
        email: user.email,
        tokenId: user.jti || 'no-jti',
      },

      // Información de request/response
      request: {
        method: request.method,
        path: request.path,
        query: this.sanitizeObject(request.query),
        params: this.sanitizeObject(request.params),
      },

      response: {
        statusCode: response.statusCode,
        statusMessage: response.statusMessage,

        // Tamaño de respuesta
        contentLength: response.getHeader('content-length') || 'unknown',

        // Headers de respuesta relevantes
        headers: this.sanitizeResponseHeaders(response.getHeaders()),

        // Datos de respuesta (limitados por tamaño)
        data: this.sanitizeResponseData(responseData),
      },

      // Métricas de rendimiento
      performance: {
        duration: `${duration}ms`,
        startTime,
        endTime,
      },
    };

    // Log en archivos
    this.logger.warn(auditData);

    // Guardar en base de datos
    try {
      await this.auditLogService.logBotActivity({
        userId: user.sub,
        companyId: user.companyId || null,
        action: 'BOT_REQUEST_SUCCESS',
        resource: `${request.method} ${request.path}`,
        details: {
          requestId,
          method: request.method,
          path: request.path,
          statusCode: response.statusCode,
          duration: `${duration}ms`,
          responseSize: response.getHeader('content-length') || 'unknown',
          query: this.sanitizeObject(request.query),
          params: this.sanitizeObject(request.params),
          tokenId: user.jti,
          timestamp: new Date(endTime).toISOString(),
        },
        ipAddress: this.getClientIP(request),
        userAgent: request.headers['user-agent'] || 'unknown',
      });
    } catch (error) {
      this.logger.error({
        message: 'Failed to save BOT success audit log to database',
        error: error.message,
        requestId,
      });
    }
  }

  private async logBotRequestError(
    request: Request,
    response: Response,
    error: any,
    user: IJwtPayload,
    requestId: string,
    startTime: number,
  ): Promise<void> {
    const endTime = Date.now();
    const duration = endTime - startTime;

    const auditData = {
      event: 'BOT_REQUEST_ERROR',
      requestId,
      timestamp: new Date(endTime).toISOString(),

      // Información del BOT
      botInfo: {
        userId: user.sub,
        email: user.email,
        tokenId: user.jti || 'no-jti',
      },

      // Información de request
      request: {
        method: request.method,
        path: request.path,
        query: this.sanitizeObject(request.query),
        params: this.sanitizeObject(request.params),
        body: this.sanitizeRequestBody(request.body, request.method),
      },

      // Información del error
      error: {
        name: error.name || 'UnknownError',
        message: error.message || 'Unknown error occurred',
        statusCode: error.status || response.statusCode || 500,
        code: error.code || 'UNKNOWN_ERROR',

        // Stack trace solo en desarrollo
        stack: process.env.NODE_ENV === 'development' ? error.stack : undefined,
      },

      // Métricas
      performance: {
        duration: `${duration}ms`,
        startTime,
        endTime,
      },
    };

    // Log en archivos
    this.logger.error(auditData);

    // Guardar en base de datos
    try {
      await this.auditLogService.logBotActivity({
        userId: user.sub,
        companyId: user.companyId || null,
        action: 'BOT_REQUEST_ERROR',
        resource: `${request.method} ${request.path}`,
        details: {
          requestId,
          method: request.method,
          path: request.path,
          error: {
            name: error.name || 'UnknownError',
            message: error.message || 'Unknown error occurred',
            statusCode: error.status || response.statusCode || 500,
            code: error.code || 'UNKNOWN_ERROR',
          },
          duration: `${duration}ms`,
          query: this.sanitizeObject(request.query),
          params: this.sanitizeObject(request.params),
          body: this.sanitizeRequestBody(request.body, request.method),
          tokenId: user.jti,
          timestamp: new Date(endTime).toISOString(),
        },
        ipAddress: this.getClientIP(request),
        userAgent: request.headers['user-agent'] || 'unknown',
      });
    } catch (dbError) {
      this.logger.error({
        message: 'Failed to save BOT error audit log to database',
        error: dbError.message,
        requestId,
        originalError: error.message,
      });
    }
  }

  private sanitizeRequestBody(body: any, method: string): any {
    if (!body || method === 'GET' || method === 'DELETE') {
      return undefined;
    }

    // Limitar tamaño del body en logs
    const bodyStr = JSON.stringify(body);
    if (bodyStr.length > 2000) {
      return {
        _truncated: true,
        _originalSize: bodyStr.length,
        _preview: bodyStr.substring(0, 500) + '...[TRUNCATED]',
      };
    }

    // Sanitizar campos sensibles
    return this.sanitizeObject(body);
  }

  private sanitizeResponseData(data: any): any {
    if (!data) {
      return undefined;
    }

    // Limitar tamaño de respuesta en logs
    const dataStr = JSON.stringify(data);
    if (dataStr.length > 1000) {
      return {
        _truncated: true,
        _originalSize: dataStr.length,
        _type: Array.isArray(data) ? 'array' : typeof data,
        _itemCount: Array.isArray(data) ? data.length : undefined,
      };
    }

    return this.sanitizeObject(data);
  }

  private sanitizeHeaders(headers: any): Record<string, string> {
    const sanitized: Record<string, string> = {};

    // Headers relevantes para auditoría
    const relevantHeaders = [
      'content-type',
      'content-length',
      'user-agent',
      'accept',
      'x-forwarded-for',
      'x-real-ip',
      'origin',
      'referer',
      'x-request-id',
      'x-correlation-id',
    ];

    relevantHeaders.forEach(header => {
      if (headers[header]) {
        sanitized[header] = String(headers[header]);
      }
    });

    // Nunca incluir Authorization header
    return sanitized;
  }

  private sanitizeResponseHeaders(headers: any): Record<string, any> {
    const sanitized: Record<string, any> = {};

    const relevantHeaders = [
      'content-type',
      'content-length',
      'x-ratelimit-limit',
      'x-ratelimit-remaining',
      'cache-control',
    ];

    relevantHeaders.forEach(header => {
      if (headers[header]) {
        sanitized[header] = headers[header];
      }
    });

    return sanitized;
  }

  private sanitizeObject(obj: any): any {
    if (!obj || typeof obj !== 'object') {
      return obj;
    }

    // Campos sensibles a ocultar
    const sensitiveFields = [
      'password',
      'token',
      'secret',
      'key',
      'auth',
      'authorization',
      'creditcard',
      'ssn',
      'pin',
    ];

    const sanitized = { ...obj };

    for (const key in sanitized) {
      const keyLower = key.toLowerCase();

      // Ocultar campos sensibles
      if (sensitiveFields.some(field => keyLower.includes(field))) {
        sanitized[key] = '[REDACTED]';
      }

      // Recursivamente sanitizar objetos anidados
      if (typeof sanitized[key] === 'object' && sanitized[key] !== null) {
        sanitized[key] = this.sanitizeObject(sanitized[key]);
      }
    }

    return sanitized;
  }

  private getClientIP(request: Request): string {
    return (
      (request.headers['x-forwarded-for'] as string) ||
      (request.headers['x-real-ip'] as string) ||
      request.connection?.remoteAddress ||
      request.socket?.remoteAddress ||
      'unknown'
    );
  }

  private generateRequestId(): string {
    return `bot_req_${Date.now()}_${Math.random().toString(36).substring(2, 10)}`;
  }
}

import { Injectable, NestMiddleware, Inject, UnauthorizedException } from '@nestjs/common';
import { Request, Response, NextFunction } from 'express';
import { ConfigService } from '@nestjs/config';
import { JwtService } from '@nestjs/jwt';
import { createHmac, timingSafeEqual } from 'crypto';
import { ILogger } from '@core/interfaces/logger.interface';
import { LOGGER_SERVICE } from '@shared/constants/tokens';
import { RolesEnum } from '@shared/constants/enums';
import { IJwtPayload } from '@application/dtos/responses/user.response';

/**
 * Global Request Integrity Middleware
 * Verifies request signatures using Bearer token to determine if BOT or Server secret should be used
 */
@Injectable()
export class RequestIntegrityMiddleware implements NestMiddleware {
  private readonly botSecret: string;
  private readonly serverSecret: string;
  private readonly skipPaths: string[];

  constructor(
    private readonly configService: ConfigService,
    private readonly jwtService: JwtService,
    @Inject(LOGGER_SERVICE)
    private readonly logger: ILogger,
  ) {
    this.logger.setContext(RequestIntegrityMiddleware.name);

    // Get secrets from environment
    this.botSecret = this.configService.get<string>('BOT_INTEGRITY_SECRET');
    this.serverSecret = this.configService.get<string>('SERVER_INTEGRITY_SECRET');

    // Paths that skip integrity verification (all public endpoints)
    this.skipPaths = [
      // Health endpoints (all public)
      '/api/health',
      '/api/health/database',
      '/api/health/ready',
      '/api/health/live',

      // Auth endpoints (public endpoints only)
      '/api/auth/login',
      '/api/auth/verify-otp',
      '/api/auth/refresh-token',
      '/api/auth/email/verify',
      '/api/auth/password/request-reset',
      '/api/auth/password/reset',

      // Company endpoints (public)
      '/api/companies/by-host',

      // Documentation and root
      '/docs', // Swagger docs
      '/', // Root
    ];

    this.validateConfiguration();
  }

  use(req: Request, res: Response, next: NextFunction): void {
    // Skip integrity check for certain paths
    if (this.shouldSkipIntegrityCheck(req.path)) {
      return next();
    }

    try {
      const signature = req.headers['x-signature'] as string;

      if (!signature) {
        this.logger.warn({
          message: 'API request missing integrity signature',
          method: req.method,
          path: req.path,
          ip: req.ip,
          userAgent: req.headers['user-agent'],
        });
        throw new UnauthorizedException('Request integrity signature required');
      }

      // Determine if request is from BOT based on Bearer token
      const isBotRequest = this.isBotRequest(req);
      const origin = isBotRequest ? 'bot' : 'server';
      const secret = isBotRequest ? this.botSecret : this.serverSecret;

      if (!secret) {
        this.logger.error({
          message: 'No secret configured for request origin',
          origin,
          method: req.method,
          path: req.path,
        });
        throw new UnauthorizedException('Request signature verification not available');
      }

      // Build payload (same logic for all requests)
      const payload = this.buildPayload(req);

      // Verify signature with appropriate secret
      const expectedSignature = this.generateSignature(payload, secret);
      const isValid = this.verifySignature(signature, expectedSignature);

      if (!isValid) {
        this.logger.warn({
          message: 'Request integrity verification failed',
          method: req.method,
          path: req.path,
          ip: req.ip,
          origin,
          signature: signature.substring(0, 16) + '***',
          expectedSignature: expectedSignature.substring(0, 16) + '***',
        });
        throw new UnauthorizedException('Invalid request signature');
      }

      this.logger.debug({
        message: 'Request integrity verification successful',
        method: req.method,
        path: req.path,
        origin,
      });

      // Store origin information in request for later use
      (req as any).signatureOrigin = origin;

      next();
    } catch (error) {
      if (error instanceof UnauthorizedException) {
        throw error;
      }

      this.logger.error({
        message: 'Error in request integrity middleware',
        method: req.method,
        path: req.path,
        error: error instanceof Error ? error.message : String(error),
      });

      throw new UnauthorizedException('Request integrity verification failed');
    }
  }

  /**
   * Determine if request comes from BOT user based on Bearer token
   */
  private isBotRequest(req: Request): boolean {
    try {
      // Extract JWT token from Authorization header
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return false; // No bearer token = assume server request
      }

      const token = authHeader.substring(7);

      // Decode JWT without verification (we just need to check roles)
      const decoded = this.jwtService.decode(token) as IJwtPayload;
      if (!decoded || !decoded.roles) {
        return false;
      }

      // Check if user has BOT role
      const isBotUser = decoded.roles.some(role => role === RolesEnum.BOT);

      this.logger.debug({
        message: 'Detected request origin based on Bearer token',
        isBotUser,
        roles: decoded.roles,
      });

      return isBotUser;
    } catch (error) {
      // If we can't decode the token, assume it's a server request
      this.logger.debug({
        message: 'Could not decode Bearer token, assuming server request',
        error: error instanceof Error ? error.message : String(error),
      });

      return false;
    }
  }

  /**
   * Build payload for signature verification (same for all request types)
   */
  private buildPayload(req: Request): string {
    const fullUrl = `${req.protocol}://${req.get('host')}${req.originalUrl}`;
    const body = req.body ? this.stringifyBody(req.body) : '';
    const timestamp = Math.floor(Date.now() / (10 * 60 * 1000)); // 10 minute window

    const components = [req.method.toUpperCase(), fullUrl, body, timestamp.toString()];

    return components.join('|');
  }

  /**
   * Generate HMAC-SHA256 signature
   */
  private generateSignature(payload: string, secret: string): string {
    const hmac = createHmac('sha256', secret);
    hmac.update(payload, 'utf8');

    return hmac.digest('hex');
  }

  /**
   * Verify signature using timing-safe comparison
   */
  private verifySignature(provided: string, expected: string): boolean {
    if (provided.length !== expected.length) {
      return false;
    }

    try {
      const providedBuffer = Buffer.from(provided, 'hex');
      const expectedBuffer = Buffer.from(expected, 'hex');

      return timingSafeEqual(providedBuffer, expectedBuffer);
    } catch (_error) {
      return false;
    }
  }

  /**
   * Convert request body to string for signature
   */
  private stringifyBody(body: any): string {
    if (!body) return '';

    try {
      if (typeof body === 'string') return body;
      if (Buffer.isBuffer(body)) return body.toString('base64');

      if (typeof body === 'object' && body !== null) {
        // Sort object keys recursively for consistent signature
        const normalized = this.sortObjectKeys(body);

        return JSON.stringify(normalized);
      }

      return JSON.stringify(body);
    } catch (error) {
      this.logger.warn({
        message: 'Failed to stringify request body',
        error: error instanceof Error ? error.message : String(error),
      });

      return '';
    }
  }

  /**
   * Recursively sort object keys for consistent signatures
   */
  private sortObjectKeys(obj: any): any {
    if (obj === null || typeof obj !== 'object') {
      return obj;
    }

    if (Array.isArray(obj)) {
      return obj.map(item => this.sortObjectKeys(item));
    }

    const sorted: Record<string, any> = {};
    Object.keys(obj)
      .sort()
      .forEach(key => {
        sorted[key] = this.sortObjectKeys(obj[key]);
      });

    return sorted;
  }

  /**
   * Check if path should skip integrity verification
   */
  private shouldSkipIntegrityCheck(path: string): boolean {
    return this.skipPaths.some(skipPath => {
      if (skipPath.endsWith('*')) {
        return path.startsWith(skipPath.slice(0, -1));
      }

      return path === skipPath || path.startsWith(skipPath + '/');
    });
  }

  /**
   * Validate middleware configuration
   */
  private validateConfiguration(): void {
    if (!this.botSecret && !this.serverSecret) {
      this.logger.error({
        message:
          'CRITICAL: At least one integrity secret must be configured (BOT_INTEGRITY_SECRET or SERVER_INTEGRITY_SECRET)',
        severity: 'CRITICAL',
      });
      throw new Error('At least one integrity secret must be configured');
    }

    if (this.botSecret && this.botSecret.length < 32) {
      throw new Error('BOT_INTEGRITY_SECRET must be at least 32 characters long');
    }

    if (this.serverSecret && this.serverSecret.length < 32) {
      throw new Error('SERVER_INTEGRITY_SECRET must be at least 32 characters long');
    }

    this.logger.log({
      message: 'Request integrity middleware configured successfully',
      hasBotSecret: !!this.botSecret,
      hasServerSecret: !!this.serverSecret,
      skipPathsCount: this.skipPaths.length,
    });
  }
}

